#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <string.h>
#include <fcntl.h>
#include <sys/stat.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/50.txt */
static void OS_FileOpen_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/50.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

int32 OS_FileOpen_Impl(const OS_object_token_t *token, const char *local_path, int32 flags, int32 access_mode)
{
    int                             os_perm;
    int                             os_mode;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /*
    ** Check for a valid access mode
    ** For creating a file, OS_READ_ONLY does not make sense
    */
    switch (access_mode)
    {
        case OS_WRITE_ONLY:
            os_perm = O_WRONLY;
            break;
        case OS_READ_ONLY:
            os_perm = O_RDONLY;
            break;
        case OS_READ_WRITE:
            os_perm = O_RDWR;
            break;
        default:
            return OS_ERROR;
    }

    if (flags & OS_FILE_FLAG_CREATE)
    {
        os_perm |= O_CREAT;
    }
    if (flags & OS_FILE_FLAG_TRUNCATE)
    {
        os_perm |= O_TRUNC;
    }

    /* Log state before applying OS_IMPL_REGULAR_FILE_FLAGS */
    OS_FileOpen_Log("OS_FileOpen_Impl ENTER: path='%s' flags=0x%X access_mode=%d impl=%p os_perm_before=0x%X OS_IMPL_REGULAR_FILE_FLAGS=0x%X",
                    local_path, (unsigned int)flags, (int)access_mode, (void *)impl, (unsigned int)os_perm, (unsigned int)OS_IMPL_REGULAR_FILE_FLAGS);

    os_perm |= OS_IMPL_REGULAR_FILE_FLAGS;

    /* Log state after applying OS_IMPL_REGULAR_FILE_FLAGS */
    OS_FileOpen_Log("OS_FileOpen_Impl AFTER_IMPL_FLAGS: path='%s' os_perm_after=0x%X",
                    local_path, (unsigned int)os_perm);

    os_mode = S_IRUSR | S_IWUSR | S_IRGRP | S_IWGRP | S_IROTH | S_IWOTH;

    impl->fd = open(local_path, os_perm, os_mode);

    if (impl->fd < 0)
    {
        OS_DEBUG("open(%s): %s\n", local_path, strerror(errno));
        OS_FileOpen_Log("OS_FileOpen_Impl OPEN_FAILED: path='%s' fd=%d errno=%d (%s)",
                        local_path, impl->fd, errno, strerror(errno));
        return OS_ERROR;
    }

    /*
     * If the flags included O_NONBLOCK, then
     * enable the "select" call on this handle.
     */
    impl->selectable = ((os_perm & O_NONBLOCK) != 0);

    /* Final log with resulting fd and selectable state */
    OS_FileOpen_Log("OS_FileOpen_Impl SUCCESS: path='%s' fd=%d selectable=%d os_perm=0x%X",
                    local_path, impl->fd, (int)impl->selectable, (unsigned int)os_perm);

    return OS_SUCCESS;
}
