#include <stdio.h>
#include <time.h>
#include <dlfcn.h>

static void OS_ModuleLoad_LogProbe(const char *label,
                                   const void *token_ptr,
                                   const void *module_ptr,
                                   const char *translated_path,
                                   unsigned long module_flags,
                                   int dl_mode,
                                   const char *extra)
{
    FILE *f = fopen("/tmp/logs/43.txt", "a");
    if (f == NULL)
    {
        return;
    }

    time_t t = time(NULL);
    struct tm tm_buf;
    if (localtime_r(&t, &tm_buf) != NULL)
    {
        char timestr[64];
        if (strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf) > 0)
        {
            fprintf(f, "%s ", timestr);
        }
    }

    fprintf(f, "[OS_ModuleLoad] %s\n", label);
    fprintf(f, "  token_ptr=%p\n", token_ptr);
    fprintf(f, "  module_ptr=%p\n", module_ptr);
    if (translated_path != NULL)
    {
        fprintf(f, "  translated_path=\"%s\"\n", translated_path);
    }
    fprintf(f, "  module_flags=0x%08lx\n", module_flags);
    fprintf(f, "  dl_mode=0x%08x\n", dl_mode);
    if (extra != NULL)
    {
        fprintf(f, "  note=%s\n", extra);
    }
    fprintf(f, "\n");
    fclose(f);
}

int32 OS_ModuleLoad_Impl(const OS_object_token_t *token, const char *translated_path)
{
    int32                             status = OS_ERROR;
    int                               dl_mode;
    OS_impl_module_internal_record_t *impl;
    OS_module_internal_record_t *     module;

    impl   = OS_OBJECT_TABLE_GET(OS_impl_module_table, *token);
    module = OS_OBJECT_TABLE_GET(OS_module_table, *token);

    /* Probe: record initial state */
    OS_ModuleLoad_LogProbe("after_object_table_get",
                           (const void *)token,
                           (const void *)module,
                           translated_path,
                           (unsigned long)module->flags,
                           0,
                           "initial state captured");

    /*
     * RTLD_NOW should instruct dlopen() to resolve all the symbols in the
     * module immediately, as opposed to waiting until they are used.
     * The latter (lazy mode) is non-deterministic - a resolution error on
     * a rarely-used symbol could cause a random failure far in the future.
     */
    dl_mode = RTLD_NOW;

    if ((module->flags & OS_MODULE_FLAG_LOCAL_SYMBOLS) != 0)
    {
        /*
         * Do not add the symbols in this module to the global symbol table.
         * This mode helps prevent any unanticipated references into this
         * module, which can in turn prevent unloading via dlclose().
         */
        dl_mode |= RTLD_LOCAL;

        /* Probe: taken LOCAL branch */
        OS_ModuleLoad_LogProbe("branch_local_taken",
                               (const void *)token,
                               (const void *)module,
                               translated_path,
                               (unsigned long)module->flags,
                               dl_mode,
                               "RTLD_LOCAL set");
    }
    else
    {
        /*
         * Default mode - add symbols to the global symbol table, so they
         * will be available to resolve symbols in future module loads.
         * However, any such references will prevent unloading of this
         * module via dlclose().
         */
        dl_mode |= RTLD_GLOBAL;

        /* Probe: taken GLOBAL branch */
        OS_ModuleLoad_LogProbe("branch_global_taken",
                               (const void *)token,
                               (const void *)module,
                               translated_path,
                               (unsigned long)module->flags,
                               dl_mode,
                               "RTLD_GLOBAL set");
    }

    /* Clear any existing dlerror and probe prior to dlopen */
    dlerror();
    OS_ModuleLoad_LogProbe("before_dlopen",
                           (const void *)token,
                           (const void *)module,
                           translated_path,
                           (unsigned long)module->flags,
                           dl_mode,
                           "calling dlopen");

    impl->dl_handle = dlopen(translated_path, dl_mode);
    if (impl->dl_handle != NULL)
    {
        status = OS_SUCCESS;

        /* Probe: dlopen succeeded */
        OS_ModuleLoad_LogProbe("dlopen_success",
                               (const void *)token,
                               (const void *)module,
                               translated_path,
                               (unsigned long)module->flags,
                               dl_mode,
                               "dlopen succeeded");
    }
    else
    {
        const char *err = dlerror();

        /* Probe: dlopen failed with error string */
        OS_ModuleLoad_LogProbe("dlopen_failure",
                               (const void *)token,
                               (const void *)module,
                               translated_path,
                               (unsigned long)module->flags,
                               dl_mode,
                               err != NULL ? err : "(no dlerror message)");

        OS_DEBUG("Error loading shared library: %s\n", err);
    }

    return status;
}
