/* Probe logging helpers for capturing control flow/state to /tmp/logs/137.txt */
#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <arpa/inet.h>

static void os_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/137.txt", "a");
    if (f == NULL)
    {
        return;
    }

    /* Timestamp for easier ordering of events */
    time_t t = time(NULL);
    struct tm tm_buf;
    (void)localtime_r(&t, &tm_buf);
    char ts[64];
    (void)strftime(ts, sizeof(ts), "%Y-%m-%d %H:%M:%S", &tm_buf);

    fprintf(f, "%s - ", ts);

    va_list ap;
    va_start(ap, fmt);
    (void)vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_SocketAddrFromString_Impl(OS_SockAddr_t *Addr, const char *string)
{
    void *                  addrbuffer;
    OS_SockAddr_Accessor_t *Accessor;

    Accessor = (OS_SockAddr_Accessor_t *)&Addr->AddrData;

    /* Log entry and key state */
    os_log_probe("Enter OS_SocketAddrFromString_Impl: Addr=%p, string=%s", (void *)Addr, (string != NULL) ? string : "(null)");
    os_log_probe("Accessor=%p, sa_family=%d", (void *)Accessor, Accessor->sa.sa_family);

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            os_log_probe("Branch AF_INET: addrbuffer=%p", addrbuffer);
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            os_log_probe("Branch AF_INET6: addrbuffer=%p", addrbuffer);
            break;
#endif
        default:
            os_log_probe("Branch default: unknown sa_family=%d - returning OS_ERR_BAD_ADDRESS", Accessor->sa.sa_family);
            return OS_ERR_BAD_ADDRESS;
    }

    /* This function is defined as returning 1 on success, not 0 */
    {
        int ret = inet_pton(Accessor->sa.sa_family, string, addrbuffer);
        os_log_probe("inet_pton called: family=%d, string=%s, addrbuffer=%p, ret=%d",
                     Accessor->sa.sa_family, (string != NULL) ? string : "(null)", addrbuffer, ret);
        if (ret != 1)
        {
            os_log_probe("inet_pton failed (ret=%d) - returning OS_ERROR", ret);
            return OS_ERROR;
        }
    }

    os_log_probe("Success - returning OS_SUCCESS");
    return OS_SUCCESS;
}
