#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>

static void OS_TranslatePath_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/98.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);

    fclose(f);
}

int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;

    /*
    ** Check to see if the path pointers are NULL
    */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    OS_TranslatePath_Log("OS_TranslatePath: entry VirtualPath=%p LocalPath=%p", (void *)VirtualPath, (void *)LocalPath);

    /*
    ** Check length
    */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
    OS_TranslatePath_Log("Computed VirtPathLen=%zu", VirtPathLen);
    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        OS_TranslatePath_Log("Early return: path too long (VirtPathLen=%zu >= OS_MAX_PATH_LEN=%d)", VirtPathLen, (int)OS_MAX_PATH_LEN);
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to see if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    if (name_ptr == NULL)
    {
        OS_TranslatePath_Log("Early return: no '/' found in VirtualPath");
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;
    if (memchr(name_ptr, 0, OS_MAX_FILE_NAME) == NULL)
    {
        OS_TranslatePath_Log("Early return: filename too long (no null within OS_MAX_FILE_NAME)");
        return OS_FS_ERR_NAME_TOO_LONG;
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen;
    OS_TranslatePath_Log("Initial values: SysMountPointLen=%zu VirtPathBegin=%zu", SysMountPointLen, VirtPathBegin);

    /*
    ** All valid Virtual paths must start with a '/' character
    */
    if (VirtualPath[0] != '/')
    {
        OS_TranslatePath_Log("Early return: VirtualPath does not start with '/': first_char=%c", VirtualPath[0]);
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    OS_TranslatePath_Log("OS_ObjectIdGetBySearch returned %d", return_code);

    if (return_code != OS_SUCCESS)
    {
        return_code = OS_FS_ERR_PATH_INVALID;
        OS_TranslatePath_Log("Object search failed, setting return_code=%d", return_code);
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        OS_TranslatePath_Log("Found filesys at %p, flags=0x%08x", (void *)filesys, (unsigned)filesys->flags);

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
            OS_TranslatePath_Log("Mounted system: system_mountpt='%s' (len=%zu), virtual_mountpt='%s' (len=%zu)",
                                 filesys->system_mountpt, SysMountPointLen, filesys->virtual_mountpt, VirtPathBegin);

            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
                OS_TranslatePath_Log("Copied system_mountpt to LocalPath, SysMountPointLen=%zu", SysMountPointLen);
            }
            else
            {
                OS_TranslatePath_Log("System mount point length too large for LocalPath (SysMountPointLen=%zu >= OS_MAX_LOCAL_PATH_LEN=%d)", SysMountPointLen, (int)OS_MAX_LOCAL_PATH_LEN);
            }
        }
        else
        {
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
            OS_TranslatePath_Log("Filesys not mounted as system, flags=0x%08x, setting return_code=%d", (unsigned)filesys->flags, return_code);
        }

        OS_ObjectIdRelease(&token);
        OS_TranslatePath_Log("Released object token");
    }

    if (return_code == OS_SUCCESS)
    {
        OS_TranslatePath_Log("Entering success path: VirtPathLen=%zu VirtPathBegin=%zu SysMountPointLen=%zu", VirtPathLen, VirtPathBegin, SysMountPointLen);

        if (VirtPathLen < VirtPathBegin)
        {
            OS_TranslatePath_Log("Invalid path: VirtPathLen (%zu) < VirtPathBegin (%zu)", VirtPathLen, VirtPathBegin);
            return_code = OS_FS_ERR_PATH_INVALID;
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            OS_TranslatePath_Log("Adjusted VirtPathLen for copy: new VirtPathLen=%zu", VirtPathLen);
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;
                OS_TranslatePath_Log("Copied VirtualPath segment to LocalPath at offset %zu (final length=%zu)", SysMountPointLen, SysMountPointLen + VirtPathLen);
            }
            else
            {
                OS_TranslatePath_Log("Local path would be too long: SysMountPointLen=%zu VirtPathLen=%zu OS_MAX_LOCAL_PATH_LEN=%d", SysMountPointLen, VirtPathLen, (int)OS_MAX_LOCAL_PATH_LEN);
                return_code = OS_FS_ERR_PATH_TOO_LONG;
            }
        }
    }

    OS_TranslatePath_Log("Exiting OS_TranslatePath with return_code=%d", return_code);

    return return_code;
}
