#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/* Probe logger: append human-readable logs for control flow/state checks. */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/258.txt", "a");
    if (!f) {
        return;
    }

    fprintf(f, "[lfs_probe] ");
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int lfs_dir_read(lfs_t *lfs, lfs_dir_t *dir, struct lfs_info *info) {
    int err = LFS_LOCK(lfs->cfg);
    lfs_probe_log("enter lfs_dir_read: lfs=%p dir=%p info=%p cfg=%p LFS_LOCK->%d",
            (void*)lfs, (void*)dir, (void*)info, (void*)lfs->cfg, err);
    if (err) {
        lfs_probe_log("lfs_dir_read: returning early due to lock error=%d", err);
        return err;
    }

    LFS_TRACE("lfs_dir_read(%p, %p, %p)",
            (void*)lfs, (void*)dir, (void*)info);

    err = lfs_dir_read_(lfs, dir, info);

    LFS_TRACE("lfs_dir_read -> %d", err);

    /* Record state just before unlock so we can demonstrate whether
       LFS_UNLOCK has any observable effect on lfs->cfg. */
    lfs_probe_log("before LFS_UNLOCK: err=%d cfg=%p", err, (void*)lfs->cfg);
    void *cfg_before = (void*)lfs->cfg;

    LFS_UNLOCK(lfs->cfg);

    lfs_probe_log("after LFS_UNLOCK: cfg=%p (before=%p) unchanged=%d",
            (void*)lfs->cfg, cfg_before,
            (cfg_before == (void*)lfs->cfg) ? 1 : 0);

    lfs_probe_log("exit lfs_dir_read: returning err=%d", err);
    return err;
}
