#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>

/* Probe logging helper: appends human-readable logs to /tmp/logs/269.txt */
static void lfs_probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/269.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int lfs_dir_seek(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    /* Log entry and initial parameter/state */
    lfs_probe_log("ENTER lfs_dir_seek: lfs=%p dir=%p off=%" PRIu32, (void*)lfs, (void*)dir, (uint32_t)off);

    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        lfs_probe_log("lfs_dir_seek: LFS_LOCK failed, err=%d", err);
        return err;
    }

    lfs_probe_log("lfs_dir_seek: locked, about to call lfs_dir_seek_ with off=%" PRIu32, (uint32_t)off);

    LFS_TRACE("lfs_dir_seek(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)dir, off);

    /* Call the underlying implementation (may modify its local parameter 'off') */
    err = lfs_dir_seek_(lfs, dir, off);

    lfs_probe_log("lfs_dir_seek: returned from lfs_dir_seek_ with err=%d", err);

    LFS_TRACE("lfs_dir_seek -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    lfs_probe_log("EXIT lfs_dir_seek: lfs=%p dir=%p off=%" PRIu32 " err=%d",
            (void*)lfs, (void*)dir, (uint32_t)off, err);
    return err;
}
