#include <stdio.h>
#include <inttypes.h>
#include <time.h>
#include <stdint.h>

/*
 * Probe helper: logs relevant information to /tmp/logs/269.txt.
 * Keeps the original logic untouched; only observes state.
 */
static void lfs_probe_log(const char *stage, lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    FILE *f = fopen("/tmp/logs/269.txt", "a");
    if (!f) {
        return;
    }
    time_t t = time(NULL);

    /* Log basic contextual fields. Use PRIu32 as in original tracing for off. */
    fprintf(f, "[%ld] %s: lfs=%p dir=%p off=%" PRIu32 " off_addr=%p sizeof(off)=%zu\n",
            (long)t, stage, (void*)lfs, (void*)dir, off, (void*)&off, sizeof(off));

    /* Dump a small hex snapshot of the stack region around 'off' to help detect illegal writes.
     * This prints up to 16 bytes starting at the address of the local copy of 'off'.
     */
    unsigned char *p = (unsigned char*)&off;
    fprintf(f, "%s: stack bytes @%p: ", stage, (void*)p);
    for (size_t i = 0; i < sizeof(off) && i < 16; i++) {
        fprintf(f, "%02x ", (unsigned)p[i]);
    }
    fprintf(f, "\n\n");

    fclose(f);
}

int lfs_dir_seek(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    /* Original trace preserved */
    LFS_TRACE("lfs_dir_seek(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)dir, off);

    /* Probe: record state before calling lfs_dir_seek_ */
    lfs_probe_log("before_lfs_dir_seek_", lfs, dir, off);

    err = lfs_dir_seek_(lfs, dir, off);

    /* Probe: record state after calling lfs_dir_seek_ */
    lfs_probe_log("after_lfs_dir_seek_", lfs, dir, off);

    LFS_TRACE("lfs_dir_seek -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
