#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stdint.h>

static void lfs_probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/50.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

/* Original function with minimal probes inserted to capture the
   control flow and state around the LFS_UNLOCK call (which is a no-op).
   The logic of the function is otherwise preserved. */
lfs_ssize_t lfs_file_read(lfs_t *lfs, lfs_file_t *file,
        void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        lfs_probe_log("lfs_file_read: LFS_LOCK failed err=%d, lfs=%p cfg=%p", err, (void*)lfs, (void*)lfs->cfg);
        return err;
    }
    LFS_TRACE("lfs_file_read(%p, %p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, buffer, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_probe_log("lfs_file_read: before lfs_file_read_: lfs=%p file=%p buffer=%p size=%"PRIu32" cfg=%p",
            (void*)lfs, (void*)file, buffer, size, (void*)lfs->cfg);

    lfs_ssize_t res = lfs_file_read_(lfs, file, buffer, size);

    LFS_TRACE("lfs_file_read -> %"PRId32, res);

    /* Probe the cfg pointer and log before/after unlock so other agents
       can observe that LFS_UNLOCK(lfs->cfg) is effectively a no-op. */
    void *cfg_before = (void*)lfs->cfg;
    lfs_probe_log("lfs_file_read: about to call LFS_UNLOCK, cfg_before=%p", cfg_before);

    LFS_UNLOCK(lfs->cfg);

    void *cfg_after = (void*)lfs->cfg;
    lfs_probe_log("lfs_file_read: after LFS_UNLOCK, cfg_after=%p cfg_unchanged=%d",
            cfg_after, (cfg_before == cfg_after));

    return res;
}
