#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append human-readable logs to /tmp/logs/65.txt */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/65.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fputc('\n', f);
    va_end(ap);
    fclose(f);
}

int lfs_file_sync(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log the result of LFS_LOCK and basic pointers so callers can observe
       that LFS_LOCK returned 0 in configurations where it is defined as
       '((void)cfg, 0)'. Do not change program logic. */
    lfs_probe_log("lfs_file_sync: after LFS_LOCK -> err=%d, lfs=%p, file=%p",
                  err, (void *)lfs, (void *)file);

    if (err) {
        /* This branch is expected to be unreachable in the reported
           configuration. Log if it ever happens so agents can capture
           evidence. */
        lfs_probe_log("lfs_file_sync: early return because err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_file_sync(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_sync_(lfs, file);

    /* Log the result from the internal sync helper before unlocking. */
    lfs_probe_log("lfs_file_sync: after lfs_file_sync_ -> err=%d", err);

    LFS_TRACE("lfs_file_sync -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    lfs_probe_log("lfs_file_sync: LFS_UNLOCK called, returning err=%d", err);
    return err;
}
