/* Probe logging helper: append human-readable trace lines to /tmp/logs/138.txt.
   This function is intentionally small and non-intrusive to preserve original logic. */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/138.txt", "a");
    if (!f) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

