#include <stdio.h>
#include <stdarg.h>

/* Simple probe logger that appends human-readable traces to /tmp/logs/138.txt */
static void lfs_probe_logf(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/138.txt", "a");
    if (!f) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

static void lfs_probe_log_entry(lfs_t *lfs, lfs_file_t *file, lfs_off_t size)
{
    lfs_probe_logf("ENTRY: lfs_file_truncate(lfs=%p, file=%p, size=%llu)",
            (void*)lfs, (void*)file, (unsigned long long)size);
}

static void lfs_probe_log_after_lock(int err)
{
    lfs_probe_logf("AFTER_LOCK: err=%d", err);
}

static void lfs_probe_log_before_call(lfs_t *lfs, lfs_file_t *file, lfs_off_t size)
{
    lfs_probe_logf("CALLING: lfs_file_truncate_(lfs=%p, file=%p, size=%llu)",
            (void*)lfs, (void*)file, (unsigned long long)size);
}

static void lfs_probe_log_after_call(int err)
{
    lfs_probe_logf("AFTER_CALL: err=%d", err);
}

int lfs_file_truncate(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    int err = LFS_LOCK(lfs->cfg);
    lfs_probe_log_entry(lfs, file, size);
    lfs_probe_log_after_lock(err);
    if (err) {
        lfs_probe_logf("RETURN (lock error): %d", err);
        return err;
    }
    LFS_TRACE("lfs_file_truncate(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_probe_log_before_call(lfs, file, size);
    err = lfs_file_truncate_(lfs, file, size);

    lfs_probe_log_after_call(err);

    LFS_TRACE("lfs_file_truncate -> %d", err);
    lfs_probe_logf("UNLOCK and RETURN: err=%d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
