#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stdint.h>

/* Probe helper: append diagnostic messages to /tmp/logs/106.txt
   This helper preserves program logic and is used by lfs_format only. */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/106.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    /* Log entry and key cfg pointers/state for reproducibility */
    lfs_probe_log("ENTER lfs_format: lfs=%p cfg=%p cfg->context=%p",
                  (void*)lfs, (void*)cfg, cfg ? cfg->context : NULL);
    if (cfg) {
        lfs_probe_log(
            "cfg fns: read=%p prog=%p erase=%p sync=%p",
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync);
        lfs_probe_log(
            "cfg sizes: read_size=%" PRIu32 ", prog_size=%" PRIu32 ", block_size=%" PRIu32 ", block_count=%" PRIu32,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count);
    }

    int err = LFS_LOCK(cfg);
    lfs_probe_log("LFS_LOCK returned %d", err);
    if (err) {
        lfs_probe_log("EXIT lfs_format early due to lock error: %d", err);
        return err;
    }

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"})",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    lfs_probe_log("About to call lfs_format_: lfs=%p cfg=%p", (void*)lfs, (void*)cfg);
    err = lfs_format_(lfs, cfg);
    lfs_probe_log("lfs_format_ returned %d", err);

    LFS_TRACE("lfs_format -> %d", err);
    LFS_UNLOCK(cfg);
    lfs_probe_log("LFS_UNLOCK called for cfg=%p; returning %d", (void*)cfg, err);
    return err;
}
