#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append logs to /tmp/logs/106.txt */
static void lfs_format_probe(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/106.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    /* Log entry and input pointers */
    lfs_format_probe("ENTER lfs_format: lfs=%p, cfg=%p, cfg->context=%p",
                     (void*)lfs, (void*)cfg, cfg ? cfg->context : NULL);

    lfs_format_probe("About to call LFS_LOCK(cfg) with cfg=%p", (void*)cfg);
    int err = LFS_LOCK(cfg);
    lfs_format_probe("Returned from LFS_LOCK(cfg): err=%d", err);

    if (err) {
        lfs_format_probe("LFS_LOCK returned non-zero, early return err=%d", err);
        return err;
    }

    lfs_format_probe("LFS_LOCK returned zero, proceeding with lfs_format_\n"
                     "(probe: cfg->read=%p, cfg->prog=%p, cfg->erase=%p, cfg->sync=%p)",
                     (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
                     (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync);

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    err = lfs_format_(lfs, cfg);

    lfs_format_probe("lfs_format_ returned: err=%d", err);
    LFS_TRACE("lfs_format -> %d", err);

    lfs_format_probe("About to call LFS_UNLOCK(cfg) with cfg=%p", (void*)cfg);
    LFS_UNLOCK(cfg);
    lfs_format_probe("Returned from LFS_UNLOCK(cfg)");

    lfs_format_probe("EXIT lfs_format: returning err=%d", err);
    return err;
}
