#include <stdio.h>
#include <inttypes.h>

int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and result of LFS_LOCK */
    {
        FILE *f = fopen("/tmp/logs/194.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_fs_grow: lfs=%p, block_count=%" PRIu32 ", LFS_LOCK returned=%d\n",
                    (void*)lfs, block_count, err);
            fclose(f);
        }
    }

    if (err) {
        /* Probe: log early return due to lock failure */
        FILE *f = fopen("/tmp/logs/194.txt", "a");
        if (f) {
            fprintf(f, "lfs_fs_grow: LFS_LOCK failed with err=%d, returning early\n", err);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    err = lfs_fs_grow_(lfs, block_count);

    /* Probe: log result of internal grow operation */
    {
        FILE *f = fopen("/tmp/logs/194.txt", "a");
        if (f) {
            fprintf(f, "lfs_fs_grow_: returned %d for lfs=%p, block_count=%" PRIu32 "\n",
                    err, (void*)lfs, block_count);
            fclose(f);
        }
    }

    LFS_TRACE("lfs_fs_grow -> %d", err);

    /* Probe: log immediately before calling LFS_UNLOCK. This documents that the
       call is executed even when LFS_UNLOCK is a no-op macro in some builds. */
    {
        FILE *f = fopen("/tmp/logs/194.txt", "a");
        if (f) {
            fprintf(f, "About to call LFS_UNLOCK with cfg=%p (LFS_UNLOCK may be a no-op)\n", (void*)lfs->cfg);
            fclose(f);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    /* Probe: log immediately after calling LFS_UNLOCK to show it was executed. */
    {
        FILE *f = fopen("/tmp/logs/194.txt", "a");
        if (f) {
            fprintf(f, "After LFS_UNLOCK for cfg=%p; final err=%d\n", (void*)lfs->cfg, err);
            fclose(f);
        }
    }

    return err;
}
