#include <stdio.h>
#include <stdarg.h>
#include <time.h>

/* Probe logger: append human-readable traces to /tmp/logs/186.txt. */
static void probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/186.txt", "a");
    if (f == NULL) {
        return;
    }

    time_t t = time(NULL);
    /* Print simple timestamp (seconds since epoch) to avoid locale/thread issues */
    fprintf(f, "[%ld] ", (long)t);

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    probe_log("ENTER lfs_fs_stat: lfs=%p fsinfo=%p", (void*)lfs, (void*)fsinfo);

    /* Preserve original behavior: evaluate LFS_LOCK exactly once and record the result. */
    int err = LFS_LOCK(lfs->cfg);
    probe_log("LFS_LOCK(lfs->cfg) -> %d", err);

    if (err) {
        probe_log("LFS_LOCK returned non-zero, early return: %d", err);
        return err;
    }

    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);
    probe_log("Calling lfs_fs_stat_ with lfs=%p fsinfo=%p", (void*)lfs, (void*)fsinfo);

    err = lfs_fs_stat_(lfs, fsinfo);
    probe_log("lfs_fs_stat_ returned %d", err);

    LFS_TRACE("lfs_fs_stat -> %d", err);
    probe_log("About to call LFS_UNLOCK(lfs->cfg)");

    LFS_UNLOCK(lfs->cfg);
    probe_log("LFS_UNLOCK done, returning %d", err);

    return err;
}
