#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stdint.h>

/* Probe helper: append formatted logs to /tmp/logs/217.txt. */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/217.txt", "a");
    if (!f) {
        return; /* best-effort logging only */
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

int lfs_mount(lfs_t *lfs, const struct lfs_config *cfg) {
    /* record entry and cfg pointer */
    lfs_probe_log("lfs_mount: enter, lfs=%p, cfg=%p, cfg->context=%p",
                  (void*)lfs, (void*)cfg, cfg ? cfg->context : NULL);

    int err = LFS_LOCK(cfg);

    /* Log the value returned from LFS_LOCK to provide evidence whether it
       can ever be non-zero (the reported violation asserts LFS_LOCK(cfg)
       expands to ((void)cfg, 0) which would make err == 0 always). */
    lfs_probe_log("lfs_mount: after LFS_LOCK, err=%d", err);

    if (err) {
        /* This branch is the one reported as unreachable in the violation
           scenario. Keep the original behavior but log if it is ever taken. */
        lfs_probe_log("lfs_mount: if(err) taken, returning err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_mount(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"}),",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    lfs_probe_log("lfs_mount: calling lfs_mount_(lfs=%p, cfg=%p)", (void*)lfs, (void*)cfg);
    err = lfs_mount_(lfs, cfg);

    LFS_TRACE("lfs_mount -> %d", err);

    /* Log the result of lfs_mount_ before unlocking to capture final state. */
    lfs_probe_log("lfs_mount: lfs_mount_ returned err=%d", err);
    lfs_probe_log("lfs_mount: calling LFS_UNLOCK(cfg=%p)", (void*)cfg);

    LFS_UNLOCK(cfg);

    lfs_probe_log("lfs_mount: exit, returning %d", err);
    return err;
}
