#include <stdio.h>
#include <stdarg.h>

/*
 * Probe/log helper: append human-readable entries to /tmp/logs/98.txt.
 * Kept as a simple helper so the original logic of lfs_stat is unchanged.
 */
static void lfs_log_probe(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/98.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

int lfs_stat(lfs_t *lfs, const char *path, struct lfs_info *info) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log the result of the lock attempt and relevant state for repro */
    lfs_log_probe("[probe] lfs_stat: after LFS_LOCK -> lfs=%p, path=%s, info=%p, err=%d",
                  (void*)lfs, (path ? path : "(null)"), (void*)info, err);

    if (err) {
        /* If this branch ever executes, log it before returning. */
        lfs_log_probe("[probe] lfs_stat: early return due to err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_stat(%p, \"%s\", %p)", (void*)lfs, path, (void*)info);

    lfs_log_probe("[probe] lfs_stat: calling lfs_stat_ -> lfs=%p, path=%s, info=%p",
                  (void*)lfs, (path ? path : "(null)"), (void*)info);

    err = lfs_stat_(lfs, path, info);

    lfs_log_probe("[probe] lfs_stat: lfs_stat_ returned err=%d", err);

    LFS_TRACE("lfs_stat -> %d", err);

    lfs_log_probe("[probe] lfs_stat: before LFS_UNLOCK -> lfs=%p, err=%d",
                  (void*)lfs, err);

    LFS_UNLOCK(lfs->cfg);

    lfs_log_probe("[probe] lfs_stat: after LFS_UNLOCK -> lfs=%p, final_err=%d",
                  (void*)lfs, err);

    return err;
}
