#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <limits.h>
#include <errno.h>
#include <stdatomic.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/37.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int metal_io_block_set(struct metal_io_region *io, unsigned long offset,
           unsigned char value, int len)
{
    unsigned char *ptr = metal_io_virt(io, offset);
    int retlen = len;

    probe_log("enter metal_io_block_set: io=%p offset=%lu value=%u len=%d io_size=%lu ptr=%p",
              (void *)io, offset, (unsigned int)value,
              len, (io ? io->size : 0UL), (void *)ptr);

    if (!ptr) {
        probe_log("exit: ptr is NULL -> return -ERANGE");
        return -ERANGE;
    }
    if ((offset + len) > io->size) {
        probe_log("adjust len: original len=%d io->size=%lu offset=%lu -> new len=%ld",
                  retlen, io->size, offset, (long)(io->size - offset));
        len = io->size - offset;
    }
    retlen = len;
    probe_log("after adjust: retlen=%d len=%d ptr=%p", retlen, len, (void *)ptr);
    if (io->ops.block_set) {
        probe_log("calling io->ops.block_set: io=%p offset=%lu value=%u len=%d",
                  (void *)io, offset, (unsigned int)value, len);
        (*io->ops.block_set)(
            io, offset, value, memory_order_seq_cst, len);
    } else {
        unsigned int cint = value;
        unsigned int i;

        probe_log("build cint: initial cint=%u value=%u", cint, (unsigned int)value);
        for (i = 1; i < sizeof(int); i++)
            cint |= ((unsigned int)value << (CHAR_BIT * i));
        probe_log("build cint: final cint=0x%x (after %u iterations)", cint, i);

        probe_log("start loop1 alignment: ptr=%p len=%d sizeof(int)=%zu",
                  (void *)ptr, len, sizeof(int));
        for (; len && ((uintptr_t)ptr % sizeof(int)); ptr++, len--) {
            probe_log("loop1: ptr=%p len=%d writing byte 0x%x", (void *)ptr, len, (unsigned int)value);
            *(unsigned char *)ptr = (unsigned char) value;
        }
        probe_log("after loop1: ptr=%p len=%d", (void *)ptr, len);

        probe_log("start loop2 word writes: ptr=%p len=%d sizeof(int)=%zu",
                  (void *)ptr, len, sizeof(int));
        /* preserve original behavior while logging each iteration to evidence len mutation */
        while (len >= (int)sizeof(int)) {
            probe_log("loop2 (before write): ptr=%p len=%d cint=0x%x", (void *)ptr, len, cint);
            *(unsigned int *)ptr = cint;
            /* advance as original for-loop would do */
            ptr += sizeof(int);
            len -= sizeof(int);
            probe_log("loop2 (after write): ptr=%p len=%d", (void *)ptr, len);
        }
        probe_log("after loop2: ptr=%p len=%d", (void *)ptr, len);

        probe_log("start loop3 tail bytes: ptr=%p len=%d", (void *)ptr, len);
        for (; len != 0; ptr++, len--) {
            probe_log("loop3: ptr=%p len=%d writing byte 0x%x", (void *)ptr, len, (unsigned int)value);
            *(unsigned char *)ptr = (unsigned char) value;
        }
        probe_log("after loop3: ptr=%p len=%d", (void *)ptr, len);

        atomic_thread_fence(memory_order_seq_cst);
    }
    probe_log("exit metal_io_block_set: returning retlen=%d", retlen);
    return retlen;
}
