#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <errno.h>

static int metal_linux_probe_driver(struct linux_bus *lbus,
                                    struct linux_driver *ldrv)
{
    char command[256];
    int ret;
    FILE *logf = fopen("/tmp/logs/19.txt", "a");

    if (logf) {
        fprintf(logf, "ENTER %s: lbus=%p, lbus->bus_name='%s', ldrv=%p, ldrv->drv_name='%s', ldrv->mod_name='%s'\n",
                __func__, (void *)lbus, lbus ? lbus->bus_name : "(null)", (void *)ldrv,
                ldrv ? ldrv->drv_name : "(null)", ldrv ? ldrv->mod_name : "(null)");
        fclose(logf);
    }

    ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);

    if (logf == NULL) /* reopen when needed to avoid holding the file */
        logf = fopen("/tmp/logs/19.txt", "a");
    if (logf) {
        fprintf(logf, "%s: after initial sysfs_open_driver -> sdrv=%p\n",
                __func__, (void *)ldrv->sdrv);
        fclose(logf);
    }

    /* Try probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        ret = snprintf(command, sizeof(command),
                       "modprobe %s > /dev/null 2>&1", ldrv->mod_name);

        if (logf == NULL)
            logf = fopen("/tmp/logs/19.txt", "a");
        if (logf) {
            fprintf(logf, "%s: snprintf returned %d, command='%s'\n",
                    __func__, ret, command);
            fclose(logf);
        }

        if (ret >= (int)sizeof(command))
            return -EOVERFLOW;
        ret = system(command);

        if (logf == NULL)
            logf = fopen("/tmp/logs/19.txt", "a");
        if (logf) {
            fprintf(logf, "%s: system(command) returned %d\n", __func__, ret);
            if (ret < 0)
                fprintf(logf, "%s: system error: errno=%d (%s)\n", __func__, errno, strerror(errno));
            fclose(logf);
        }
        metal_log(METAL_LOG_WARNING,
                  "%s: executing system command '%s' failed.\n",
                  __func__, command);

        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);

        if (logf == NULL)
            logf = fopen("/tmp/logs/19.txt", "a");
        if (logf) {
            fprintf(logf, "%s: after sysfs_open_driver (modprobe) -> sdrv=%p\n",
                    __func__, (void *)ldrv->sdrv);
            fclose(logf);
        }
    }

    /* Try sudo probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        ret = snprintf(command, sizeof(command),
                       "sudo modprobe %s > /dev/null 2>&1", ldrv->mod_name);

        if (logf == NULL)
            logf = fopen("/tmp/logs/19.txt", "a");
        if (logf) {
            fprintf(logf, "%s: snprintf (sudo) returned %d, command='%s'\n",
                    __func__, ret, command);
            fclose(logf);
        }

        if (ret >= (int)sizeof(command))
            return -EOVERFLOW;
        ret = system(command);

        if (logf == NULL)
            logf = fopen("/tmp/logs/19.txt", "a");
        if (logf) {
            fprintf(logf, "%s: system(sudo) returned %d\n", __func__, ret);
            if (ret < 0)
                fprintf(logf, "%s: system(sudo) error: errno=%d (%s)\n", __func__, errno, strerror(errno));
            fclose(logf);
        }
        metal_log(METAL_LOG_WARNING,
                  "%s: executing system command '%s' failed.\n",
                  __func__, command);

        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);

        if (logf == NULL)
            logf = fopen("/tmp/logs/19.txt", "a");
        if (logf) {
            fprintf(logf, "%s: after sysfs_open_driver (sudo) -> sdrv=%p\n",
                    __func__, (void *)ldrv->sdrv);
            fclose(logf);
        }
    }

    /* If all else fails... */
    if (logf == NULL)
        logf = fopen("/tmp/logs/19.txt", "a");
    if (logf) {
        fprintf(logf, "%s: exit -> returning %d, final sdrv=%p\n",
                __func__, ldrv->sdrv ? 0 : -ENODEV, (void *)ldrv->sdrv);
        fclose(logf);
    }

    return ldrv->sdrv ? 0 : -ENODEV;
}
