#include <stdio.h>
#include <stdarg.h>
#include <sys/mman.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <unistd.h>
#include <errno.h>
#include <sys/file.h>

static void metal_map_log(const char *fmt, ...)
{
    int saved_errno = errno;
    FILE *f = fopen("/tmp/logs/109.txt", "a");
    if (f) {
        va_list ap;
        va_start(ap, fmt);
        vfprintf(f, fmt, ap);
        fprintf(f, "\n");
        va_end(ap);
        fclose(f);
    }
    errno = saved_errno;
}

int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;

    /* Log entry state */
    metal_map_log("enter: fd=%d offset=%lld size=%zu expand=%d flags=0x%x",
                  fd, (long long)offset, size, expand, flags);

    /* This is the modification reported as violating MISRA 17.8: parameter modification */
    flags |= MAP_SHARED;
    metal_map_log("after flags |= MAP_SHARED: flags=0x%x", flags);

    if (fd < 0) {
        metal_map_log("branch: fd < 0 (fd=%d). Setting fd=-1 and flags=MAP_PRIVATE|MAP_ANONYMOUS", fd);
        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;
        metal_map_log("after fd<0 assignment: fd=%d flags=0x%x", fd, flags);
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat statbuf;

        metal_map_log("branch: expand path taken. reqsize=%lld", (long long)reqsize);

        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        metal_map_log("after flock LOCK_EX: error=%d (errno=%d)", error, errno);
        if (!error)
            error = fstat(fd, &statbuf);
        metal_map_log("after fstat: error=%d (st_size=%lld)", error, (long long)statbuf.st_size);
        if (!error && statbuf.st_size < reqsize)
            error = ftruncate(fd, reqsize);
        metal_map_log("after potential ftruncate: error=%d", error);
        if (!error)
            flock(fd, LOCK_UN);
        metal_map_log("after flock LOCK_UN (if it was taken): error=%d", error);
        if (error)
            return -errno;
    }

    metal_map_log("about to call mmap: fd=%d offset=%lld size=%zu prot=0x%x flags=0x%x",
                  fd, (long long)offset, size, prot, flags);
    mem = mmap(NULL, size, prot, flags, fd, offset);
    if (mem == MAP_FAILED) {
        metal_map_log("mmap failed: errno=%d", errno);
        return -errno;
    }
    *result = mem;
    metal_map_log("mmap succeeded: mem=%p returned via result", mem);
    return 0;
}
