#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdint.h>

/* Probe logging helper: append textual logs to /tmp/logs/122.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/122.txt", "a");
    if (!f)
        return; /* Do not change program behavior if logging fails */

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

static int metal_uio_dev_dma_map(struct linux_bus *lbus,
                 struct linux_device *ldev,
                 uint32_t dir,
                 struct metal_sg *sg_in,
                 int nents_in,
                 struct metal_sg *sg_out)
{
    int i, j;
    void *vaddr_sg_lo, *vaddr_sg_hi, *vaddr_lo, *vaddr_hi;
    struct metal_io_region *io;

    /* Preserve original unused-parameter suppression */
    (void)lbus;
    (void)dir;

    /* Log function entry and note that the (void)lbus and (void)dir statements executed */
    probe_log("ENTER: %s: lbus=%p, ldev=%p, dir=%u, nents_in=%d -- executed (void)lbus and (void)dir",
          __func__, (void *)lbus, (void *)ldev, (unsigned)dir, nents_in);

    /* Check if the the input virt address is MMIO address */
    for (i = 0; i < nents_in; i++) {
        vaddr_sg_lo = sg_in[i].virt;
        vaddr_sg_hi = vaddr_sg_lo + sg_in[i].len;

        probe_log("ITER: %s: checking sg[%d]: virt=%p, len=%d, range=[%p,%p)",
              __func__, i, vaddr_sg_lo, sg_in[i].len, vaddr_sg_lo, vaddr_sg_hi);

        for (j = 0, io = ldev->device.regions;
             j < (int)ldev->device.num_regions; j++, io++) {
            vaddr_lo = io->virt;
            vaddr_hi = vaddr_lo + io->size;
            if (vaddr_sg_lo >= vaddr_lo &&
                vaddr_sg_hi <= vaddr_hi) {
                probe_log("MATCH: %s: sg[%d] fits in region %d: region_range=[%p,%p)",
                      __func__, i, j, vaddr_lo, vaddr_hi);
                break;
            }
        }
        if (j == (int)ldev->device.num_regions) {
            /* Log the failure details to the probe log before returning */
            probe_log("FAIL: %s: input address isn't MMIO addr for sg[%d]: virt=%p, len=%d, num_regions=%d",
                  __func__, i, vaddr_sg_lo, sg_in[i].len, (int)ldev->device.num_regions);

            /* Preserve original behavior and original warning call */
            metal_log(METAL_LOG_WARNING,
              "%s,%s: input address isn't MMIO addr: 0x%x,%d.\n",
            __func__, ldev->dev_name, vaddr_sg_lo, sg_in[i].len);
            probe_log("EXIT: %s: returning -EINVAL (input not MMIO)", __func__);
            return -EINVAL;
        }
    }

    if (sg_out != sg_in) {
        probe_log("ACTION: %s: copying %d sg entries from sg_in(%p) to sg_out(%p)",
              __func__, nents_in, (void *)sg_in, (void *)sg_out);
        memcpy(sg_out, sg_in, nents_in*(sizeof(struct metal_sg)));
        probe_log("ACTION_DONE: %s: memcpy completed", __func__);
    } else {
        probe_log("NO_ACTION: %s: sg_out == sg_in, no memcpy performed", __func__);
    }

    probe_log("EXIT: %s: returning %d (nents_in)", __func__, nents_in);
    return nents_in;
}
