#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <unistd.h>
#include <errno.h>
#include <string.h>

/* Probe logging helper: appends control flow / state information to /tmp/logs/11.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/11.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static int metal_uio_dev_open(struct linux_bus *lbus, struct linux_device *ldev)
{
	char *instance, path[SYSFS_PATH_MAX];
	struct linux_driver *ldrv = ldev->ldrv;
	unsigned long *phys, offset = 0, size = 0;
	struct metal_io_region *io;
	struct dlist *dlist;
	int result, i;
	void *virt;
	int irq_info;

	/* Entry probe */
	probe_log("metal_uio_dev_open: entry lbus=%p ldev=%p bus_name=%s dev_name=%s",
	          (void *)lbus, (void *)ldev,
	          lbus ? lbus->bus_name : "(null)",
	          ldev ? ldev->dev_name : "(null)");

	ldev->fd = -1;
	ldev->device.irq_info = (void *)-1;

	ldev->sdev = sysfs_open_device(lbus->bus_name, ldev->dev_name);
	if (!ldev->sdev) {
		metal_log(METAL_LOG_ERROR, "device %s:%s not found\n",
			  lbus->bus_name, ldev->dev_name);
		probe_log("metal_uio_dev_open: sysfs_open_device failed for %s:%s",
		          lbus->bus_name, ldev->dev_name);
		return -ENODEV;
	}
	probe_log("metal_uio_dev_open: opened sdev path=%s", ldev->sdev->path);
	metal_log(METAL_LOG_DEBUG, "opened sysfs device %s:%s\n",
		  lbus->bus_name, ldev->dev_name);

	result = metal_uio_dev_bind(ldev, ldrv);
	probe_log("metal_uio_dev_open: metal_uio_dev_bind returned %d", result);
	if (result)
		return result;

	result = snprintf(path, sizeof(path), "%s/uio", ldev->sdev->path);
	probe_log("metal_uio_dev_open: snprintf path='%s' result=%d sizeof(path)=%zu",
	          path, result, (size_t)sizeof(path));
	if (result >= (int)sizeof(path))
		return -EOVERFLOW;
	dlist = sysfs_open_directory_list(path);
	if (!dlist) {
		metal_log(METAL_LOG_ERROR, "failed to scan class path %s\n",
			  path);
		probe_log("metal_uio_dev_open: sysfs_open_directory_list returned NULL for path=%s",
		          path);
		return -errno;
	}
	probe_log("metal_uio_dev_open: opened dlist for path=%s, dlist=%p", path, (void *)dlist);

	dlist_for_each_data(dlist, instance, char) {
		probe_log("metal_uio_dev_open: in dlist loop: instance ptr=%p", (void *)instance);
		/* Log instance string carefully: instance may be a pointer to read-only memory */
		if (instance)
			probe_log("metal_uio_dev_open: instance string=\"%s\"", instance);
		else
			probe_log("metal_uio_dev_open: instance is NULL");

		result = snprintf(ldev->cls_path, sizeof(ldev->cls_path),
				  "%s/%s", path, instance);
		probe_log("metal_uio_dev_open: snprintf cls_path='%s' result=%d sizeof(ldev->cls_path)=%zu",
		          ldev->cls_path, result, (size_t)sizeof(ldev->cls_path));
		if (result >= (int)sizeof(ldev->cls_path))
			return -EOVERFLOW;

		result = snprintf(ldev->dev_path, sizeof(ldev->dev_path),
				  "/dev/%s", instance);
		probe_log("metal_uio_dev_open: snprintf dev_path='%s' result=%d sizeof(ldev->dev_path)=%zu",
		          ldev->dev_path, result, (size_t)sizeof(ldev->dev_path));
		if (result >= (int)sizeof(ldev->dev_path))
			return -EOVERFLOW;
		break;
	}
	sysfs_close_list(dlist);
	probe_log("metal_uio_dev_open: closed dlist; cls_path='%s' dev_path='%s'",
	          ldev->cls_path, ldev->dev_path);

	if (sysfs_path_is_dir(ldev->cls_path) != 0) {
		metal_log(METAL_LOG_ERROR, "invalid device class path %s\n",
			  ldev->cls_path);
		probe_log("metal_uio_dev_open: sysfs_path_is_dir failed for cls_path=%s", ldev->cls_path);
		return -ENODEV;
	}

	i = 0;
	do {
		if (!access(ldev->dev_path, F_OK))
			break;
		usleep(10);
		i++;
	} while (i < 1000);
	probe_log("metal_uio_dev_open: wait loop finished after %d iterations", i);
	if (i >= 1000) {
		metal_log(METAL_LOG_ERROR, "failed to open file %s, timeout.\n",
			  ldev->dev_path);
		probe_log("metal_uio_dev_open: timeout waiting for dev_path=%s", ldev->dev_path);
		return -ENODEV;
	}
	result = metal_open(ldev->dev_path, 0);
	probe_log("metal_uio_dev_open: metal_open('%s') returned %d", ldev->dev_path, result);
	if (result < 0) {
		metal_log(METAL_LOG_ERROR, "failed to open device %s\n",
			  ldev->dev_path, strerror(-result));
		probe_log("metal_uio_dev_open: metal_open failed for %s error=%s", ldev->dev_path, strerror(-result));
		return result;
	}
	ldev->fd = result;

	metal_log(METAL_LOG_DEBUG, "opened %s:%s as %s\n",
		  lbus->bus_name, ldev->dev_name, ldev->dev_path);
	probe_log("metal_uio_dev_open: assigned fd=%d for dev_path=%s", ldev->fd, ldev->dev_path);

	for (i = 0, result = 0; !result && i < METAL_MAX_DEVICE_REGIONS; i++) {
		phys = &ldev->region_phys[ldev->device.num_regions];
		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "offset", &offset));
		probe_log("metal_uio_dev_open: map[%d] offset read result=%d offset=%lu", i, result, offset);
		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "addr", phys));
		probe_log("metal_uio_dev_open: map[%d] addr read result=%d phys=%lu", i, result, (unsigned long)*phys);
		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "size", &size));
		probe_log("metal_uio_dev_open: map[%d] size read result=%d size=%lu", i, result, size);
		result = (result ? result :
			 metal_map(ldev->fd, i * getpagesize(), size, 0, 0, &virt));
		probe_log("metal_uio_dev_open: map[%d] metal_map result=%d virt=%p", i, result, virt);
		if (!result) {
			io = &ldev->device.regions[ldev->device.num_regions];
			metal_io_init(io, virt, phys, size, -1, 0, NULL);
			ldev->device.num_regions++;
			probe_log("metal_uio_dev_open: region %d initialised virt=%p phys=%lu size=%lu",
				  ldev->device.num_regions - 1, virt, (unsigned long)*phys, size);
		}
	}

	irq_info = 1;
	if (write(ldev->fd, &irq_info, sizeof(irq_info)) <= 0) {
		metal_log(METAL_LOG_INFO,
			  "%s: No IRQ for device %s.\n",
			  __func__, ldev->dev_name);
		ldev->device.irq_num =  0;
		ldev->device.irq_info = (void *)-1;
		probe_log("metal_uio_dev_open: write returned <=0; no IRQ; fd=%d irq_info=%d", ldev->fd, irq_info);
	} else {
		ldev->device.irq_num =  1;
		ldev->device.irq_info = (void *)(intptr_t)ldev->fd;
		metal_linux_irq_register_dev(&ldev->device, ldev->fd);
		probe_log("metal_uio_dev_open: IRQ enabled fd=%d irq_info=%p; registered device",
		          ldev->fd, ldev->device.irq_info);
	}

	probe_log("metal_uio_dev_open: exit success ldev=%p fd=%d num_regions=%d",
	          (void *)ldev, ldev->fd, ldev->device.num_regions);
	return 0;
}
