#include <stdio.h>
#include <errno.h>
#include <string.h>
#include <stdint.h>
#include <sys/types.h>
#include <time.h>

/* Probe helper: append a single-line record to /tmp/logs/128.txt. */
static void probe_log(const char *event, void *addr, off_t offset, uint64_t entry, int pread_ret, int err)
{
    FILE *f = fopen("/tmp/logs/128.txt", "a");
    if (f == NULL) {
        /* If logging cannot be performed, do not alter program flow. */
        return;
    }

    time_t t = time(NULL);
    /* Write a compact, machine-parseable record. */
    fprintf(f, "%ld | %s | addr=%p | offset=0x%llx | entry=0x%llx | pread_ret=%d | errno=%d | errmsg=%s\n",
            (long)t,
            event,
            addr,
            (unsigned long long)offset,
            (unsigned long long)entry,
            pread_ret,
            err,
            (err != 0) ? strerror(err) : "");

    fclose(f);
}

static int metal_virt2phys(void *addr, unsigned long *phys)
{
    off_t offset;
    uint64_t entry;
    int error;

    /* Log entry to function. */
    probe_log("enter", addr, 0, 0, 0, 0);

    if (_metal.pagemap_fd < 0) {
        /* Log reason for early return. */
        probe_log("no_pagemap_fd", addr, 0, 0, 0, EINVAL);
        return -EINVAL;
    }

    offset = ((uintptr_t)addr >> _metal.page_shift) * sizeof(entry);
    /* Log before pread with computed offset. */
    probe_log("pread_before", addr, offset, 0, 0, 0);

    error = pread(_metal.pagemap_fd, &entry, sizeof(entry), offset);

    /* Log result of pread. If pread returns -1, errno holds the error. */
    probe_log("pread_after", addr, offset, entry, error, (error < 0) ? errno : 0);

    if (error < 0) {
        metal_log(METAL_LOG_ERROR, "failed pagemap pread (offset %llx) - %s\n",
                  (unsigned long long)offset, strerror(errno));
        /* Log the failure return value as well. */
        probe_log("pread_failed_return", addr, offset, entry, error, errno);
        return -errno;
    }

    /* Check page present and not swapped. */
    if ((entry >> 62) != 2) {
        metal_log(METAL_LOG_ERROR, "pagemap page not present, %llx -> %llx\n",
                  (unsigned long long)offset, (unsigned long long)entry);
        probe_log("page_not_present", addr, offset, entry, error, ENOENT);
        return -ENOENT;
    }

    *phys = (entry & ((1ULL << 54) - 1)) << _metal.page_shift;

    /* Log successful translation and resulting physical address. */
    probe_log("success", addr, offset, entry, 0, 0);
    return 0;
}
