static int metal_virt2phys(void *addr, unsigned long *phys)
{
	off_t offset;
	uint64_t entry;
	int error;
	FILE *log = fopen("/tmp/logs/131.txt", "a");

	/* Probe: record function entry and pointer values */
	if (log) {
		fprintf(log, "ENTER metal_virt2phys: addr=%p, phys=%p\n", addr, (void *)phys);
		fflush(log);
	}

	if (_metal.pagemap_fd < 0) {
		if (log) {
			fprintf(log, "pagemap_fd invalid: %d -> returning -EINVAL\n", _metal.pagemap_fd);
			fclose(log);
		}
		return -EINVAL;
	}

	/* Compute offset from the input pointer (note: addr is not const-qualified)
	 * Probe this value so other agents can reproduce the scenario where the
	 * pointer is only read (cast to uintptr_t) and not modified. */
	offset = ((uintptr_t)addr >> _metal.page_shift) * sizeof(entry);
	if (log) {
		fprintf(log, "computed offset: addr=(%p) -> uintptr=0x%llx, page_shift=%d, offset=0x%llx\n",
			addr, (unsigned long long)((uintptr_t)addr), _metal.page_shift,
			(unsigned long long)offset);
		fflush(log);
	}

	error = pread(_metal.pagemap_fd, &entry, sizeof(entry), offset);
	if (log) {
		if (error < 0)
			fprintf(log, "pread failed: pagemap_fd=%d, offset=0x%llx, error=%d, errno=%d (%s)\n",
				_metal.pagemap_fd, (unsigned long long)offset, error, errno, strerror(errno));
		else
			fprintf(log, "pread returned bytes=%d, entry=0x%llx\n", error, (unsigned long long)entry);
		fflush(log);
	}

	if (error < 0) {
		metal_log(METAL_LOG_ERROR, "failed pagemap pread (offset %llx) - %s\n",
			  (unsigned long long)offset, strerror(errno));
		if (log) {
			fprintf(log, "RETURNING -errno: -%d\n", errno);
			fclose(log);
		}
		return -errno;
	}

	/* Check page present and not swapped. */
	if ((entry >> 62) != 2) {
		metal_log(METAL_LOG_ERROR, "pagemap page not present, %llx -> %llx\n",
			  (unsigned long long)offset, (unsigned long long)entry);
		if (log) {
			fprintf(log, "page not present check failed: entry=0x%llx -> returning -ENOENT\n", (unsigned long long)entry);
			fclose(log);
		}
		return -ENOENT;
	}

	*phys = (entry & ((1ULL << 54) - 1)) << _metal.page_shift;
	if (log) {
		fprintf(log, "resolved phys=0x%lx written to *phys=%p -> returning 0\n", *phys, (void *)phys);
		fclose(log);
	}
	return 0;
}
