#include <stdio.h>
#include <stdarg.h>
#include <time.h>

/* Probe helper: append formatted log lines to /tmp/logs/267.txt. */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/267.txt", "a");
  if (NULL == f)
    return;
  {
    time_t t = time (NULL);
    (void) fprintf (f, "[%ld] ", (long) t);
  }
  va_list ap;
  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);
  (void) fprintf (f, "\n");
  (void) fclose (f);
}

static void
mhd_probe_log_ptr (const char *name, const void *p)
{
  mhd_probe_log ("%s=%p", name, p);
}

static enum MHD_Result
MHD_accept_connection (struct MHD_Daemon *daemon)
{
  struct sockaddr_storage addrstorage;
  socklen_t addrlen;
  MHD_socket s;
  MHD_socket fd;
  bool sk_nonbl;
  bool sk_spipe_supprs;
  bool sk_cloexec;
  enum MHD_tristate sk_non_ip;
#if defined(_DEBUG) && defined (USE_ACCEPT4)
  const bool use_accept4 = ! daemon->avoid_accept4;
#elif defined (USE_ACCEPT4)
  static const bool use_accept4 = true;
#else  /* ! USE_ACCEPT4 && ! _DEBUG */
  static const bool use_accept4 = false;
#endif /* ! USE_ACCEPT4 && ! _DEBUG */

#ifdef MHD_USE_THREADS
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Log entry and initial daemon state useful to reproduce accept flow */
  mhd_probe_log ("MHD_accept_connection ENTRY: daemon=%p", (void *) daemon);
  /* log some commonly available members; cast integers to long where needed */
  mhd_probe_log ("listen_fd=%ld was_quiesced=%d connections=%u",
                 (long) daemon->listen_fd,
                 (int) daemon->was_quiesced,
                 (unsigned int) daemon->connections);
#if defined(_DEBUG) && defined (USE_ACCEPT4)
  mhd_probe_log ("avoid_accept4=%d use_accept4=%d",
                 (int) daemon->avoid_accept4,
                 (int) use_accept4);
#else
  mhd_probe_log ("use_accept4=%d", (int) use_accept4);
#endif

  if ( (MHD_INVALID_SOCKET == (fd = daemon->listen_fd)) ||
       (daemon->was_quiesced) )
  {
    mhd_probe_log ("early return: invalid listen fd or was_quiesced: fd=%ld was_quiesced=%d",
                   (long) fd, (int) daemon->was_quiesced);
    return MHD_NO;
  }

  addrlen = (socklen_t) sizeof (addrstorage);
  memset (&addrstorage,
          0,
          (size_t) addrlen);
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen;
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

  /* Initialise with default values to avoid compiler warnings */
  sk_nonbl = false;
  sk_spipe_supprs = false;
  sk_cloexec = false;
  s = MHD_INVALID_SOCKET;

#ifdef USE_ACCEPT4
  if (use_accept4 &&
      (MHD_INVALID_SOCKET !=
       (s = accept4 (fd,
                     (struct sockaddr *) &addrstorage,
                     &addrlen,
                     SOCK_CLOEXEC_OR_ZERO | SOCK_NONBLOCK_OR_ZERO
                     | SOCK_NOSIGPIPE_OR_ZERO))))
  {
    sk_nonbl = (SOCK_NONBLOCK_OR_ZERO != 0);
#ifndef MHD_WINSOCK_SOCKETS
    sk_spipe_supprs = (SOCK_NOSIGPIPE_OR_ZERO != 0);
#else  /* MHD_WINSOCK_SOCKETS */
    sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
    sk_cloexec = (SOCK_CLOEXEC_OR_ZERO != 0);
    mhd_probe_log ("accept4 succeeded: s=%ld sk_nonbl=%d sk_spipe_supprs=%d sk_cloexec=%d",
                   (long) s, (int) sk_nonbl, (int) sk_spipe_supprs, (int) sk_cloexec);
  }
#endif /* USE_ACCEPT4 */
#if defined(_DEBUG) || ! defined(USE_ACCEPT4)
  if (! use_accept4 &&
      (MHD_INVALID_SOCKET !=
       (s = accept (fd,
                    (struct sockaddr *) &addrstorage,
                    &addrlen))))
  {
#ifdef MHD_ACCEPT_INHERIT_NONBLOCK
    sk_nonbl = daemon->listen_nonblk;
#else  /* ! MHD_ACCEPT_INHERIT_NONBLOCK */
    sk_nonbl = false;
#endif /* ! MHD_ACCEPT_INHERIT_NONBLOCK */
#ifndef MHD_WINSOCK_SOCKETS
    sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
    sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
    sk_cloexec = false;
    mhd_probe_log ("accept succeeded: s=%ld sk_nonbl=%d sk_spipe_supprs=%d sk_cloexec=%d",
                   (long) s, (int) sk_nonbl, (int) sk_spipe_supprs, (int) sk_cloexec);
  }
#endif /* _DEBUG || !USE_ACCEPT4 */

  if (MHD_INVALID_SOCKET == s)
  {
    const int err = MHD_socket_get_error_ ();

    /* Log the accept failure and socket error */
    mhd_probe_log ("accept failed: s=%ld err=%d", (long) s, err);
#ifdef HAVE_MESSAGES
    if (! MHD_SCKT_ERR_IS_EAGAIN_ (err) )
      mhd_probe_log ("accept error string: %s", MHD_socket_strerr_ (err));
#endif

    /* This could be a common occurrence with multiple worker threads */
    if (MHD_SCKT_ERR_IS_ (err,
                          MHD_SCKT_EINVAL_))
    {
      mhd_probe_log ("accept early shutdown, EINVAL-like, returning MHD_NO");
      return MHD_NO;   /* can happen during shutdown */
    }
    if (MHD_SCKT_ERR_IS_DISCNN_BEFORE_ACCEPT_ (err))
    {
      mhd_probe_log ("client disconnected before accept, returning MHD_NO");
      return MHD_NO;   /* do not print error if client just disconnected early */
    }
#ifdef HAVE_MESSAGES
    if (! MHD_SCKT_ERR_IS_EAGAIN_ (err) )
      MHD_DLOG (daemon,
                _ ("Error accepting connection: %s\n"),
                MHD_socket_strerr_ (err));
#endif
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err) )
    {
      /* system/process out of resources */
      if (0 == daemon->connections)
      {
#ifdef HAVE_MESSAGES
        /* Not setting 'at_limit' flag, as there is no way it
           would ever be cleared.  Instead trying to produce
           bit fat ugly warning. */
        MHD_DLOG (daemon,
                  _ ("Hit process or system resource limit at FIRST " \
                     "connection. This is really bad as there is no sane " \
                     "way to proceed. Will try busy waiting for system " \
                     "resources to become magically available.\n"));
#endif
        mhd_probe_log ("low resources at FIRST connection; connections==0");
      }
      else
      {
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
        MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif
        mhd_probe_log ("low resources at connections=%u, setting at_limit true",
                       (unsigned int) daemon->connections);
        daemon->at_limit = true;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
        MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("Hit process or system resource limit at %u " \
                     "connections, temporarily suspending accept(). " \
                     "Consider setting a lower MHD_OPTION_CONNECTION_LIMIT.\n"),
                  (unsigned int) daemon->connections);
#endif
      }
    }
    return MHD_NO;
  }

  sk_non_ip = daemon->listen_is_unix;
  if (0 >= addrlen)
  {
#ifdef HAVE_MESSAGES
    if (_MHD_NO != daemon->listen_is_unix)
      MHD_DLOG (daemon,
                _ ("Accepted socket has zero-length address. "
                   "Processing the new socket as a socket with " \
                   "unknown type.\n"));
#endif
    addrlen = 0;
    sk_non_ip = _MHD_YES; /* IP-type addresses have non-zero length */
    mhd_probe_log ("addrlen was zero or negative; forced sk_non_ip=_MHD_YES");
  }
  if (((socklen_t) sizeof (addrstorage)) < addrlen)
  {
    /* Should not happen as 'sockaddr_storage' must be large enough to
     * store any address supported by the system. */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Accepted socket address is larger than expected by " \
                 "system headers. Processing the new socket as a socket with " \
                 "unknown type.\n"));
#endif
    addrlen = 0;
    sk_non_ip = _MHD_YES; /* IP-type addresses must fit */
    mhd_probe_log ("addrlen larger than sockaddr_storage; forced sk_non_ip=_MHD_YES");
  }

  mhd_probe_log ("before nonblocking set: s=%ld sk_nonbl(initial)=%d",
                 (long) s, (int) sk_nonbl);
  if (! sk_nonbl && ! MHD_socket_nonblocking_ (s))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on incoming connection " \
                 "socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
    mhd_probe_log ("failed to set nonblocking on s=%ld: last_err=%s",
                   (long) s, MHD_socket_last_strerr_ ());
  }
  else
    sk_nonbl = true;

  mhd_probe_log ("after nonblocking: s=%ld sk_nonbl=%d", (long) s, (int) sk_nonbl);

  if (! sk_cloexec && ! MHD_socket_noninheritable_ (s))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on incoming connection " \
                 "socket.\n"));
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
    mhd_probe_log ("failed to set noninheritable on s=%ld", (long) s);
  }

#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs && ! MHD_socket_nosignal_ (s))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on incoming connection " \
                 "socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
    mhd_probe_log ("failed to suppress SIGPIPE on s=%ld: last_err=%s",
                   (long) s, MHD_socket_last_strerr_ ());
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      (void) MHD_socket_close_ (s);
      mhd_probe_log ("closed s=%ld due to unsuppressable SIGPIPE and sigpipe not blocked; returning MHD_NO",
                     (long) s);
      return MHD_NO;
    }
#endif /* MSG_NOSIGNAL */
  }
  else
    sk_spipe_supprs = true;
#endif /* MHD_socket_nosignal_ */
#ifdef HAVE_MESSAGES
#if _MHD_DEBUG_CONNECT
  MHD_DLOG (daemon,
            _ ("Accepted connection on socket %d\n"),
            s);
#endif
#endif

  /* Log final connection state before handing off to internal_add_connection */
  mhd_probe_log ("handing off: daemon=%p s=%ld addrlen=%u sk_nonbl=%d sk_spipe_supprs=%d sk_cloexec=%d sk_non_ip=%d connections=%u",
                 (void *) daemon,
                 (long) s,
                 (unsigned int) addrlen,
                 (int) sk_nonbl,
                 (int) sk_spipe_supprs,
                 (int) sk_cloexec,
                 (int) sk_non_ip,
                 (unsigned int) daemon->connections);

  (void) internal_add_connection (daemon,
                                  s,
                                  &addrstorage,
                                  addrlen,
                                  false,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  sk_non_ip);
  mhd_probe_log ("internal_add_connection returned; leaving MHD_accept_connection");
  return MHD_YES;
}
