#include <stdio.h>
#include <stddef.h>
#include <stdlib.h>

/* Probe logging function: append control-flow and state information to /tmp/logs/51.txt */
static void mhd_probe_log(const char *stage,
                          const struct MHD_Response *response,
                          const char *header,
                          const char *content)
{
  FILE *f = fopen("/tmp/logs/51.txt", "a");
  if (NULL == f)
    return;
  fprintf(f, "[%s] header=%s header_ptr=%p content=%s content_ptr=%p response=%p",
          stage,
          (NULL != header) ? header : "(null)",
          (const void *) header,
          (NULL != content) ? content : "(null)",
          (const void *) content,
          (const void *) response);
  if (NULL != response) {
    /* response->flags and response->flags_auto are accessed by the original code; print them */
    fprintf(f, " flags=0x%llx flags_auto=0x%llx",
            (unsigned long long) response->flags,
            (unsigned long long) response->flags_auto);
  }
  fprintf(f, "\n");
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  /* Log entry into function */
  mhd_probe_log("ENTRY", response, header, content);

  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION)) {
    /* Log the specific branch where the potential const-discard violation may occur */
    mhd_probe_log("BRANCH_CONNECTION_BEFORE_CALL", response, header, content);
    /* Preserve original call and logic */
    enum MHD_Result res = add_response_header_connection (response, content);
    /* Log the result of the call */
    mhd_probe_log("BRANCH_CONNECTION_AFTER_CALL", response, header, content);
    return res;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    mhd_probe_log("BRANCH_TRANSFER_ENCODING", response, header, content);
    if (! MHD_str_equal_caseless_ (content, "chunked")) {
      mhd_probe_log("RETURN_NOT_CHUNKED", response, header, content);
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    }
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED)) {
      mhd_probe_log("RETURN_ALREADY_CHUNKED", response, header, content);
      return MHD_YES;  /* Already has "chunked" encoding header */
    }
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ) {
      mhd_probe_log("RETURN_HAS_CONTENT_LENGTH_NO_INSANITY", response, header, content);
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      mhd_probe_log("RETURN_ADDED_TRANS_ENC_CHUNKED", response, header, content);
      return MHD_YES;
    }
    mhd_probe_log("RETURN_ADD_RESPONSE_ENTRY_FAILED_TRANS_ENCODING", response, header, content);
    return MHD_NO;
  }
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    mhd_probe_log("BRANCH_DATE", response, header, content);
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      mhd_assert (NULL != hdr);
      _MHD_remove_header (response, hdr);
      if (NULL != hdr->value)
        free (hdr->value);
      free (hdr->header);
      free (hdr);
      mhd_probe_log("DATE_CLEARED_PREVIOUS", response, header, content);
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      mhd_probe_log("RETURN_ADDED_DATE", response, header, content);
      return MHD_YES;
    }
    mhd_probe_log("RETURN_ADD_RESPONSE_ENTRY_FAILED_DATE", response, header, content);
    return MHD_NO;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    mhd_probe_log("BRANCH_CONTENT_LENGTH", response, header, content);
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        mhd_probe_log("RETURN_ADDED_CONTENT_LENGTH", response, header, content);
        return MHD_YES;
      }
    }
    mhd_probe_log("RETURN_ADD_RESPONSE_ENTRY_FAILED_CONTENT_LENGTH", response, header, content);
    return MHD_NO;
  }

  mhd_probe_log("DEFAULT_ADD_RESPONSE_ENTRY_CALL", response, header, content);
  return add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
}
