#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper - appends logs to /tmp/logs/272.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/272.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

void
MHD_connection_handle_write (struct MHD_Connection *connection)
{
  struct MHD_Response *response;
  ssize_t ret;

  /* Log entry and core state */
  mhd_probe_log ("ENTER MHD_connection_handle_write: state=%d suspended=%d tls_state=%d write_send=%zu write_append=%zu rsp_write_position=%zu url=%s",
                 (int) connection->state,
                 (int) connection->suspended,
                 (int) connection->tls_state,
                 (size_t) connection->write_buffer_send_offset,
                 (size_t) connection->write_buffer_append_offset,
                 (size_t) connection->rp.rsp_write_position,
                 (connection->rq.url ? connection->rq.url : "(null)"));

  if (connection->suspended)
  {
    mhd_probe_log ("EXIT: suspended = true, returning immediately (state=%d)", (int) connection->state);
    return;
  }

#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    if (MHD_TLS_CONN_CONNECTED > connection->tls_state)
    {
      mhd_probe_log ("TLS handshake needed: tls_state=%d", (int) connection->tls_state);
      if (! MHD_run_tls_handshake_ (connection))
      {
        mhd_probe_log ("TLS handshake not completed, returning (tls_state=%d)", (int) connection->tls_state);
        return;
      }
      mhd_probe_log ("TLS handshake completed, continuing (tls_state=%d)", (int) connection->tls_state);
    }
  }
#endif /* HTTPS_SUPPORT */

#if DEBUG_STATES
  MHD_DLOG (connection->daemon,
            _ ("In function %s handling connection at state: %s\n"),
            MHD_FUNC_,
            MHD_state_to_string (connection->state));
#endif
  switch (connection->state)
  {
  case MHD_CONNECTION_INIT:
  case MHD_CONNECTION_REQ_LINE_RECEIVING:
  case MHD_CONNECTION_REQ_LINE_RECEIVED:
  case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
  case MHD_CONNECTION_HEADERS_RECEIVED:
  case MHD_CONNECTION_HEADERS_PROCESSED:
    mhd_probe_log ("ASSERTION PATH: reached mhd_assert(0) for header/initial states, state=%d", (int) connection->state);
    mhd_assert (0);
    mhd_probe_log ("RETURN after mhd_assert(0) for state=%d", (int) connection->state);
    return;
  case MHD_CONNECTION_CONTINUE_SENDING:
    ret = MHD_send_data_ (connection,
                          &HTTP_100_CONTINUE
                          [connection->continue_message_write_offset],
                          MHD_STATICSTR_LEN_ (HTTP_100_CONTINUE)
                          - connection->continue_message_write_offset,
                          true);
    mhd_probe_log ("CONTINUE_SENDING: sent attempt ret=%zd continue_offset=%zu",
                   (ssize_t) ret,
                   connection->continue_message_write_offset);
    if (ret < 0)
    {
      if (MHD_ERR_AGAIN_ == ret)
      {
        mhd_probe_log ("CONTINUE_SENDING: would block (MHD_ERR_AGAIN_), returning");
        return;
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (connection->daemon,
                _ ("Failed to send data in request for %s.\n"),
                connection->rq.url);
#endif
      mhd_probe_log ("CONTINUE_SENDING: error ret=%zd, closing connection, url=%s",
                     (ssize_t) ret,
                     (connection->rq.url ? connection->rq.url : "(null)"));
      CONNECTION_CLOSE_ERROR (connection,
                              NULL);
      return;
    }
#if _MHD_DEBUG_SEND_DATA
    fprintf (stderr,
             _ ("Sent 100 continue response: `%.*s'\n"),
             (int) ret,
             &HTTP_100_CONTINUE[connection->continue_message_write_offset]);
#endif
    connection->continue_message_write_offset += (size_t) ret;
    MHD_update_last_activity_ (connection);
    mhd_probe_log ("CONTINUE_SENDING: progress continue_message_write_offset=%zu", connection->continue_message_write_offset);
    return;
  case MHD_CONNECTION_BODY_RECEIVING:
  case MHD_CONNECTION_BODY_RECEIVED:
  case MHD_CONNECTION_FOOTERS_RECEIVING:
  case MHD_CONNECTION_FOOTERS_RECEIVED:
  case MHD_CONNECTION_FULL_REQ_RECEIVED:
    mhd_probe_log ("ASSERTION PATH: reached mhd_assert(0) for body/footer/fullreq states, state=%d", (int) connection->state);
    mhd_assert (0);
    mhd_probe_log ("RETURN after mhd_assert(0) for state=%d", (int) connection->state);
    return;
  case MHD_CONNECTION_START_REPLY:
    mhd_probe_log ("ASSERTION PATH: reached mhd_assert(0) for START_REPLY state=%d", (int) connection->state);
    mhd_assert (0);
    mhd_probe_log ("RETURN after mhd_assert(0) for START_REPLY state=%d", (int) connection->state);
    return;
  case MHD_CONNECTION_HEADERS_SENDING:
    {
      struct MHD_Response *const resp = connection->rp.response;
      const size_t wb_ready = connection->write_buffer_append_offset
                              - connection->write_buffer_send_offset;
      mhd_assert (connection->write_buffer_append_offset >= \
                  connection->write_buffer_send_offset);
      mhd_assert (NULL != resp);
      mhd_assert ( (0 == resp->data_size) || \
                   (0 == resp->data_start) || \
                   (NULL != resp->crc) );
      mhd_assert ( (0 == connection->rp.rsp_write_position) || \
                   (resp->total_size ==
                    connection->rp.rsp_write_position) );
      mhd_assert ((MHD_CONN_MUST_UPGRADE != connection->keepalive) || \
                  (! connection->rp.props.send_reply_body));

      mhd_probe_log ("HEADERS_SENDING: wb_ready=%zu resp_total=%zu resp_data_size=%zu send_reply_body=%d chunked=%d rsp_write_position=%zu",
                     wb_ready,
                     (size_t) resp->total_size,
                     (size_t) resp->data_size,
                     (int) connection->rp.props.send_reply_body,
                     (int) connection->rp.props.chunked,
                     (size_t) connection->rp.rsp_write_position);

      if ( (connection->rp.props.send_reply_body) &&
           (NULL == resp->crc) &&
           (NULL == resp->data_iov) &&
           /* TODO: remove the next check as 'send_reply_body' is used */
           (0 == connection->rp.rsp_write_position) &&
           (! connection->rp.props.chunked) )
      {
        mhd_assert (resp->total_size >= resp->data_size);
        mhd_assert (0 == resp->data_start);
        /* Send response headers alongside the response body, if the body
         * data is available. */
        ret = MHD_send_hdr_and_body_ (connection,
                                      &connection->write_buffer
                                      [connection->write_buffer_send_offset],
                                      wb_ready,
                                      false,
                                      resp->data,
                                      resp->data_size,
                                      (resp->total_size == resp->data_size));
        mhd_probe_log ("HEADERS_SENDING: called send_hdr_and_body (with body), ret=%zd", (ssize_t) ret);
      }
      else
      {
        /* This is response for HEAD request or reply body is not allowed
         * for any other reason or reply body is dynamically generated. */
        /* Do not send the body data even if it's available. */
        ret = MHD_send_hdr_and_body_ (connection,
                                      &connection->write_buffer
                                      [connection->write_buffer_send_offset],
                                      wb_ready,
                                      false,
                                      NULL,
                                      0,
                                      ((0 == resp->total_size) ||
                                       (! connection->rp.props.send_reply_body)
                                      ));
        mhd_probe_log ("HEADERS_SENDING: called send_hdr_and_body (no body), ret=%zd", (ssize_t) ret);
      }

      if (ret < 0)
      {
        if (MHD_ERR_AGAIN_ == ret)
        {
          mhd_probe_log ("HEADERS_SENDING: would block (MHD_ERR_AGAIN_), returning");
          return;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("Failed to send the response headers for the " \
                     "request for `%s'. Error: %s\n"),
                  connection->rq.url,
                  str_conn_error_ (ret));
#endif
        mhd_probe_log ("HEADERS_SENDING: error ret=%zd, closing connection, url=%s",
                       (ssize_t) ret,
                       (connection->rq.url ? connection->rq.url : "(null)"));
        CONNECTION_CLOSE_ERROR (connection,
                                NULL);
        return;
      }
      /* 'ret' is not negative, it's safe to cast it to 'size_t'. */
      if (((size_t) ret) > wb_ready)
      {
        /* The complete header and some response data have been sent,
         * update both offsets. */
        mhd_assert (0 == connection->rp.rsp_write_position);
        mhd_assert (! connection->rp.props.chunked);
        mhd_assert (connection->rp.props.send_reply_body);
        connection->write_buffer_send_offset += wb_ready;
        connection->rp.rsp_write_position = ((size_t) ret) - wb_ready;
        mhd_probe_log ("HEADERS_SENDING: sent headers + body fragment, send_offset=%zu rsp_write_position=%zu",
                       connection->write_buffer_send_offset,
                       connection->rp.rsp_write_position);
      }
      else
      {
        connection->write_buffer_send_offset += (size_t) ret;
        mhd_probe_log ("HEADERS_SENDING: sent headers only, send_offset=%zu", connection->write_buffer_send_offset);
      }
      MHD_update_last_activity_ (connection);
      if (MHD_CONNECTION_HEADERS_SENDING != connection->state)
      {
        mhd_probe_log ("HEADERS_SENDING: state changed during send, new state=%d, returning", (int) connection->state);
        return;
      }
      check_write_done (connection,
                        MHD_CONNECTION_HEADERS_SENT);
      mhd_probe_log ("HEADERS_SENDING: check_write_done returned, state now=%d", (int) connection->state);
      return;
    }
