    mhd_probe_log ("HEADERS_SENT: nothing to do, returning");
    return;
  case MHD_CONNECTION_NORMAL_BODY_READY:
    response = connection->rp.response;
    mhd_probe_log ("NORMAL_BODY_READY: rsp_write_position=%zu response_total=%zu",
                   (size_t) connection->rp.rsp_write_position,
