#include <stdio.h>
#include <stdint.h>
#include <stddef.h>

/* Probe helper: append state and control-path information to /tmp/logs/273.txt
 * Does not change program logic, only records the runtime state for later
 * analysis. Safe-guards are used to avoid dereferencing NULL pointers. */
static void
mhd_probe_log (const struct MHD_Connection *connection, const char *point)
{
  FILE *f = fopen ("/tmp/logs/273.txt", "a");
  if (NULL == f)
    return;

  fprintf (f, "PROBE: %s\n", point);
  if (NULL == connection)
  {
    fprintf (f, "  connection: NULL\n\n");
    fclose (f);
    return;
  }

  fprintf (f, "  connection=%p\n", (const void *) connection);
  fprintf (f, "  state=%d suspended=%d\n", (int) connection->state,
           (int) connection->suspended);
#ifdef HTTPS_SUPPORT
  fprintf (f, "  tls_state=%d\n", (int) connection->tls_state);
#endif
  /* Safe prints of offsets/indices that are commonly present in the struct. */
  fprintf (f, "  continue_message_write_offset=%zu\n",
           (size_t) connection->continue_message_write_offset);
  fprintf (f, "  write_buffer_append_offset=%zu write_buffer_send_offset=%zu\n",
           (size_t) connection->write_buffer_append_offset,
           (size_t) connection->write_buffer_send_offset);

  /* rp and response may be present; check their pointers before deref. */
  fprintf (f, "  rp.response=%p rp.rsp_write_position=%zu\n",
           (void *) connection->rp.response,
           (size_t) connection->rp.rsp_write_position);
  if (NULL != connection->rp.response)
  {
    const struct MHD_Response *resp = connection->rp.response;
    fprintf (f, "    response.total_size=%zu data_size=%zu data_start=%zu\n",
             (size_t) resp->total_size,
             (size_t) resp->data_size,
             (size_t) resp->data_start);
    fprintf (f, "    response.crc=%p response.data_iov=%p\n",
             (void *) resp->crc,
             (void *) resp->data_iov);
  }

  /* Print some request related info if available. Avoid dereferencing NULLs. */
  fprintf (f, "  rq.url=%p daemon=%p\n",
           (void *) connection->rq.url,
           (void *) connection->daemon);

  fprintf (f, "---\n");
  fclose (f);
}

void
MHD_connection_handle_write (struct MHD_Connection *connection)
{
  struct MHD_Response *response;
  ssize_t ret;

  /* Entry probe */
  mhd_probe_log (connection, "entry");

  if (connection->suspended)
  {
    mhd_probe_log (connection, "early-return: suspended");
    return;
  }

#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    if (MHD_TLS_CONN_CONNECTED > connection->tls_state)
    {
      mhd_probe_log (connection, "before-tls-handshake");
      if (! MHD_run_tls_handshake_ (connection))
      {
        mhd_probe_log (connection, "early-return: tls-handshake-failed");
        return;
      }
      mhd_probe_log (connection, "after-tls-handshake-success");
    }
  }
#endif /* HTTPS_SUPPORT */

#if DEBUG_STATES
  MHD_DLOG (connection->daemon,
            _ ("In function %s handling connection at state: %s\n"),
            MHD_FUNC_,
            MHD_state_to_string (connection->state));
#endif
  switch (connection->state)
  {
  case MHD_CONNECTION_INIT:
  case MHD_CONNECTION_REQ_LINE_RECEIVING:
  case MHD_CONNECTION_REQ_LINE_RECEIVED:
  case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
  case MHD_CONNECTION_HEADERS_RECEIVED:
  case MHD_CONNECTION_HEADERS_PROCESSED:
    mhd_probe_log (connection, "assert-case: initial/request-headers states");
    mhd_assert (0);
    return;
  case MHD_CONNECTION_CONTINUE_SENDING:
    ret = MHD_send_data_ (connection,
                          &HTTP_100_CONTINUE
                          [connection->continue_message_write_offset],
                          MHD_STATICSTR_LEN_ (HTTP_100_CONTINUE)
                          - connection->continue_message_write_offset,
                          true);
    if (ret < 0)
    {
      mhd_probe_log (connection, "continue-sending: ret<0");
      if (MHD_ERR_AGAIN_ == ret)
      {
        mhd_probe_log (connection, "continue-sending: would-block (AGAIN)");
        return;
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (connection->daemon,
                _ ("Failed to send data in request for %s.\n"),
                connection->rq.url);
#endif
      mhd_probe_log (connection, "continue-sending: fatal send error, closing");
      CONNECTION_CLOSE_ERROR (connection,
                              NULL);
      return;
    }
#if _MHD_DEBUG_SEND_DATA
    fprintf (stderr,
             _ ("Sent 100 continue response: `%.*s'\n"),
             (int) ret,
             &HTTP_100_CONTINUE[connection->continue_message_write_offset]);
#endif
    connection->continue_message_write_offset += (size_t) ret;
    MHD_update_last_activity_ (connection);
    mhd_probe_log (connection, "continue-sending: success and returning");
    return;
  case MHD_CONNECTION_BODY_RECEIVING:
  case MHD_CONNECTION_BODY_RECEIVED:
  case MHD_CONNECTION_FOOTERS_RECEIVING:
  case MHD_CONNECTION_FOOTERS_RECEIVED:
  case MHD_CONNECTION_FULL_REQ_RECEIVED:
    mhd_probe_log (connection, "assert-case: body/footers/full-req states");
    mhd_assert (0);
    return;
  case MHD_CONNECTION_START_REPLY:
    mhd_probe_log (connection, "assert-case: start-reply");
    mhd_assert (0);
    return;
  case MHD_CONNECTION_HEADERS_SENDING:
    {
      struct MHD_Response *const resp = connection->rp.response;
      const size_t wb_ready = connection->write_buffer_append_offset
                              - connection->write_buffer_send_offset;
      mhd_assert (connection->write_buffer_append_offset >= \
                  connection->write_buffer_send_offset);
      mhd_assert (NULL != resp);
      mhd_assert ( (0 == resp->data_size) || \
                   (0 == resp->data_start) || \
                   (NULL != resp->crc) );
      mhd_assert ( (0 == connection->rp.rsp_write_position) || \
                   (resp->total_size ==
                    connection->rp.rsp_write_position) );
      mhd_assert ((MHD_CONN_MUST_UPGRADE != connection->keepalive) || \
                  (! connection->rp.props.send_reply_body));

      mhd_probe_log (connection, "headers-sending: enter");

      if ( (connection->rp.props.send_reply_body) &&
           (NULL == resp->crc) &&
           (NULL == resp->data_iov) &&
           /* TODO: remove the next check as 'send_reply_body' is used */
           (0 == connection->rp.rsp_write_position) &&
           (! connection->rp.props.chunked) )
      {
        mhd_assert (resp->total_size >= resp->data_size);
        mhd_assert (0 == resp->data_start);
        /* Send response headers alongside the response body, if the body
         * data is available. */
        ret = MHD_send_hdr_and_body_ (connection,
                                      &connection->write_buffer
                                      [connection->write_buffer_send_offset],
                                      wb_ready,
                                      false,
                                      resp->data,
                                      resp->data_size,
                                      (resp->total_size == resp->data_size));
      }
      else
      {
        /* This is response for HEAD request or reply body is not allowed
         * for any other reason or reply body is dynamically generated. */
        /* Do not send the body data even if it's available. */
        ret = MHD_send_hdr_and_body_ (connection,
                                      &connection->write_buffer
                                      [connection->write_buffer_send_offset],
                                      wb_ready,
                                      false,
                                      NULL,
                                      0,
                                      ((0 == resp->total_size) ||
                                       (! connection->rp.props.send_reply_body)
                                      ));
      }

      if (ret < 0)
      {
        mhd_probe_log (connection, "headers-sending: ret<0");
        if (MHD_ERR_AGAIN_ == ret)
        {
          mhd_probe_log (connection, "headers-sending: would-block (AGAIN)");
          return;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("Failed to send the response headers for the " \
                     "request for `%s'. Error: %s\n"),
                  connection->rq.url,
                  str_conn_error_ (ret));
#endif
        mhd_probe_log (connection, "headers-sending: fatal error, closing");
        CONNECTION_CLOSE_ERROR (connection,
                                NULL);
        return;
      }
      /* 'ret' is not negative, it's safe to cast it to 'size_t'. */
      if (((size_t) ret) > wb_ready)
      {
        /* The complete header and some response data have been sent,
         * update both offsets. */
        mhd_assert (0 == connection->rp.rsp_write_position);
        mhd_assert (! connection->rp.props.chunked);
        mhd_assert (connection->rp.props.send_reply_body);
        connection->write_buffer_send_offset += wb_ready;
        connection->rp.rsp_write_position = ((size_t) ret) - wb_ready;
      }
      else
        connection->write_buffer_send_offset += (size_t) ret;
      MHD_update_last_activity_ (connection);
      mhd_probe_log (connection, "headers-sending: done, checking state");
      if (MHD_CONNECTION_HEADERS_SENDING != connection->state)
        return;
      check_write_done (connection,
                        MHD_CONNECTION_HEADERS_SENT);
      mhd_probe_log (connection, "headers-sending: after check_write_done");
      return;
    }
  case MHD_CONNECTION_HEADERS_SENT:
    mhd_probe_log (connection, "headers-sent: nothing to do, return");
    return;
  case MHD_CONNECTION_NORMAL_BODY_READY:
    response = connection->rp.response;
    if (connection->rp.rsp_write_position <
        connection->rp.response->total_size)
    {
      uint64_t data_write_offset;

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
      if (NULL != response->crc)
        MHD_mutex_lock_chk_ (&response->mutex);
#endif
      if (MHD_NO == try_ready_normal_body (connection))
      {
        /* mutex was already unlocked by try_ready_normal_body */
        mhd_probe_log (connection, "normal-body-ready: try_ready_normal_body deferred");
        return;
      }
#if defined(_MHD_HAVE_SENDFILE)
      if (MHD_resp_sender_sendfile == connection->rp.resp_sender)
      {
        mhd_assert (NULL == response->data_iov);
        ret = MHD_send_sendfile_ (connection);
      }
      else /* combined with the next 'if' */
#endif /* _MHD_HAVE_SENDFILE */
      if (NULL != response->data_iov)
      {
        ret = MHD_send_iovec_ (connection,
                               &connection->rp.resp_iov,
                               true);
      }
      else
      {
        data_write_offset = connection->rp.rsp_write_position
                            - response->data_start;
        if (data_write_offset > (uint64_t) SIZE_MAX)
          MHD_PANIC (_ ("Data offset exceeds limit.\n"));
        ret = MHD_send_data_ (connection,
                              &response->data
                              [(size_t) data_write_offset],
                              response->data_size
                              - (size_t) data_write_offset,
                              true);
#if _MHD_DEBUG_SEND_DATA
        if (ret > 0)
          fprintf (stderr,
                   _ ("Sent %d-byte DATA response: `%.*s'\n"),
                   (int) ret,
                   (int) ret,
                   &rp.response->data[connection->rp.rsp_write_position
                                      - rp.response->data_start]);
#endif
      }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
      if (NULL != response->crc)
        MHD_mutex_unlock_chk_ (&response->mutex);
#endif
      if (ret < 0)
      {
        mhd_probe_log (connection, "normal-body-ready: ret<0");
        if (MHD_ERR_AGAIN_ == ret)
        {
          mhd_probe_log (connection, "normal-body-ready: would-block (AGAIN)");
          return;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("Failed to send the response body for the " \
                     "request for `%s'. Error: %s\n"),
                  connection->rq.url,
                  str_conn_error_ (ret));
#endif
        mhd_probe_log (connection, "normal-body-ready: fatal error, closing");
        CONNECTION_CLOSE_ERROR (connection,
                                NULL);
        return;
      }
      connection->rp.rsp_write_position += (size_t) ret;
      MHD_update_last_activity_ (connection);
    }
    if (connection->rp.rsp_write_position ==
        connection->rp.response->total_size)
      connection->state = MHD_CONNECTION_FULL_REPLY_SENT;
    mhd_probe_log (connection, "normal-body-ready: done");
    return;
  case MHD_CONNECTION_NORMAL_BODY_UNREADY:
    mhd_probe_log (connection, "assert-case: normal-body-unready");
    mhd_assert (0);
    return;
  case MHD_CONNECTION_CHUNKED_BODY_READY:
    ret = MHD_send_data_ (connection,
                          &connection->write_buffer
                          [connection->write_buffer_send_offset],
                          connection->write_buffer_append_offset
                          - connection->write_buffer_send_offset,
                          true);
    if (ret < 0)
    {
      mhd_probe_log (connection, "chunked-body-ready: ret<0");
      if (MHD_ERR_AGAIN_ == ret)
      {
        mhd_probe_log (connection, "chunked-body-ready: would-block (AGAIN)");
        return;
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (connection->daemon,
                _ ("Failed to send the chunked response body for the " \
                   "request for `%s'. Error: %s\n"),
                connection->rq.url,
                str_conn_error_ (ret));
#endif
      mhd_probe_log (connection, "chunked-body-ready: fatal error, closing");
      CONNECTION_CLOSE_ERROR (connection,
                              NULL);
      return;
    }
    connection->write_buffer_send_offset += (size_t) ret;
    MHD_update_last_activity_ (connection);
    if (MHD_CONNECTION_CHUNKED_BODY_READY != connection->state)
      return;
    check_write_done (connection,
                      (connection->rp.response->total_size ==
                       connection->rp.rsp_write_position) ?
                      MHD_CONNECTION_CHUNKED_BODY_SENT :
                      MHD_CONNECTION_CHUNKED_BODY_UNREADY);
    mhd_probe_log (connection, "chunked-body-ready: after check_write_done");
    return;
  case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
  case MHD_CONNECTION_CHUNKED_BODY_SENT:
    mhd_probe_log (connection, "assert-case: chunked-body-unready/sent");
    mhd_assert (0);
    return;
  case MHD_CONNECTION_FOOTERS_SENDING:
    ret = MHD_send_data_ (connection,
                          &connection->write_buffer
                          [connection->write_buffer_send_offset],
                          connection->write_buffer_append_offset
                          - connection->write_buffer_send_offset,
                          true);
    if (ret < 0)
    {
      mhd_probe_log (connection, "footers-sending: ret<0");
      if (MHD_ERR_AGAIN_ == ret)
      {
        mhd_probe_log (connection, "footers-sending: would-block (AGAIN)");
        return;
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (connection->daemon,
                _ ("Failed to send the footers for the " \
                   "request for `%s'. Error: %s\n"),
                connection->rq.url,
                str_conn_error_ (ret));
#endif
      mhd_probe_log (connection, "footers-sending: fatal error, closing");
      CONNECTION_CLOSE_ERROR (connection,
                              NULL);
      return;
    }
    connection->write_buffer_send_offset += (size_t) ret;
    MHD_update_last_activity_ (connection);
    if (MHD_CONNECTION_FOOTERS_SENDING != connection->state)
      return;
    check_write_done (connection,
                      MHD_CONNECTION_FULL_REPLY_SENT);
    mhd_probe_log (connection, "footers-sending: done");
    return;
  case MHD_CONNECTION_FULL_REPLY_SENT:
    mhd_probe_log (connection, "assert-case: full-reply-sent");
    mhd_assert (0);
    return;
  case MHD_CONNECTION_CLOSED:
    mhd_probe_log (connection, "closed: return");
    return;
#ifdef UPGRADE_SUPPORT
  case MHD_CONNECTION_UPGRADE:
    mhd_probe_log (connection, "assert-case: upgrade");
    mhd_assert (0);
    return;
#endif /* UPGRADE_SUPPORT */
  default:
    mhd_probe_log (connection, "default: internal error path");
    mhd_assert (0);
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Internal error.\n"));
    break;
  }
  mhd_probe_log (connection, "function-exit-end");
  return;
}
