#include <stdio.h>
#include <time.h>

/* Probe helper: log control flow and key state to /tmp/logs/690.txt. */
static void
mhd_probe_log_state (const char *stage, struct MHD_Daemon *daemon)
{
  FILE *f = fopen("/tmp/logs/690.txt", "a");
  if (f == NULL)
    return;

  time_t t = time(NULL);
  fprintf(f, "%s: time=%ld, daemon=%p", stage, (long)t, (void *) daemon);
  if (daemon != NULL)
    {
      /* daemon->master is compared to NULL in the original code, so treat as pointer */
      fprintf(f, ", master=%p", (void *) daemon->master);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
      fprintf(f, ", per_ip_connection_mutex=%p", (void *) &daemon->per_ip_connection_mutex);
#endif
    }
  fprintf(f, "\n");
  fclose(f);
}

static void
MHD_ip_count_lock (struct MHD_Daemon *daemon)
{
  /* Log state before the assertion (shows the value the assertion checks). */
  mhd_probe_log_state("enter_before_assert", daemon);

  /* Original assertion retained to preserve original logic. */
  mhd_assert (NULL == daemon->master);

  /* Log state after the assertion (assert is a no-op macro in the reported scenario).
     These two logs help reproduce/evidence that the assert expansion has no effect. */
  mhd_probe_log_state("after_assert", daemon);

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_probe_log_state("before_mutex_lock", daemon);
  MHD_mutex_lock_chk_ (&daemon->per_ip_connection_mutex);
  mhd_probe_log_state("after_mutex_lock", daemon);
#else
  (void) daemon;
  mhd_probe_log_state("no_threading_branch", daemon);
#endif
}
