#include <stdio.h>

static void
MHD_ip_count_lock (struct MHD_Daemon *daemon)
{
  mhd_assert (NULL == daemon->master);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  /* Log that threaded branch is taken (probe for control flow/state). */
  {
    FILE *f = fopen("/tmp/logs/690.txt", "a");
    if (f)
    {
      fprintf(f, "MHD_ip_count_lock: threaded branch taken. daemon=%p master=%p\n",
              (void *) daemon,
              (void *) (daemon ? daemon->master : NULL));
      fclose(f);
    }
  }
  MHD_mutex_lock_chk_ (&daemon->per_ip_connection_mutex);
#else
  /* Non-threaded build: record that the no-threads branch executed. This
     preserves the original suppression of unused-parameter warnings while
     emitting an observable log entry to demonstrate the execution path. */
  {
    FILE *f = fopen("/tmp/logs/690.txt", "a");
    if (f)
    {
      fprintf(f, "MHD_ip_count_lock: no-threads branch taken. daemon=%p master=%p\n",
              (void *) daemon,
              (void *) (daemon ? daemon->master : NULL));
      fclose(f);
    }
  }
#endif
}
