#include <stdio.h>

/* Probe function: logs control flow and relevant state to /tmp/logs/698.txt
 * Kept static to limit linkage and avoid affecting other translation units. */
static void
MHD_probe_log (const struct MHD_Daemon *daemon, const char *stage)
{
  FILE *fp = fopen("/tmp/logs/698.txt", "a");
  if (fp != NULL)
    {
      /* Log location, pointer to daemon and daemon->master if daemon is non-NULL. */
      if (daemon != NULL)
        {
          fprintf (fp, "MHD_ip_count_unlock: %s: daemon=%p, master=%p\n",
                   stage, (const void *) daemon, (const void *) daemon->master);
        }
      else
        {
          fprintf (fp, "MHD_ip_count_unlock: %s: daemon=NULL\n", stage);
        }
      fclose (fp);
    }
}

static void
MHD_ip_count_unlock (struct MHD_Daemon *daemon)
{
  /* Log entry and state before the assert. This helps demonstrate that the
     mhd_assert(...) call (which may expand to ((void)0)) is effectively a
     no-op at runtime while still being executed. */
  MHD_probe_log (daemon, "before_assert");

  /* Original assertion left in place to preserve behaviour; may expand to
     a no-op depending on build configuration. */
  mhd_assert (NULL == daemon->master);

  /* Log after the assert to capture that execution continued. */
  MHD_probe_log (daemon, "after_assert");

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_unlock_chk_ (&daemon->per_ip_connection_mutex);
  /* Log that the mutex-unlock branch was taken. */
  MHD_probe_log (daemon, "after_mutex_unlock");
#else
  /* Log that the no-threads branch was taken (the original code casts daemon
     to void to avoid unused-parameter warnings). */
  MHD_probe_log (daemon, "no_threads_branch");
  (void) daemon;
#endif
}
