#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <stdbool.h>

static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/626.txt", "a");
  if (!f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

struct MemoryPool *
MHD_pool_create (size_t max)
{
  struct MemoryPool *pool;
  size_t alloc_size;

  probe_log("MHD_pool_create: enter max=%zu", max);
  mhd_assert (max > 0);
  alloc_size = 0;
  probe_log("MHD_pool_create: initial alloc_size set to %zu", alloc_size);
  pool = malloc (sizeof (struct MemoryPool));
  if (NULL == pool)
  {
    probe_log("MHD_pool_create: malloc for pool failed, returning NULL");
    return NULL;
  }
#if defined(MAP_ANONYMOUS) || defined(_WIN32)
  if ( (max <= 32 * 1024) ||
       (max < MHD_sys_page_size_ * 4 / 3) )
  {
    pool->memory = MAP_FAILED;
    probe_log("MHD_pool_create: chose fallback path -> pool->memory = MAP_FAILED (small max=%zu)", max);
  }
  else
  {
    /* Round up allocation to page granularity. */
    alloc_size = max + MHD_sys_page_size_ - 1;
    alloc_size -= alloc_size % MHD_sys_page_size_;
    probe_log("MHD_pool_create: rounded alloc_size for mmap/VirtualAlloc = %zu (page=%zu, max=%zu)", alloc_size, (size_t)MHD_sys_page_size_, max);
#if defined(MAP_ANONYMOUS) && ! defined(_WIN32)
    pool->memory = mmap (NULL,
                         alloc_size,
                         PROT_READ | PROT_WRITE,
                         MAP_PRIVATE | MAP_ANONYMOUS,
                         -1,
                         0);
    probe_log("MHD_pool_create: attempted mmap -> pool->memory=%p", pool->memory);
#elif defined(_WIN32)
    pool->memory = VirtualAlloc (NULL,
                                 alloc_size,
                                 MEM_COMMIT | MEM_RESERVE,
                                 PAGE_READWRITE);
    probe_log("MHD_pool_create: attempted VirtualAlloc -> pool->memory=%p", pool->memory);
#endif /* _WIN32 */
  }
#else  /* ! _WIN32 && ! MAP_ANONYMOUS */
  pool->memory = MAP_FAILED;
  probe_log("MHD_pool_create: no mmap/VirtualAlloc available -> pool->memory = MAP_FAILED");
#endif /* ! _WIN32 && ! MAP_ANONYMOUS */
  if (MAP_FAILED == pool->memory)
  {
    alloc_size = ROUND_TO_ALIGN (max);
    probe_log("MHD_pool_create: MAP_FAILED branch -> alloc_size set to ROUND_TO_ALIGN(max) = %zu", alloc_size);
    pool->memory = malloc (alloc_size);
    probe_log("MHD_pool_create: malloc fallback -> pool->memory=%p, alloc_size=%zu", pool->memory, alloc_size);
    if (NULL == pool->memory)
    {
      probe_log("MHD_pool_create: malloc fallback failed, freeing pool and returning NULL");
      free (pool);
      return NULL;
    }
    pool->is_mmap = false;
    probe_log("MHD_pool_create: using malloc memory, is_mmap=false");
  }
#if defined(MAP_ANONYMOUS) || defined(_WIN32)
  else
  {
    pool->is_mmap = true;
    probe_log("MHD_pool_create: using mmap/VirtualAlloc memory, is_mmap=true");
  }
#endif /* _WIN32 || MAP_ANONYMOUS */
  mhd_assert (0 == (((uintptr_t) pool->memory) % ALIGN_SIZE));
  pool->pos = 0;
  pool->end = alloc_size;
  pool->size = alloc_size;
  probe_log("MHD_pool_create: final state -> memory=%p, pos=%zu, end=%zu, size=%zu, alloc_size=%zu", pool->memory, pool->pos, pool->end, pool->size, alloc_size);
  mhd_assert (0 < alloc_size);
  _MHD_POISON_MEMORY (pool->memory, pool->size);
  probe_log("MHD_pool_create: exit returning pool=%p", pool);
  return pool;
}
