void *
MHD_pool_reallocate (struct MemoryPool *pool,
                     void *old,
                     size_t old_size,
                     size_t new_size)
{
  size_t asize;
  uint8_t *new_blc;

  /* Log entry: parameters and pool state */
  {
    FILE *lf = fopen("/tmp/logs/10.txt", "a");
    if (lf)
    {
      fprintf (lf, "MHD_pool_reallocate ENTRY: pool=%p old=%p old_size=%zu new_size=%zu pos=%zu end=%zu size=%zu\n",
               (void *) pool, (void *) old, old_size, new_size,
               (size_t) pool->pos, (size_t) pool->end, (size_t) pool->size);
      fclose (lf);
    }
  }

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (old != NULL || old_size == 0);
  mhd_assert (pool->size >= old_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (old, old_size));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  if (NULL != old)
  {   /* Have previously allocated data */
    const size_t old_offset = mp_ptr_diff_ (old, pool->memory);
    const bool shrinking = (old_size > new_size);

    /* Log path into existing block */
    {
      FILE *lf = fopen("/tmp/logs/10.txt", "a");
      if (lf)
      {
        fprintf (lf, "EXISTING_BLOCK: old=%p old_offset=%zu old_size=%zu new_size=%zu shrinking=%d\n",
                 (void *) old, old_offset, old_size, new_size, (int) shrinking);
        fclose (lf);
      }
    }

    mhd_assert (mp_ptr_le_ (pool->memory, old));
    /* (pool->memory + pool->size >= (uint8_t*) old + old_size) */
    mhd_assert ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size));
    /* Blocks "from the end" must not be reallocated */
    /* (old_size == 0 || pool->memory + pool->pos > (uint8_t*) old) */
    mhd_assert ((old_size == 0) || \
                (pool->pos > old_offset));
    mhd_assert ((old_size == 0) || \
                ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));
    /* Try resizing in-place */
    if (shrinking)
    {     /* Shrinking in-place, zero-out freed part */
      /* Log shrinking details */
      {
        FILE *lf = fopen("/tmp/logs/10.txt", "a");
        if (lf)
        {
          fprintf (lf, "SHRINK_IN_PLACE: old=%p new_size=%zu freed_bytes=%zu freed_addr=%p\n",
                   (void *) old, new_size, (size_t) (old_size - new_size), (void *) ((uint8_t *) old + new_size));
          fclose (lf);
        }
      }

      memset ((uint8_t *) old + new_size, 0, old_size - new_size);
      _MHD_POISON_MEMORY ((uint8_t *) old + new_size, old_size - new_size);
      /* Log that poison call was made (may be a no-op if macro is (void)0) */
      {
        FILE *lf = fopen("/tmp/logs/10.txt", "a");
        if (lf)
        {
          fprintf (lf, "CALLED _MHD_POISON_MEMORY on %p len=%zu\n",
                   (void *) ((uint8_t *) old + new_size), (size_t) (old_size - new_size));
          fclose (lf);
        }
      }
    }
    if (pool->pos ==
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + old_size))
    {     /* "old" block is the last allocated block */
      const size_t new_apos =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + new_size);

      /* Log last-block resize attempt */
      {
        FILE *lf = fopen("/tmp/logs/10.txt", "a");
        if (lf)
        {
          fprintf (lf, "LAST_BLOCK: old_offset=%zu old_size=%zu new_size=%zu new_apos=%zu pos=%zu end=%zu shrinking=%d\n",
                   old_offset, old_size, new_size, new_apos, (size_t) pool->pos, (size_t) pool->end, (int) (old_size > new_size));
          fclose (lf);
        }
      }

      if (! shrinking)
      {                               /* Grow in-place, check for enough space. */
        if ( (new_apos > pool->end) ||
             (new_apos < pool->pos) ) /* Value wrap */
        {
          FILE *lf = fopen("/tmp/logs/10.txt", "a");
          if (lf)
          {
            fprintf (lf, "NO_SPACE for in-place grow: new_apos=%zu end=%zu pos=%zu\n",
                     new_apos, (size_t) pool->end, (size_t) pool->pos);
            fclose (lf);
          }
          return NULL;                /* No space */
        }
      }
      /* Resized in-place */
      pool->pos = new_apos;
      _MHD_UNPOISON_MEMORY (old, new_size);
      /* Log unpoison and return */
      {
        FILE *lf = fopen("/tmp/logs/10.txt", "a");
        if (lf)
        {
          fprintf (lf, "RESIZED_IN_PLACE: old=%p new_size=%zu new_pos=%zu RETURN old\n",
                   (void *) old, new_size, (size_t) pool->pos);
          fclose (lf);
        }
      }
      return old;
    }
    if (shrinking)
    {
      /* Log that shrinking returned the same pointer */
      {
        FILE *lf = fopen("/tmp/logs/10.txt", "a");
        if (lf)
        {
          fprintf (lf, "SHRINK_RETURN_OLD: old=%p old_size=%zu new_size=%zu\n",
                   (void *) old, old_size, new_size);
          fclose (lf);
        }
      }
      return old;   /* Resized in-place, freed part remains allocated */
    }
  }
  /* Need to allocate new block */
  asize = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);

  /* Log allocation decision */
  {
    FILE *lf = fopen("/tmp/logs/10.txt", "a");
    if (lf)
    {
      fprintf (lf, "ALLOCATE_NEW: new_size=%zu asize=%zu available=%zu\n",
               new_size, asize, (size_t) (pool->end - pool->pos));
      fclose (lf);
    }
  }

  if ( ( (0 == asize) &&
         (0 != new_size) ) || /* Value wrap, too large new_size. */
       (asize > pool->end - pool->pos) ) /* Not enough space */
  {
    FILE *lf = fopen("/tmp/logs/10.txt", "a");
    if (lf)
    {
      fprintf (lf, "ALLOC_FAIL: asize=%zu new_size=%zu available=%zu\n",
               asize, new_size, (size_t) (pool->end - pool->pos));
      fclose (lf);
    }
    return NULL;
  }

  new_blc = pool->memory + pool->pos;
  pool->pos += asize;

  _MHD_UNPOISON_MEMORY (new_blc, new_size);
  /* Log new block assignment */
  {
    FILE *lf = fopen("/tmp/logs/10.txt", "a");
    if (lf)
    {
      fprintf (lf, "NEW_BLOCK: new_blc=%p new_size=%zu asize=%zu new_pos=%zu\n",
               (void *) new_blc, new_size, asize, (size_t) pool->pos);
      fclose (lf);
    }
  }

  if (0 != old_size)
  {
    /* Move data to new block, old block remains allocated */
    {
      FILE *lf = fopen("/tmp/logs/10.txt", "a");
      if (lf)
      {
        fprintf (lf, "MOVE_DATA: new_blc=%p old=%p old_size=%zu\n",
                 (void *) new_blc, (void *) old, old_size);
        fclose (lf);
      }
    }

    memcpy (new_blc, old, old_size);
    /* Zero-out old block */
    memset (old, 0, old_size);
    _MHD_POISON_MEMORY (old, old_size);
    /* Log poison call on old block (may be no-op) */
    {
      FILE *lf = fopen("/tmp/logs/10.txt", "a");
      if (lf)
      {
        fprintf (lf, "CALLED _MHD_POISON_MEMORY on old=%p len=%zu\n", (void *) old, old_size);
        fclose (lf);
      }
    }
  }

  /* Log final return */
  {
    FILE *lf = fopen("/tmp/logs/10.txt", "a");
    if (lf)
    {
      fprintf (lf, "RETURN_NEW_BLOCK: %p\n", (void *) new_blc);
      fclose (lf);
    }
  }
  return new_blc;
}
