#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>

/* Probe logging helper: append human-readable trace info to /tmp/logs/578.txt */
static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/578.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len =
    MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  /* Log entry and initial params */
  mhd_probe_log("ENTRY: MHD_queue_basic_auth_required_response3 connection=%p response=%p realm=%p prefer_utf8=%d",
                (void *) connection, (void *) response, (void *) realm, prefer_utf8);

  if (NULL == response)
  {
    mhd_probe_log("EARLY_EXIT: response is NULL -> returning MHD_NO");
    return MHD_NO;
  }

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  h_maxlen = prefix_len + realm_len * 2 + suffix_len;

  /* Log computed sizes */
  mhd_probe_log("CALC: prefix_len=%zu realm_len=%zu suffix_len=%zu h_maxlen=%zu",
                prefix_len, realm_len, suffix_len, h_maxlen);

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              "Failed to allocate memory for Basic Authentication header.\n");
#endif /* HAVE_MESSAGES */
    mhd_probe_log("ALLOC_FAIL: malloc returned NULL for size %zu", h_maxlen + 1);
    return MHD_NO;
  }
  mhd_probe_log("ALLOC_OK: h_str=%p allocated_size=%zu", (void *) h_str, h_maxlen + 1);

  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos,
                                    h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;

  /* Log after quoting */
  mhd_probe_log("AFTER_QUOTE: realm_quoted_len=%zu pos=%zu h_maxlen=%zu",
                realm_quoted_len, pos, h_maxlen);

  /* Explicitly evaluate the assertion conditions and log them so callers
     can observe that mhd_assert itself may be a no-op in this build. */
  {
    int assert_cond1 = (int) (pos + suffix_len <= h_maxlen);
    mhd_probe_log("ASSERT_CHECK: pos+suffix_len<=h_maxlen -> %d (pos=%zu suffix_len=%zu h_maxlen=%zu)",
                  assert_cond1, pos, suffix_len, h_maxlen);
  }
  mhd_assert (pos + suffix_len <= h_maxlen);

  if (0 == prefer_utf8)
  {
    h_str[pos++] = '"';
    h_str[pos++] = 0; /* Zero terminate the result */
    {
      int assert_cond2 = (int) (pos <= h_maxlen + 1);
      mhd_probe_log("BRANCH_SIMPLE: added '"' and zero-terminator -> pos=%zu assert(pos<=h_maxlen+1)=%d h_maxlen=%zu",
                    pos, assert_cond2, h_maxlen);
    }
    mhd_assert (pos <= h_maxlen + 1);
    /* Log the resulting header string (as much as is valid) */
    mhd_probe_log("RESULT_HEADER_SIMPLE: h_str_sample='%s'", h_str);
  }
  else
  {
    /* Copy with the final zero-termination */
    {
      int assert_cond3 = (int) (pos + suff_charset_len <= h_maxlen);
      mhd_probe_log("BRANCH_UTF8: before memcpy check pos+suff_charset_len<=h_maxlen -> %d (pos=%zu suff_charset_len=%zu h_maxlen=%zu)",
                    assert_cond3, pos, suff_charset_len, h_maxlen);
    }
    mhd_assert (pos + suff_charset_len <= h_maxlen);
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
    {
      int zero_check = (int) (0 == h_str[pos + suff_charset_len]);
      mhd_probe_log("BRANCH_UTF8_AFTER_COPY: copied suff_charset at pos=%zu suff_charset_len=%zu zero_at_end=%d",
                    pos, suff_charset_len, zero_check);
    }
    mhd_assert (0 == h_str[pos + suff_charset_len]);
    /* Log the resulting header string (as much as is valid) */
    mhd_probe_log("RESULT_HEADER_UTF8: h_str_sample='%s'", h_str);
  }

  ret = MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_WWW_AUTHENTICATE,
                                 h_str);
  mhd_probe_log("AFTER_ADD_HEADER: ret=%d", (int) ret);
  free (h_str);
  mhd_probe_log("FREED_h_str: pointer freed");

  if (MHD_NO != ret)
  {
    ret = MHD_queue_response (connection,
                              MHD_HTTP_UNAUTHORIZED,
                              response);
    mhd_probe_log("QUEUED_RESPONSE: ret=%d", (int) ret);
  }
  else
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to add Basic Authentication header.\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("ADD_HEADER_FAILED: response header add returned MHD_NO");
  }
  mhd_probe_log("EXIT: returning ret=%d", (int) ret);
  return ret;
}
