/* Probe helper: append formatted logs to /tmp/logs/578.txt. */
static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/578.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len =
    MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  /* Log input state */
  mhd_probe_log("ENTER: MHD_queue_basic_auth_required_response3: connection=%p realm=%p prefer_utf8=%d response=%p",
                (void *) connection, (const void *) realm, prefer_utf8, (void *) response);

  if (NULL == response)
  {
    mhd_probe_log("EXIT: response is NULL -> returning MHD_NO");
    return MHD_NO;
  }

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  h_maxlen = prefix_len + realm_len * 2 + suffix_len;

  mhd_probe_log("computed lengths: prefix_len=%zu realm_len=%zu suffix_len=%zu h_maxlen=%zu",
                prefix_len, realm_len, suffix_len, h_maxlen);

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              "Failed to allocate memory for Basic Authentication header.\n");
#endif /* HAVE_MESSAGES */
    mhd_probe_log("malloc failed for h_maxlen=%zu -> returning MHD_NO", h_maxlen);
    return MHD_NO;
  }
  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos,
                                    h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;

  /* Probe for the first assert: mhd_assert (pos + suffix_len <= h_maxlen); */
  {
    int cond = (int) (pos + suffix_len <= h_maxlen);
    mhd_probe_log("ASSERT_CHECK 1: pos=%zu suffix_len=%zu h_maxlen=%zu cond=%d",
                  pos, suffix_len, h_maxlen, cond);
  }
  mhd_assert (pos + suffix_len <= h_maxlen);

  if (0 == prefer_utf8)
  {
    h_str[pos++] = '"';
    h_str[pos++] = 0; /* Zero terminate the result */
    /* Probe for the second assert: mhd_assert (pos <= h_maxlen + 1); */
    {
      int cond = (int) (pos <= h_maxlen + 1);
      mhd_probe_log("ASSERT_CHECK 2 (prefer_utf8==0): pos=%zu h_maxlen=%zu cond=%d",
                    pos, h_maxlen + 1, cond);
    }
    mhd_assert (pos <= h_maxlen + 1);
  }
  else
  {
    /* Copy with the final zero-termination */
    /* Probe for the third assert: mhd_assert (pos + suff_charset_len <= h_maxlen); */
    {
      int cond = (int) (pos + suff_charset_len <= h_maxlen);
      mhd_probe_log("ASSERT_CHECK 3 (prefer_utf8!=0): pos=%zu suff_charset_len=%zu h_maxlen=%zu cond=%d",
                    pos, suff_charset_len, h_maxlen, cond);
    }
    mhd_assert (pos + suff_charset_len <= h_maxlen);
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
    /* Probe for the fourth assert: mhd_assert (0 == h_str[pos + suff_charset_len]); */
    {
      int cond = (int) (0 == h_str[pos + suff_charset_len]);
      mhd_probe_log("ASSERT_CHECK 4: h_str[pos+suff_charset_len]=%d cond=%d",
                    (int) h_str[pos + suff_charset_len], cond);
    }
    mhd_assert (0 == h_str[pos + suff_charset_len]);
  }

  mhd_probe_log("header string prepared: h_str=\"%s\"", h_str);

  ret = MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_WWW_AUTHENTICATE,
                                 h_str);
  mhd_probe_log("MHD_add_response_header returned: ret=%d", (int) ret);
  free (h_str);
  if (MHD_NO != ret)
  {
    ret = MHD_queue_response (connection,
                              MHD_HTTP_UNAUTHORIZED,
                              response);
    mhd_probe_log("MHD_queue_response called: ret=%d", (int) ret);
  }
  else
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to add Basic Authentication header.\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("Failed to add Basic Authentication header; leaving ret=%d", (int) ret);
  }
  mhd_probe_log("EXIT: MHD_queue_basic_auth_required_response3 returning ret=%d", (int) ret);
  return ret;
}
