/* Probe logging helpers inserted to capture control flow and state for Rule 21.17 investigation. */
static void
mhd_write_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/383.txt", "a");
  if (! f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static void
mhd_probe_log_context (struct MHD_Connection *connection,
                       struct MHD_Response *response,
                       struct MHD_HTTP_Res_Header *conn_header,
                       unsigned int status_code)
{
  /* Log a compact snapshot of pointers and a few numeric state values. */
  mhd_write_probe_log ("[MHD_probe] connection=%p response=%p status_code=%u",
                       (void *) connection, (void *) response, status_code);
  if (connection)
  {
    mhd_write_probe_log ("[MHD_probe] connection->daemon=%p connection->state=%d in_access_handler=%d suspended=%d in_idle=%d",
                         (void *) connection->daemon,
                         (int) connection->state,
                         (int) connection->in_access_handler,
                         (int) connection->suspended,
                         (int) connection->in_idle);
    /* rq and rp are present in original code; log a couple of their values */
    mhd_write_probe_log ("[MHD_probe] rq.http_ver=%d rq.http_mthd=%d rp.response=%p rp.responseCode=%u rp.responseIcy=%d",
                         (int) connection->rq.http_ver,
                         (int) connection->rq.http_mthd,
                         (void *) connection->rp.response,
                         (unsigned) connection->rp.responseCode,
                         (int) connection->rp.responseIcy);
  }
  if (response)
  {
    mhd_write_probe_log ("[MHD_probe] response->first_header=%p response->flags_auto=0x%08x response->flags=0x%08x total_size=%llu fd=%d is_pipe=%d",
                         (void *) response->first_header,
                         (unsigned int) response->flags_auto,
                         (unsigned int) response->flags,
                         (unsigned long long) response->total_size,
                         (int) response->fd,
                         (int) response->is_pipe);
  }
  if (conn_header)
  {
    /* Do NOT dereference header/value as strings here to avoid provoking the very OOB; print pointers only. */
    mhd_write_probe_log ("[MHD_probe] conn_header=%p conn_header->header=%p conn_header->value=%p",
                         (void *) conn_header,
                         (void *) conn_header->header,
                         (void *) conn_header->value);
  }
}

_MHD_EXTERN enum MHD_Result
MHD_queue_response (struct MHD_Connection *connection,
                    unsigned int status_code,
                    struct MHD_Response *response)
{
  struct MHD_Daemon *daemon;
  bool reply_icy;

  /* Entry probe */
  mhd_write_probe_log ("[MHD_probe] Entering MHD_queue_response: connection=%p response=%p status_code=%u",
                       (void *) connection, (void *) response, status_code);

  if ((NULL == connection) || (NULL == response))
  {
    mhd_write_probe_log ("[MHD_probe] Early return: NULL input - connection=%p response=%p",
                         (void *) connection, (void *) response);
    return MHD_NO;
  }

  daemon = connection->daemon;
  if ((! connection->in_access_handler) && (! connection->suspended) &&
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    mhd_write_probe_log ("[MHD_probe] Early return: thread/context check failed - in_access_handler=%d suspended=%d daemon=%p",
                         (int) connection->in_access_handler, (int) connection->suspended, (void *) daemon);
    return MHD_NO;
  }

  reply_icy = (0 != (status_code & MHD_ICY_FLAG));
  status_code &= ~MHD_ICY_FLAG;

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if ( (! connection->suspended) &&
       MHD_D_IS_USING_THREADS_ (daemon) &&
       (! MHD_thread_handle_ID_is_current_thread_ (connection->tid)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Attempted to queue response on wrong thread!\n"));
#endif
    mhd_write_probe_log ("[MHD_probe] Early return: Attempted to queue response on wrong thread - tid=%p",
                         (void *) connection->tid);
    return MHD_NO;
  }
#endif

  if (NULL != connection->rp.response)
  {
    mhd_write_probe_log ("[MHD_probe] Early return: response already set (connection->rp.response=%p)",
                         (void *) connection->rp.response);
    return MHD_NO; /* The response was already set */
  }

  if ( (MHD_CONNECTION_HEADERS_PROCESSED != connection->state) &&
       (MHD_CONNECTION_FULL_REQ_RECEIVED != connection->state) )
  {
    mhd_write_probe_log ("[MHD_probe] Early return: wrong connection state (%d)", (int) connection->state);
    return MHD_NO; /* Wrong connection state */
  }

  if (daemon->shutdown)
  {
    mhd_write_probe_log ("[MHD_probe] Early return: daemon shutdown flag set");
    return MHD_NO;
  }

#ifdef UPGRADE_SUPPORT
  if (NULL != response->upgrade_handler)
  {
    struct MHD_HTTP_Res_Header *conn_header;
    if (0 == (daemon->options & MHD_ALLOW_UPGRADE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Attempted 'upgrade' connection on daemon without" \
                   " MHD_ALLOW_UPGRADE option!\n"));
#endif
      mhd_write_probe_log ("[MHD_probe] Early return: upgrade handler set but daemon disallows upgrades (daemon=%p)", (void *) daemon);
      return MHD_NO;
    }
    if (MHD_HTTP_SWITCHING_PROTOCOLS != status_code)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid status code for" \
                   " 'upgrade' response!\n"));
#endif
      mhd_write_probe_log ("[MHD_probe] Early return: upgrade response but status_code != 101 (status_code=%u)", status_code);
      return MHD_NO;
    }
    if (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"Connection\" header!\n"));
#endif
      mhd_write_probe_log ("[MHD_probe] Early return: upgrade response missing Connection header flag (response=%p)", (void *) response);
      return MHD_NO;
    }
    conn_header = response->first_header;

    /* Log context immediately after obtaining conn_header. This is crucial to capture cases
       when conn_header is NULL or conn_header->header is NULL. Do not dereference header/value
       contents here to avoid provoking out-of-bounds reads. */
    mhd_probe_log_context (connection, response, conn_header, status_code);

    mhd_assert (NULL != conn_header);

    /* Log pointer information right before calling the string compare routine that is
       suspected to read past buffer bounds if the header isn't NUL-terminated. */
    mhd_write_probe_log ("[MHD_probe] About to call MHD_str_equal_caseless_ - conn_header->header=%p expected_header=%p",
                         (void *) (conn_header ? conn_header->header : NULL),
                         (void *) MHD_HTTP_HEADER_CONNECTION);

    mhd_assert (MHD_str_equal_caseless_ (conn_header->header,
                                         MHD_HTTP_HEADER_CONNECTION));

    /* If execution reaches here, the assertion passed. Log that as well. */
    mhd_write_probe_log ("[MHD_probe] MHD_str_equal_caseless_ asserted true for header pointer=%p",
                         (void *) (conn_header ? conn_header->header : NULL));

    if (! MHD_str_has_s_token_caseless_ (conn_header->value,
                                         "upgrade"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"upgrade\" token in" \
                   " \"Connection\" header!\n"));
#endif
      mhd_write_probe_log ("[MHD_probe] Early return: Connection header does not contain 'upgrade' token - conn_header->value=%p",
                           (void *) (conn_header ? conn_header->value : NULL));
      return MHD_NO;
    }
    if (! MHD_IS_HTTP_VER_1_1_COMPAT (connection->rq.http_ver))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Connection \"Upgrade\" can be used only " \
                   "with HTTP/1.1 connections!\n"));
#endif
      mhd_write_probe_log ("[MHD_probe] Early return: Upgrade only for HTTP/1.1 - rq.http_ver=%d",
                           (int) connection->rq.http_ver);
      return MHD_NO;
    }
  }
#endif /* UPGRADE_SUPPORT */
  if (MHD_HTTP_SWITCHING_PROTOCOLS == status_code)
  {
#ifdef UPGRADE_SUPPORT
    if (NULL == response->upgrade_handler)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used status code 101 \"Switching Protocols\" " \
                   "with non-'upgrade' response!\n"));
#endif /* HAVE_MESSAGES */
      mhd_write_probe_log ("[MHD_probe] Early return: status 101 but no upgrade_handler (response=%p)", (void *) response);
      return MHD_NO;
    }
#else  /* ! UPGRADE_SUPPORT */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Application used status code 101 \"Switching Protocols\", " \
                 "but this MHD was built without \"Upgrade\" support!\n"));
#endif /* HAVE_MESSAGES */
    mhd_write_probe_log ("[MHD_probe] Early return: status 101 but UPGRADE_SUPPORT not compiled in");
    return MHD_NO;
#endif /* ! UPGRADE_SUPPORT */
  }
  if ( (100 > status_code) ||
       (999 < status_code) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Refused wrong status code (%u). " \
                 "HTTP requires three digits status code!\n"),
              status_code);
#endif
    mhd_write_probe_log ("[MHD_probe] Early return: invalid numeric status code=%u", status_code);
    return MHD_NO;
  }
  if (200 > status_code)
  {
    if (MHD_HTTP_VER_1_0 == connection->rq.http_ver)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 clients do not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_write_probe_log ("[MHD_probe] Early return: HTTP/1.0 and status < 200 (http_ver=%d status=%u)",
                           (int) connection->rq.http_ver, status_code);
      return MHD_NO;
    }
    if (0 != (response->flags & (MHD_RF_HTTP_1_0_COMPATIBLE_STRICT
                                 | MHD_RF_HTTP_1_0_SERVER)))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 reply mode does not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_write_probe_log ("[MHD_probe] Early return: response flags indicate HTTP/1.0 reply mode and status < 200");
      return MHD_NO;
    }
  }
  if ( (MHD_HTTP_MTHD_CONNECT == connection->rq.http_mthd) &&
       (2 == status_code / 100) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Successful (%u) response code cannot be used to answer " \
                 "\"CONNECT\" request!\n"),
              (status_code));
#endif
    mhd_write_probe_log ("[MHD_probe] Early return: CONNECT method cannot be answered with 2xx (http_mthd=%d status=%u)",
                         (int) connection->rq.http_mthd, status_code);
    return MHD_NO;
  }

  if ( (0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
       (RP_BODY_HEADERS_ONLY < is_reply_body_needed (connection, status_code)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("HEAD-only response cannot be used when the request requires "
                 "reply body to be sent!\n"));
#endif
    mhd_write_probe_log ("[MHD_probe] Early return: HEAD-only response but reply body needed");
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) &&
       (0 != (MHD_RAF_HAS_CONTENT_LENGTH & response->flags_auto)) )
  {
    MHD_DLOG (daemon,
              _ ("The response has application-defined \"Content-Length\" " \
                 "header. The reply to the request will be not " \
                 "HTTP-compliant and may result in hung connection or " \
                 "other problems!\n"));
  }
#endif

  MHD_increment_response_rc (response);
  connection->rp.response = response;
  connection->rp.responseCode = status_code;
  connection->rp.responseIcy = reply_icy;
#if defined(_MHD_HAVE_SENDFILE)
  if ( (response->fd == -1) ||
       (response->is_pipe) ||
       (0 != (connection->daemon->options & MHD_USE_TLS))
#if defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED) && \
       defined(MHD_SEND_SPIPE_SUPPRESS_POSSIBLE)
       || (! daemon->sigpipe_blocked && ! connection->sk_spipe_suppress)
#endif /* MHD_SEND_SPIPE_SUPPRESS_NEEDED &&
          MHD_SEND_SPIPE_SUPPRESS_POSSIBLE */
       )
    connection->rp.resp_sender = MHD_resp_sender_std;
  else
    connection->rp.resp_sender = MHD_resp_sender_sendfile;
#endif /* _MHD_HAVE_SENDFILE */
  /* FIXME: if 'is_pipe' is set, TLS is off, and we have *splice*, we could use splice()
     to avoid two user-space copies... */

  if ( (MHD_HTTP_MTHD_HEAD == connection->rq.http_mthd) ||
       (MHD_HTTP_OK > status_code) ||
       (MHD_HTTP_NO_CONTENT == status_code) ||
       (MHD_HTTP_NOT_MODIFIED == status_code) )
  {
    /* if this is a "HEAD" request, or a status code for
       which a body is not allowed, pretend that we
       have already sent the full message body. */
    /* TODO: remove the next assignment, use 'rp_props.send_reply_body' in
     * checks */
    connection->rp.rsp_write_position = response->total_size;
  }
  if (MHD_CONNECTION_HEADERS_PROCESSED == connection->state)
  {
    /* response was queued "early", refuse to read body / footers or
       further requests! */
    connection->discard_request = true;
    connection->state = MHD_CONNECTION_START_REPLY;
    connection->rq.remaining_upload_size = 0;
  }
  if (! connection->in_idle)
    (void) MHD_connection_handle_idle (connection);
  MHD_update_last_activity_ (connection);

  mhd_write_probe_log ("[MHD_probe] Exiting MHD_queue_response: success - connection=%p response=%p status_code=%u",
                       (void *) connection, (void *) response, status_code);
  return MHD_YES;
}
