#include <stdio.h>
#include <stdint.h>
#include <stdbool.h>
#include <inttypes.h>

/* Probe logger: append human-readable state to /tmp/logs/132.txt. */
static void
MHD_probe_log (const char *stage,
               struct MHD_Daemon *daemon,
               int32_t millisec,
               int err_state,
               MHD_socket maxsock,
               struct timeval *tv,
               struct timeval timeout,
               int num_ready,
               MHD_socket ls,
               bool itc_added)
{
  FILE *f = fopen("/tmp/logs/132.txt", "a");
  if (NULL == f)
    return;
  fprintf (f, "--- MHD_select probe: %s\n", stage);
  fprintf (f, "  daemon=%p\n", (void *) daemon);
  fprintf (f, "  millisec=%" PRId32 "\n", millisec);
  fprintf (f, "  err_state=%d\n", err_state);
  fprintf (f, "  maxsock=%lld\n", (long long) maxsock);
  fprintf (f, "  tv=%p\n", (void *) tv);
  fprintf (f, "  timeout.tv_sec=%ld, timeout.tv_usec=%ld\n",
           (long) timeout.tv_sec, (long) timeout.tv_usec);
  fprintf (f, "  num_ready=%d\n", num_ready);
  fprintf (f, "  listen_fd=%lld\n", (long long) ls);
  fprintf (f, "  itc_added=%d\n", itc_added ? 1 : 0);
  fprintf (f, "\n");
  fclose (f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
    return MHD_NO;

  /* Log entry state */
  MHD_probe_log ("entry", daemon, millisec, -1, (MHD_socket) -1, timeout, &timeout, -1, (MHD_socket) -1, false);

  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
    millisec = 0;

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      err_state = MHD_YES;
      /* log failure to get fdset */
      MHD_probe_log ("internal_get_fdset2_failed", daemon, millisec, err_state, maxsock, &timeout, timeout, -1, (MHD_socket) -1, false);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
        MHD_probe_log ("itc_add_failed", daemon, millisec, err_state, maxsock, &timeout, timeout, -1, (MHD_socket) -1, itc_added);
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          err_state = MHD_YES;
          MHD_probe_log ("listen_add_failed", daemon, millisec, err_state, maxsock, &timeout, timeout, -1, ls, itc_added);
        }
        else
        {
          /* Log successful addition of listen fd */
          MHD_probe_log ("listen_add_succeeded", daemon, millisec, err_state, maxsock, &timeout, timeout, -1, ls, itc_added);
        }
      }
    }
    else
    {
      /* Log that either listen fd invalid or quiesced */
      MHD_probe_log ("listen_skipped_or_quiesced", daemon, millisec, err_state, maxsock, &timeout, timeout, -1, ls, itc_added);
    }
  }

  if (MHD_NO != err_state)
    millisec = 0;
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
    }
  }

  /* Log state immediately before select */
  MHD_probe_log ("before_select", daemon, millisec, err_state, maxsock, tv, timeout, -1, ls, false);

  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);

  /* Log result of select */
  MHD_probe_log ("after_select", daemon, millisec, err_state, maxsock, tv, timeout, num_ready, ls, false);

  if (daemon->shutdown)
    return MHD_NO;
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    /* Log select error */
    MHD_probe_log ("select_failed", daemon, millisec, err_state, maxsock, tv, timeout, num_ready, ls, false);
    return MHD_NO;
  }
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;

  /* Log exit state (no ready descriptors processed) */
  MHD_probe_log ("exit_no_ready", daemon, millisec, err_state, maxsock, tv, timeout, num_ready, ls, false);
  return MHD_NO;
}
