#include <stdio.h>
#include <stdarg.h>
#include <time.h>

/* Probe logging helper - append logs to /tmp/logs/132.txt */
static void
MHD_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/132.txt", "a");
  if (NULL == f)
    return;

  /* simple timestamp */
  time_t t = time(NULL);
  if (t != (time_t)(-1))
    fprintf(f, "[%ld] ", (long)t);

  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);

  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  /* Log entry and initial state */
  MHD_log_probe("enter MHD_select: daemon=%p, millisec=%d, shutdown=%d",
                (void *) daemon, (int) millisec,
                (daemon != NULL) ? (int) daemon->shutdown : -1);

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
  {
    MHD_log_probe("early return: daemon->shutdown");
    return MHD_NO;
  }
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;

  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
  {
    MHD_log_probe("resume_suspended_connections triggered, setting millisec=0 (was %d)", millisec);
    millisec = 0;
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      MHD_log_probe("internal_get_fdset2 failed");
      err_state = MHD_YES;
    }
    else
    {
      MHD_log_probe("internal_get_fdset2 succeeded: maxsock=%ld", (long) maxsock);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      MHD_log_probe("itc_added=%d", (int) itc_added);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          MHD_log_probe("failed to add listen socket to fdset: ls=%ld", (long) ls);
          err_state = MHD_YES;
        }
        else
        {
          MHD_log_probe("added listen socket to fdset: ls=%ld, maxsock=%ld", (long) ls, (long) maxsock);
        }
      }
      else
      {
        MHD_log_probe("skipping adding listen socket: itc_added=%d, connections=%d, connection_limit=%d, at_limit=%d",
                      (int) itc_added,
                      (int) daemon->connections,
                      (int) daemon->connection_limit,
                      (int) daemon->at_limit);
      }
    }
  }

  if (MHD_NO != err_state)
  {
    MHD_log_probe("err_state set, forcing millisec=0 (was %d)", millisec);
    millisec = 0;
  }
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
    MHD_log_probe("using immediate timeout (millisec==0)");
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
      MHD_log_probe("timeout computed from mhd_tmo=%llu, millisec=%d, select_tmo=%llu",
                    (unsigned long long) mhd_tmo, (int) millisec, (unsigned long long) select_tmo);
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
      MHD_log_probe("timeout computed from millisec only, select_tmo=%llu", (unsigned long long) select_tmo);
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
      MHD_log_probe("no timeout (tv=NULL)");
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
      MHD_log_probe("timeout set: tv_sec=%ld, tv_usec=%ld", (long) timeout.tv_sec, (long) timeout.tv_usec);
    }
  }
  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);
  MHD_log_probe("after select: num_ready=%d, maxsock=%ld", num_ready, (long) maxsock);

  if (daemon->shutdown)
  {
    MHD_log_probe("returning MHD_NO due to daemon->shutdown after select");
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    MHD_log_probe("select error: err=%d", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      MHD_log_probe("select interrupted by signal, returning %s", (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO");
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    MHD_log_probe("select failed permanently, returning MHD_NO");
    return MHD_NO;
  }
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    MHD_log_probe("internal_run_from_select indicated readiness, returning %s", (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO");
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }

  MHD_log_probe("exiting MHD_select normally, returning MHD_NO");
  return MHD_NO;
}
