#include <stdio.h>
#include <stdarg.h>

static void probe_printf(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/130.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fflush(f);
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  /* Initial (potentially dead) assignments we are investigating */
  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  probe_printf("ENTRY: initial assign timeout addr=%p tv_sec=%ld tv_usec=%ld millisec=%d daemon=%p\n",
               (void *)&timeout,
               (long) timeout.tv_sec,
               (long) timeout.tv_usec,
               (int) millisec,
               (void *) daemon);

  if (daemon->shutdown)
  {
    probe_printf("EARLY_RETURN: daemon->shutdown true, returning MHD_NO daemon=%p\n", (void *) daemon);
    return MHD_NO;
  }

  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
    millisec = 0;

  probe_printf("AFTER_SUSPEND_CHECK: millisec=%d daemon=%p\n", (int) millisec, (void *) daemon);

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      err_state = MHD_YES;
      probe_printf("GET_FDSET_FAIL: internal_get_fdset2 failed, err_state=%d daemon=%p\n", err_state, (void *) daemon);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
        probe_printf("ITC_ADD_FAIL: itc_added=%d err_state=%d daemon=%p\n", (int) itc_added, err_state, (void *) daemon);
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          err_state = MHD_YES;
          probe_printf("LISTEN_ADD_FAIL: ls=%d err_state=%d daemon=%p\n", (int) ls, err_state, (void *) daemon);
        }
      }
    }
  }

  if (MHD_NO != err_state)
  {
    probe_printf("ERR_STATE_NONZERO: err_state=%d setting millisec=0 daemon=%p\n", err_state, (void *) daemon);
    millisec = 0;
  }
  if (0 == millisec)
  {
    probe_printf("BRANCH_ZERO_MILLISEC: entering zero timeout branch, pre-timeout tv_sec=%ld tv_usec=%ld daemon=%p\n",
                 (long) timeout.tv_sec, (long) timeout.tv_usec, (void *) daemon);
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
    probe_printf("BRANCH_ZERO_MILLISEC: after assign tv=%p timeout.tv_sec=%ld timeout.tv_usec=%ld daemon=%p\n",
                 (void *) tv,
                 (long) timeout.tv_sec,
                 (long) timeout.tv_usec,
                 (void *) daemon);
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    probe_printf("BRANCH_NONZERO_MILLISEC: millisec=%d thread_per_conn=%d daemon=%p\n",
                 (int) millisec,
                 (int) MHD_D_IS_USING_THREAD_PER_CONN_ (daemon),
                 (void *) daemon);

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
      probe_printf("USE_MHD_TIMEOUT: mhd_tmo=%llu select_tmo=%llu tv=%p daemon=%p\n",
                   (unsigned long long) mhd_tmo,
                   (unsigned long long) select_tmo,
                   (void *) tv,
                   (void *) daemon);
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
      probe_printf("USE_MILLISEC_AS_SELECT_TMO: select_tmo=%llu tv=%p daemon=%p\n",
                   (unsigned long long) select_tmo,
                   (void *) tv,
                   (void *) daemon);
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
      probe_printf("NO_TIMEOUT_USED: tv=NULL daemon=%p\n", (void *) daemon);
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);

      probe_printf("TIMEOUT_COMPUTED: select_tmo=%llu timeout.addr=%p timeout.tv_sec=%ld timeout.tv_usec=%ld daemon=%p\n",
                   (unsigned long long) select_tmo,
                   (void *) &timeout,
                   (long) timeout.tv_sec,
                   (long) timeout.tv_usec,
                   (void *) daemon);
    }
  }
  probe_printf("BEFORE_SELECT: maxsock=%d tv=%p timeout.tv_sec=%ld timeout.tv_usec=%ld daemon=%p\n",
               (int) maxsock,
               (void *) tv,
               (long) timeout.tv_sec,
               (long) timeout.tv_usec,
               (void *) daemon);

  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);
  probe_printf("AFTER_SELECT: num_ready=%d daemon=%p\n", num_ready, (void *) daemon);

  if (daemon->shutdown)
  {
    probe_printf("POST_SELECT_SHUTDOWN: daemon->shutdown true, returning MHD_NO daemon=%p\n", (void *) daemon);
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    probe_printf("SELECT_FAILED: num_ready=%d err=%d daemon=%p\n", num_ready, err, (void *) daemon);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      probe_printf("SELECT_EINTR: returning %s daemon=%p\n",
                   (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO",
                   (void *) daemon);
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    return MHD_NO;
  }
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &es,
                                          &es,
                                          (int) FD_SETSIZE))
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  return MHD_NO;
}
