#include <stdio.h>
#include <stdint.h>
#include <stdbool.h>

/* Probe helpers: write small trace entries to /tmp/logs/130.txt. */
static void mhd_probe_log_int(const char *tag, int val)
{
    FILE *f = fopen("/tmp/logs/130.txt", "a");
    if (f)
    {
        fprintf(f, "%s: %d\n", tag, val);
        fclose(f);
    }
}

static void mhd_probe_log_str(const char *tag, const char *s)
{
    FILE *f = fopen("/tmp/logs/130.txt", "a");
    if (f)
    {
        fprintf(f, "%s: %s\n", tag, s);
        fclose(f);
    }
}

static void mhd_probe_log_ptr(const char *tag, const void *p)
{
    FILE *f = fopen("/tmp/logs/130.txt", "a");
    if (f)
    {
        fprintf(f, "%s: %p\n", tag, p);
        fclose(f);
    }
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  /* Probe: entry and provided millisec */
  mhd_probe_log_int("MHD_select.enter.millisec", (int) millisec);
  /* Probe: FD_SETSIZE value observed at runtime */
  mhd_probe_log_int("MHD_select.FD_SETSIZE", (int) FD_SETSIZE);

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
  {
    mhd_probe_log_str("MHD_select.exit", "daemon->shutdown == true");
    return MHD_NO;
  }
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
    millisec = 0;

  /* Probe which threading model we're in */
  mhd_probe_log_str("MHD_select.threading_model",
                    MHD_D_IS_USING_THREAD_PER_CONN_ (daemon) ? "thread_per_conn" : "single_thread");

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    mhd_probe_log_str("MHD_select.branch", "single_thread");
    /* Log the intended fd_setsize argument just before the call. This
       allows reproducing whether any cast or dummy operation prior to
       assignment occurs in surrounding build variants. */
    mhd_probe_log_int("MHD_select.before_internal_get_fdset2.fd_setsize", (int) FD_SETSIZE);
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      err_state = MHD_YES;
      mhd_probe_log_str("MHD_select.internal_get_fdset2", "failed");
    }
    else
    {
      mhd_probe_log_str("MHD_select.internal_get_fdset2", "succeeded");
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    mhd_probe_log_str("MHD_select.branch", "thread_per_conn");
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      mhd_probe_log_int("MHD_select.before_add_itc.fd_setsize", (int) FD_SETSIZE);
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      mhd_probe_log_int("MHD_select.add_itc.result", itc_added ? 1 : 0);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        mhd_probe_log_int("MHD_select.before_add_listen.fd_setsize", (int) FD_SETSIZE);
        mhd_probe_log_int("MHD_select.listen_fd", (int) ls);
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          err_state = MHD_YES;
          mhd_probe_log_str("MHD_select.add_listen", "failed");
        }
        else
        {
          mhd_probe_log_str("MHD_select.add_listen", "succeeded");
        }
      }
    }
  }

  if (MHD_NO != err_state)
  {
    mhd_probe_log_str("MHD_select.err_state", "non_zero");
    millisec = 0;
  }
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
    mhd_probe_log_str("MHD_select.timeout", "immediate");
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
      mhd_probe_log_int("MHD_select.timeout.sec", (int) timeout.tv_sec);
      mhd_probe_log_int("MHD_select.timeout.usec", (int) timeout.tv_usec);
    }
    else
    {
      mhd_probe_log_str("MHD_select.timeout", "none (tv == NULL)");
    }
  }

  mhd_probe_log_int("MHD_select.before_select.maxsock", (int) maxsock);
  mhd_probe_log_ptr("MHD_select.before_select.tv", tv);
  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);
  mhd_probe_log_int("MHD_select.after_select.num_ready", num_ready);

  if (daemon->shutdown)
  {
    mhd_probe_log_str("MHD_select.exit", "daemon->shutdown after select");
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    mhd_probe_log_int("MHD_select.select_error", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_probe_log_str("MHD_select.select_error", "EINTR");
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    mhd_probe_log_str("MHD_select.select_error", "other");
    return MHD_NO;
  }
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    mhd_probe_log_str("MHD_select.internal_run_from_select", "active");
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  mhd_probe_log_str("MHD_select.exit", "no_ready");
  return MHD_NO;
}
