/* Probe helpers inserted to capture control flow and fd_set state. */
#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <unistd.h>

static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/131.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  struct tm tmv;
  localtime_r(&t, &tmv);
  fprintf(f, "%04d-%02d-%02d %02d:%02d:%02d: ",
          tmv.tm_year + 1900, tmv.tm_mon + 1, tmv.tm_mday,
          tmv.tm_hour, tmv.tm_min, tmv.tm_sec);
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static void
mhd_probe_dump_fdset (FILE *f, fd_set *set, int maxfds, const char *name)
{
  int i, first = 1;
  fprintf(f, "  %s: ", name);
  for (i = 0; i < maxfds; ++i)
  {
    if (FD_ISSET(i, set))
    {
      if (!first)
        fprintf(f, ",");
      fprintf(f, "%d", i);
      first = 0;
    }
  }
  if (first)
    fprintf(f, "<none>");
  fprintf(f, "\n");
}

static void
mhd_probe_log_fdsets (fd_set *rs, fd_set *ws, fd_set *es, int maxfds)
{
  FILE *f = fopen("/tmp/logs/131.txt", "a");
  if (NULL == f)
    return;
  mhd_probe_dump_fdset(f, rs, maxfds, "read_set");
  mhd_probe_dump_fdset(f, ws, maxfds, "write_set");
  mhd_probe_dump_fdset(f, es, maxfds, "exc_set");
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  mhd_probe_log("enter MHD_select: daemon=%p, millisec=%d", (void*)daemon, (int)millisec);

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
  {
    mhd_probe_log("daemon->shutdown set, returning MHD_NO");
    return MHD_NO;
  }
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  mhd_probe_log("fd_sets zeroed");

  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
    millisec = 0;

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      mhd_probe_log("internal_get_fdset2 FAILED");
      err_state = MHD_YES;
    }
    else
    {
      mhd_probe_log("internal_get_fdset2 succeeded, maxsock=%ld", (long) maxsock);
      mhd_probe_log_fdsets(&rs, &ws, &es, (int) FD_SETSIZE);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        mhd_probe_log("failed to add ITC fd to fdset");
        err_state = MHD_YES;
      }
      else
      {
        mhd_probe_log("added ITC fd to fdset, maxsock=%ld", (long) maxsock);
        mhd_probe_log_fdsets(&rs, &ws, &es, (int) FD_SETSIZE);
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          mhd_probe_log("failed to add listen socket to fdset");
          err_state = MHD_YES;
        }
        else
        {
          mhd_probe_log("added listen socket %ld to fdset, maxsock=%ld", (long) ls, (long) maxsock);
          mhd_probe_log_fdsets(&rs, &ws, &es, (int) FD_SETSIZE);
        }
      }
    }
  }

  if (MHD_NO != err_state)
  {
    mhd_probe_log("err_state != MHD_NO, forcing millisec=0 (was %d)", (int) millisec);
    millisec = 0;
  }
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
      mhd_probe_log("timeout set: tv_sec=%ld, tv_usec=%ld", (long) timeout.tv_sec, (long) timeout.tv_usec);
    }
  }
  mhd_probe_log("about to call select: maxsock=%ld, tv=%p", (long) maxsock, (void*) tv);
  mhd_probe_log_fdsets(&rs, &ws, &es, (int) FD_SETSIZE);

  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);
  mhd_probe_log("select returned num_ready=%d", num_ready);
  if (daemon->shutdown)
  {
    mhd_probe_log("daemon->shutdown set after select, returning MHD_NO");
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    mhd_probe_log("select failed with err=%d", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_probe_log("select interrupted by signal (EINTR), returning %s", (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO");
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    return MHD_NO;
  }

  /* Log the fd_sets as they are passed to internal_run_from_select */
  mhd_probe_log("fd_sets after select (about to call internal_run_from_select):");
  mhd_probe_log_fdsets(&rs, &ws, &es, (int) FD_SETSIZE);

  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    mhd_probe_log("internal_run_from_select returned MHD_YES (or non-MHD_NO)");
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  mhd_probe_log("exiting MHD_select with MHD_NO");
  return MHD_NO;
}
