#include <stdio.h>
#include <stdarg.h>

static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/337.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);
  fclose (f);
}

ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data)
{
  MHD_socket s = connection->socket_fd;
  ssize_t ret;
#ifdef HTTPS_SUPPORT
  const bool tls_conn = (connection->daemon->options & MHD_USE_TLS);
#else  /* ! HTTPS_SUPPORT */
  const bool tls_conn = false;
#endif /* ! HTTPS_SUPPORT */

  /* Log entry and compile-time configuration */
#ifdef HTTPS_SUPPORT
  mhd_log_probe ("MHD_send_data_: entry (HTTPS_SUPPORT defined). socket=%d state=%d buffer_size=%zu push_data=%d",
                 (int) s, (int) connection->state, buffer_size, (int) push_data);
#else
  mhd_log_probe ("MHD_send_data_: entry (HTTPS_SUPPORT NOT defined). socket=%d state=%d buffer_size=%zu push_data=%d",
                 (int) s, (int) connection->state, buffer_size, (int) push_data);
#endif
  mhd_log_probe ("tls_conn=%d daemon_options=0x%lx",
                 (int) tls_conn,
                 (unsigned long) connection->daemon->options);

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_log_probe ("MHD_send_data_: early return: not connected (socket=%d state=%d)",
                   (int) s, (int) connection->state);
    return MHD_ERR_NOTCONN_;
  }

  if (buffer_size > SSIZE_MAX)
  {
    buffer_size = SSIZE_MAX; /* Max return value */
    push_data = false; /* Incomplete send */
    mhd_log_probe ("MHD_send_data_: buffer_size trimmed to SSIZE_MAX, push_data set to false");
  }

  if (tls_conn)
  {
    mhd_log_probe ("MHD_send_data_: entering TLS branch");
#ifdef HTTPS_SUPPORT
    pre_send_setopt (connection, (! tls_conn), push_data);
    ret = gnutls_record_send (connection->tls_session,
                              buffer,
                              buffer_size);
    mhd_log_probe ("MHD_send_data_: gnutls_record_send returned %zd", ret);
    if (GNUTLS_E_AGAIN == ret)
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif
      mhd_log_probe ("MHD_send_data_: GNUTLS_E_AGAIN -> returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (GNUTLS_E_INTERRUPTED == ret)
    {
      mhd_log_probe ("MHD_send_data_: GNUTLS_E_INTERRUPTED -> returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if ( (GNUTLS_E_ENCRYPTION_FAILED == ret) ||
         (GNUTLS_E_INVALID_SESSION == ret) ||
         (GNUTLS_E_COMPRESSION_FAILED == ret) ||
         (GNUTLS_E_EXPIRED == ret) ||
         (GNUTLS_E_HASH_FAILED == ret) )
    {
      mhd_log_probe ("MHD_send_data_: TLS hard error %zd -> returning MHD_ERR_TLS_", ret);
      return MHD_ERR_TLS_;
    }
    if ( (GNUTLS_E_PUSH_ERROR == ret) ||
         (GNUTLS_E_INTERNAL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_IOCTL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_DEVICE_ERROR == ret) )
    {
      mhd_log_probe ("MHD_send_data_: TLS pipe error %zd -> returning MHD_ERR_PIPE_", ret);
      return MHD_ERR_PIPE_;
    }
#if defined(GNUTLS_E_PREMATURE_TERMINATION)
    if (GNUTLS_E_PREMATURE_TERMINATION == ret)
    {
      mhd_log_probe ("MHD_send_data_: GNUTLS_E_PREMATURE_TERMINATION -> returning MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#elif defined(GNUTLS_E_UNEXPECTED_PACKET_LENGTH)
    if (GNUTLS_E_UNEXPECTED_PACKET_LENGTH == ret)
    {
      mhd_log_probe ("MHD_send_data_: GNUTLS_E_UNEXPECTED_PACKET_LENGTH -> returning MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#endif /* GNUTLS_E_UNEXPECTED_PACKET_LENGTH */
    if (GNUTLS_E_MEMORY_ERROR == ret)
    {
      mhd_log_probe ("MHD_send_data_: GNUTLS_E_MEMORY_ERROR -> returning MHD_ERR_NOMEM_");
      return MHD_ERR_NOMEM_;
    }
    if (ret < 0)
    {
      /* Treat any other error as hard error. */
      mhd_log_probe ("MHD_send_data_: TLS other error %zd -> returning MHD_ERR_NOTCONN_", ret);
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    /* Unlike non-TLS connections, do not reset "write-ready" if
     * sent amount smaller than provided amount, as TLS
     * connections may break data into smaller parts for sending. */
#endif /* EPOLL_SUPPORT */
#else  /* ! HTTPS_SUPPORT  */
    /* This branch is compiled in when HTTPS_SUPPORT is NOT defined.
     * However it sits inside `if (tls_conn)` where tls_conn is a
     * compile-time constant false in that configuration, so this
     * statement will be unreachable at runtime. We still log the
     * presence of the compiled-in fallback to aid static analysis. */
    mhd_log_probe ("MHD_send_data_: compiled fallback in TLS branch when HTTPS_SUPPORT not defined -> setting ret = MHD_ERR_NOTCONN_");
    ret = MHD_ERR_NOTCONN_;
#endif /* ! HTTPS_SUPPORT  */
  }
  else
  {
    /* plaintext transmission */
    mhd_log_probe ("MHD_send_data_: entering plaintext branch");
    if (buffer_size > MHD_SCKT_SEND_MAX_SIZE_)
    {
      buffer_size = MHD_SCKT_SEND_MAX_SIZE_; /* send() return value limit */
      push_data = false; /* Incomplete send */
      mhd_log_probe ("MHD_send_data_: buffer_size trimmed to MHD_SCKT_SEND_MAX_SIZE_, push_data set to false");
    }

    pre_send_setopt (connection, (! tls_conn), push_data);
#ifdef MHD_USE_MSG_MORE
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      push_data ? 0 : MSG_MORE);
#else
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      0);
#endif

    mhd_log_probe ("MHD_send_data_: plaintext send returned %zd", ret);

    if (0 > ret)
    {
      const int err = MHD_socket_get_error_ ();

      if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
      {
#ifdef EPOLL_SUPPORT
        /* EAGAIN, no longer write-ready */
        connection->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
        mhd_log_probe ("MHD_send_data_: plaintext EAGAIN -> returning MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        mhd_log_probe ("MHD_send_data_: plaintext EINTR -> returning MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
      {
        mhd_log_probe ("MHD_send_data_: plaintext REMOTE_DISCONNECT -> returning MHD_ERR_CONNRESET_");
        return MHD_ERR_CONNRESET_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
      {
        mhd_log_probe ("MHD_send_data_: plaintext EPIPE -> returning MHD_ERR_PIPE_");
        return MHD_ERR_PIPE_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
      {
        mhd_log_probe ("MHD_send_data_: plaintext EOPNOTSUPP -> returning MHD_ERR_OPNOTSUPP_");
        return MHD_ERR_OPNOTSUPP_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
      {
        mhd_log_probe ("MHD_send_data_: plaintext ENOTCONN -> returning MHD_ERR_NOTCONN_");
        return MHD_ERR_NOTCONN_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
      {
        mhd_log_probe ("MHD_send_data_: plaintext EINVAL -> returning MHD_ERR_INVAL_");
        return MHD_ERR_INVAL_;
      }
      if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
      {
        mhd_log_probe ("MHD_send_data_: plaintext low resources -> returning MHD_ERR_NOMEM_");
        return MHD_ERR_NOMEM_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
      {
        mhd_log_probe ("MHD_send_data_: plaintext EBADF -> returning MHD_ERR_BADF_");
        return MHD_ERR_BADF_;
      }
      /* Treat any other error as a hard error. */
      mhd_log_probe ("MHD_send_data_: plaintext other error %d -> returning MHD_ERR_NOTCONN_", err);
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    else if (buffer_size > (size_t) ret)
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
  }

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  /* If TLS connection is used then next final send() will be
   * without MSG_MORE support. If non-TLS connection is used
   * it's unknown whether sendfile() will be used or not so
   * assume that next call will be the same, like this call. */
  if ( (push_data) &&
       (buffer_size == (size_t) ret) )
  {
    mhd_log_probe ("MHD_send_data_: calling post_send_setopt (push_data=%d, buffer_size=%zu, ret=%zd)", (int) push_data, buffer_size, ret);
    post_send_setopt (connection, (! tls_conn), push_data);
  }

  mhd_log_probe ("MHD_send_data_: exit ret=%zd", ret);
  return ret;
}
