#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper. Opens /tmp/logs/362.txt in append mode and writes messages.
 * Kept minimal to avoid changing program logic. */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/362.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

_MHD_EXTERN void
MHD_stop_daemon (struct MHD_Daemon *daemon)
{
  MHD_socket fd;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  unsigned int i;
#endif

  mhd_probe_log("MHD_stop_daemon: entry daemon=%p", (void *)daemon);

  if (NULL == daemon)
  {
    mhd_probe_log("MHD_stop_daemon: daemon is NULL -> return");
    return;
  }

  mhd_probe_log("MHD_stop_daemon: shutdown=%d master=%p",
                (int)(daemon->shutdown), (void *)daemon->master);
  if ( (daemon->shutdown) && (NULL == daemon->master) )
    MHD_PANIC (_ ("MHD_stop_daemon() was called twice."));

  /* Log values used in assertions to provide runtime evidence. */
  mhd_probe_log("MHD_stop_daemon: options=0x%08x worker_pool=%p tid=%p",
                (unsigned int)daemon->options,
                (void *)daemon->worker_pool,
                (void *)daemon->tid);
  mhd_probe_log("MHD_stop_daemon: assert-1 operands: use_select_internally=%d, worker_pool!=NULL=%d, tid_valid=%d",
                (int)(0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)),
                (int)(NULL != daemon->worker_pool),
                (int)MHD_thread_handle_ID_is_valid_handle_(daemon->tid));

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (NULL != daemon->worker_pool) || \
              (MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_probe_log("MHD_stop_daemon: after first mhd_assert (note: mhd_assert may be a no-op)");

  mhd_probe_log("MHD_stop_daemon: assert-2 operands: select_and_pool=%d, not_tid_valid=%d",
                (int)(((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) &&
                       (NULL == daemon->worker_pool))),
                (int)(! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));

  mhd_assert (((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) &&
               (NULL == daemon->worker_pool)) || \
              (! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_probe_log("MHD_stop_daemon: after second mhd_assert (note: mhd_assert may be a no-op)");

  /* Slave daemons must be stopped by master daemon. */
  mhd_probe_log("MHD_stop_daemon: master=%p shutdown=%d", (void *)daemon->master, (int)daemon->shutdown);
  mhd_assert ( (NULL == daemon->master) || (daemon->shutdown) );
  mhd_probe_log("MHD_stop_daemon: after master/shutdown mhd_assert (note: mhd_assert may be a no-op)");

  daemon->shutdown = true;
  mhd_probe_log("MHD_stop_daemon: set daemon->shutdown = true");

  if (daemon->was_quiesced)
    fd = MHD_INVALID_SOCKET; /* Do not use FD if daemon was quiesced */
  else
    fd = daemon->listen_fd;

  mhd_probe_log("MHD_stop_daemon: computed fd=%ld was_quiesced=%d",
                (long)fd, (int)daemon->was_quiesced);

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {   /* Master daemon with worker pool. */
    mhd_probe_log("MHD_stop_daemon: entering worker_pool branch worker_pool=%p size=%u",
                  (void *)daemon->worker_pool,
                  (unsigned)daemon->worker_pool_size);

    mhd_assert (1 < daemon->worker_pool_size);
    mhd_assert (MHD_D_IS_USING_THREADS_ (daemon));
    mhd_probe_log("MHD_stop_daemon: after worker pool assertions");

    /* Let workers shutdown in parallel. */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      mhd_probe_log("MHD_stop_daemon: loop worker i=%u before set shutdown", i);
      daemon->worker_pool[i].shutdown = true;
      if (MHD_ITC_IS_VALID_ (daemon->worker_pool[i].itc))
      {
        mhd_probe_log("MHD_stop_daemon: worker[%u].itc is valid -> activating itc", i);
        if (! MHD_itc_activate_ (daemon->worker_pool[i].itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
        mhd_probe_log("MHD_stop_daemon: worker[%u].itc activation returned", i);
      }
      else
      {
        mhd_probe_log("MHD_stop_daemon: worker[%u].itc is INVALID -> fd=%ld (will assert that fd != MHD_INVALID_SOCKET)",
                      i, (long)fd);
        /* This mhd_assert is the one reported as dead in the scenario. We leave it
         * in place and add logging so that runtime traces contain the evaluated
         * state and show that the assertion had no runtime effect if the macro is
         * defined to ((void)0). */
        mhd_assert (MHD_INVALID_SOCKET != fd);
        mhd_probe_log("MHD_stop_daemon: after mhd_assert(MHD_INVALID_SOCKET != fd) (note: may be no-op)");
      }
    }
#ifdef HAVE_LISTEN_SHUTDOWN
    if (MHD_INVALID_SOCKET != fd)
    {
      (void) shutdown (fd,
                       SHUT_RDWR);
      mhd_probe_log("MHD_stop_daemon: performed shutdown(fd)");
    }
#endif /* HAVE_LISTEN_SHUTDOWN */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      mhd_probe_log("MHD_stop_daemon: recursively stopping worker %u at %p", i, (void *)&daemon->worker_pool[i]);
      MHD_stop_daemon (&daemon->worker_pool[i]);
    }
    free (daemon->worker_pool);
    mhd_probe_log("MHD_stop_daemon: freed worker_pool");
    mhd_assert (MHD_ITC_IS_INVALID_ (daemon->itc));
#ifdef EPOLL_SUPPORT
    mhd_assert (-1 == daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    mhd_assert (-1 == daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */
  }
  else
#endif
  {   /* Worker daemon or single daemon. */
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    if (MHD_D_IS_USING_THREADS_ (daemon))
    {     /* Worker daemon or single daemon with internal thread(s). */
      mhd_probe_log("MHD_stop_daemon: using threads branch (worker_pool_size=%u)", (unsigned)daemon->worker_pool_size);
      mhd_assert (0 == daemon->worker_pool_size);
      /* Separate thread(s) is used for polling sockets. */
      if (MHD_ITC_IS_VALID_ (daemon->itc))
      {
        mhd_probe_log("MHD_stop_daemon: itc is valid -> activating itc for worker/single daemon");
        if (! MHD_itc_activate_ (daemon->itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
      }
      else
      {
#ifdef HAVE_LISTEN_SHUTDOWN
        if (MHD_INVALID_SOCKET != fd)
        {
          if (NULL == daemon->master)
            (void) shutdown (fd,
                             SHUT_RDWR);
          mhd_probe_log("MHD_stop_daemon: shutdown(fd) called in threads branch");
        }
        else
#endif /* HAVE_LISTEN_SHUTDOWN */
        mhd_assert (false); /* Should never happen */
      }

      if (! MHD_thread_handle_ID_join_thread_ (daemon->tid))
      {
        MHD_PANIC (_ ("Failed to join a thread.\n"));
      }
      mhd_probe_log("MHD_stop_daemon: joined thread tid=%p", (void *)daemon->tid);
      /* close_all_connections() was called in daemon thread. */
    }
    else
#endif
    {
      /* No internal threads are used for polling sockets. */
      mhd_probe_log("MHD_stop_daemon: no internal threads -> close_all_connections(%p)", (void *)daemon);
      close_all_connections (daemon);
    }
    mhd_probe_log("MHD_stop_daemon: post-connections cleanup checks: connections_head=%p cleanup_head=%p suspended=%p new=%p",
                  (void *)daemon->connections_head,
                  (void *)daemon->cleanup_head,
                  (void *)daemon->suspended_connections_head,
                  (void *)daemon->new_connections_head);
    mhd_assert (NULL == daemon->connections_head);
    mhd_assert (NULL == daemon->cleanup_head);
    mhd_assert (NULL == daemon->suspended_connections_head);
    mhd_assert (NULL == daemon->new_connections_head);
#if defined(UPGRADE_SUPPORT) && defined(HTTPS_SUPPORT)
    mhd_assert (NULL == daemon->urh_head);
#endif /* UPGRADE_SUPPORT && HTTPS_SUPPORT */

    if (MHD_ITC_IS_VALID_ (daemon->itc))
      MHD_itc_destroy_chk_ (daemon->itc);

#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_fd) )
      MHD_socket_close_chk_ (daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_upgrade_fd) )
      MHD_socket_close_chk_ (daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->cleanup_connection_mutex);
    MHD_mutex_destroy_chk_ (&daemon->new_connections_mutex);
#endif
  }

  if (NULL == daemon->master)
  {   /* Cleanup that should be done only one time in master/single daemon.
       * Do not perform this cleanup in worker daemons. */

    mhd_probe_log("MHD_stop_daemon: master is NULL -> master cleanup path (fd=%ld)", (long)fd);

    if (MHD_INVALID_SOCKET != fd)
    {
      MHD_socket_close_chk_ (fd);
      mhd_probe_log("MHD_stop_daemon: closed listen fd=%ld", (long)fd);
    }

    /* TLS clean up */
#ifdef HTTPS_SUPPORT
    if (daemon->have_dhparams)
    {
      gnutls_dh_params_deinit (daemon->https_mem_dhparams);
      daemon->have_dhparams = false;
      mhd_probe_log("MHD_stop_daemon: deinitialized dhparams");
    }
    if (0 != (daemon->options & MHD_USE_TLS))
    {
      gnutls_priority_deinit (daemon->priority_cache);
      if (daemon->x509_cred)
        gnutls_certificate_free_credentials (daemon->x509_cred);
      if (daemon->psk_cred)
        gnutls_psk_free_server_credentials (daemon->psk_cred);
      mhd_probe_log("MHD_stop_daemon: TLS credentials cleaned up");
    }
#endif /* HTTPS_SUPPORT */

#ifdef DAUTH_SUPPORT
    free (daemon->digest_auth_random_copy);
    free (daemon->nnc);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->nnc_lock);
#endif
    mhd_probe_log("MHD_stop_daemon: DAUTH support cleanup done");
#endif
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->per_ip_connection_mutex);
#endif
    free (daemon);
    mhd_probe_log("MHD_stop_daemon: freed daemon at %p","", (void*)daemon);
  }
}
