#include <stdio.h>
#include <stdlib.h>
#include <stdint.h>
#include <time.h>
#include <stdbool.h>

/* Forward declaration of the daemon structure used in the original file. */
struct MHD_Daemon;

/*
 * Simple probe logger that appends control-flow and selected state
 * information to /tmp/logs/362.txt. It intentionally mirrors fields
 * referenced in MHD_stop_daemon to capture evidence that mhd_assert
 * calls are executed but (in the reported configuration) perform no
 * effect.
 *
 * This helper preserves original logic: it only observes state and
 * performs I/O to a log file. It must not change daemon state.
 */
static void
mhd_probe_log (const char *label, const struct MHD_Daemon *daemon)
{
  FILE *f = fopen("/tmp/logs/362.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  fprintf(f, "[%ld] %s: daemon=%p\n", (long)t, label, (const void *) daemon);
  if (daemon)
  {
    /* We access the fields that are referenced in the original function.
     * Casts to integer types are used where necessary to avoid warnings.
     * The exact types of some fields come from the original compilation unit
     * and are expected to be compatible. */
    /* Note: we don't modify any fields. */
    fprintf(f, "  fields: \\n");
    /* Many fields are present in the original function. Print them if available. */
    /* Using pointer casts for opaque types like tid, itc, worker_pool, etc. */
    fprintf(f, "    shutdown=%d\n", (int) ((const volatile bool *) &daemon->shutdown)[0]);
    fprintf(f, "    master=%p\n", (const void *) daemon->master);
    fprintf(f, "    options=%u\n", (unsigned int) daemon->options);
    fprintf(f, "    worker_pool=%p\n", (const void *) daemon->worker_pool);
    fprintf(f, "    worker_pool_size=%u\n", (unsigned int) daemon->worker_pool_size);
    fprintf(f, "    was_quiesced=%d\n", (int) daemon->was_quiesced);
    fprintf(f, "    listen_fd=%ld\n", (long) (intptr_t) daemon->listen_fd);
    fprintf(f, "    connections_head=%p\n", (const void *) daemon->connections_head);
    fprintf(f, "    cleanup_head=%p\n", (const void *) daemon->cleanup_head);
    fprintf(f, "    suspended_connections_head=%p\n", (const void *) daemon->suspended_connections_head);
    fprintf(f, "    new_connections_head=%p\n", (const void *) daemon->new_connections_head);
#ifdef UPGRADE_SUPPORT
#ifdef HTTPS_SUPPORT
    fprintf(f, "    urh_head=%p\n", (const void *) daemon->urh_head);
#endif
#endif
#ifdef EPOLL_SUPPORT
    fprintf(f, "    epoll_fd=%d\n", (int) daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    fprintf(f, "    epoll_upgrade_fd=%d\n", (int) daemon->epoll_upgrade_fd);
#endif
#endif
    /* Log tid and itc as opaque handles. */
    fprintf(f, "    tid=%p\n", (const void *) (uintptr_t) daemon->tid);
    fprintf(f, "    itc=%p\n", (const void *) (uintptr_t) daemon->itc);
  }
  fclose(f);
}

/* Modified MHD_stop_daemon with probes inserted. Original logic preserved. */
_MHD_EXTERN void
MHD_stop_daemon (struct MHD_Daemon *daemon)
{
  MHD_socket fd;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  unsigned int i;
#endif

  mhd_probe_log("enter MHD_stop_daemon", daemon);

  if (NULL == daemon)
  {
    mhd_probe_log("early_return NULL daemon", daemon);
    return;
  }
  if ( (daemon->shutdown) && (NULL == daemon->master) )
  {
    mhd_probe_log("panic: called twice scenario", daemon);
    MHD_PANIC (_ ("MHD_stop_daemon() was called twice."));
  }

  mhd_probe_log("before assert: select/worker/tid check", daemon);
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (NULL != daemon->worker_pool) || \
              (MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_probe_log("after assert: select/worker/tid check", daemon);

  mhd_probe_log("before assert: select/thread inverse check", daemon);
  mhd_assert (((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) &&
               (NULL == daemon->worker_pool)) || \
              (! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_probe_log("after assert: select/thread inverse check", daemon);

  /* Slave daemons must be stopped by master daemon. */
  mhd_probe_log("before assert: slave daemon check", daemon);
  mhd_assert ( (NULL == daemon->master) || (daemon->shutdown) );
  mhd_probe_log("after assert: slave daemon check", daemon);

  daemon->shutdown = true;
  mhd_probe_log("after setting daemon->shutdown=true", daemon);

  if (daemon->was_quiesced)
    fd = MHD_INVALID_SOCKET; /* Do not use FD if daemon was quiesced */
  else
    fd = daemon->listen_fd;
  mhd_probe_log("after fd selection", daemon);

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {   /* Master daemon with worker pool. */
    mhd_probe_log("enter: master daemon with worker_pool", daemon);
    mhd_assert (1 < daemon->worker_pool_size);
    mhd_assert (MHD_D_IS_USING_THREADS_ (daemon));

    /* Let workers shutdown in parallel. */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      daemon->worker_pool[i].shutdown = true;
      if (MHD_ITC_IS_VALID_ (daemon->worker_pool[i].itc))
      {
        if (! MHD_itc_activate_ (daemon->worker_pool[i].itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
      }
      else
      {
        mhd_probe_log("worker_pool: itc invalid for worker", daemon);
        mhd_assert (MHD_INVALID_SOCKET != fd);
      }
    }
#ifdef HAVE_LISTEN_SHUTDOWN
    if (MHD_INVALID_SOCKET != fd)
    {
      (void) shutdown (fd,
                       SHUT_RDWR);
    }
#endif /* HAVE_LISTEN_SHUTDOWN */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      mhd_probe_log("recursing into worker: before MHD_stop_daemon", &daemon->worker_pool[i]);
      MHD_stop_daemon (&daemon->worker_pool[i]);
      mhd_probe_log("recursing into worker: after MHD_stop_daemon", &daemon->worker_pool[i]);
    }
    free (daemon->worker_pool);
    mhd_probe_log("freed worker_pool", daemon);
    mhd_assert (MHD_ITC_IS_INVALID_ (daemon->itc));
#ifdef EPOLL_SUPPORT
    mhd_assert (-1 == daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    mhd_assert (-1 == daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */
  }
  else
#endif
  {   /* Worker daemon or single daemon. */
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    if (MHD_D_IS_USING_THREADS_ (daemon))
    {     /* Worker daemon or single daemon with internal thread(s). */
      mhd_probe_log("enter: daemon uses internal threads", daemon);
      mhd_assert (0 == daemon->worker_pool_size);
      /* Separate thread(s) is used for polling sockets. */
      if (MHD_ITC_IS_VALID_ (daemon->itc))
      {
        if (! MHD_itc_activate_ (daemon->itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
      }
      else
      {
#ifdef HAVE_LISTEN_SHUTDOWN
        if (MHD_INVALID_SOCKET != fd)
        {
          if (NULL == daemon->master)
            (void) shutdown (fd,
                             SHUT_RDWR);
        }
        else
#endif /* HAVE_LISTEN_SHUTDOWN */
        mhd_assert (false); /* Should never happen */
      }

      if (! MHD_thread_handle_ID_join_thread_ (daemon->tid))
      {
        MHD_PANIC (_ ("Failed to join a thread.\n"));
      }
      /* close_all_connections() was called in daemon thread. */
    }
    else
#endif
    {
      /* No internal threads are used for polling sockets. */
      mhd_probe_log("calling close_all_connections", daemon);
      close_all_connections (daemon);
      mhd_probe_log("returned from close_all_connections", daemon);
    }
    mhd_probe_log("before asserts checking that lists are empty", daemon);
    mhd_assert (NULL == daemon->connections_head);
    mhd_assert (NULL == daemon->cleanup_head);
    mhd_probe_log("before assert: suspended_connections_head check", daemon);
    mhd_assert (NULL == daemon->suspended_connections_head);
    mhd_probe_log("after assert: suspended_connections_head check", daemon);
    mhd_assert (NULL == daemon->new_connections_head);
#if defined(UPGRADE_SUPPORT) && defined(HTTPS_SUPPORT)
    mhd_assert (NULL == daemon->urh_head);
#endif /* UPGRADE_SUPPORT && HTTPS_SUPPORT */

    if (MHD_ITC_IS_VALID_ (daemon->itc))
      MHD_itc_destroy_chk_ (daemon->itc);

#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_fd) )
      MHD_socket_close_chk_ (daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_upgrade_fd) )
      MHD_socket_close_chk_ (daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->cleanup_connection_mutex);
    MHD_mutex_destroy_chk_ (&daemon->new_connections_mutex);
#endif
  }

  if (NULL == daemon->master)
  {   /* Cleanup that should be done only one time in master/single daemon.
       * Do not perform this cleanup in worker daemons. */

    if (MHD_INVALID_SOCKET != fd)
      MHD_socket_close_chk_ (fd);

    mhd_probe_log("master cleanup: after socket close (if any)", daemon);

    /* TLS clean up */
#ifdef HTTPS_SUPPORT
    if (daemon->have_dhparams)
    {
      gnutls_dh_params_deinit (daemon->https_mem_dhparams);
      daemon->have_dhparams = false;
    }
    if (0 != (daemon->options & MHD_USE_TLS))
    {
      gnutls_priority_deinit (daemon->priority_cache);
      if (daemon->x509_cred)
        gnutls_certificate_free_credentials (daemon->x509_cred);
      if (daemon->psk_cred)
        gnutls_psk_free_server_credentials (daemon->psk_cred);
    }
#endif /* HTTPS_SUPPORT */

#ifdef DAUTH_SUPPORT
    free (daemon->digest_auth_random_copy);
    free (daemon->nnc);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->nnc_lock);
#endif
#endif
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->per_ip_connection_mutex);
#endif
    mhd_probe_log("about to free daemon (master)", daemon);
    free (daemon);
    mhd_probe_log("freed daemon (master)", NULL);
  }
  mhd_probe_log("exit MHD_stop_daemon", NULL);
}
