#include <stdio.h>
#include <stdlib.h>
#include <stdarg.h>
#include <time.h>
#include <stdbool.h>

/* Lightweight probe logging helpers. These are non-intrusive and
 * preserve original behavior. Logs are appended to /tmp/logs/363.txt.
 */
static void
mhd_probe_log_v (const char *fmt, va_list ap)
{
  FILE *f = fopen("/tmp/logs/363.txt", "a");
  if (NULL == f)
    return;
  time_t now = time(NULL);
  if (now != (time_t)-1)
    fprintf(f, "[%ld] ", (long) now);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  fclose(f);
}

static void
mhd_probe_log (const char *fmt, ...)
{
  va_list ap;
  va_start(ap, fmt);
  mhd_probe_log_v(fmt, ap);
  va_end(ap);
}

_MHD_EXTERN void
MHD_stop_daemon (struct MHD_Daemon *daemon)
{
  MHD_socket fd;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  unsigned int i;
#endif

  mhd_probe_log("Entered MHD_stop_daemon: daemon=%p", (void *) daemon);

  if (NULL == daemon)
  {
    mhd_probe_log("Daemon pointer is NULL, returning immediately");
    return;
  }
  if ( (daemon->shutdown) && (NULL == daemon->master) )
  {
    mhd_probe_log("Panic: MHD_stop_daemon() called twice on master daemon=%p", (void *) daemon);
    MHD_PANIC (_ ("MHD_stop_daemon() was called twice."));
  }

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (NULL != daemon->worker_pool) || \
              (MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_assert (((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) &&
               (NULL == daemon->worker_pool)) || \
              (! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));

  /* Slave daemons must be stopped by master daemon. */
  mhd_assert ( (NULL == daemon->master) || (daemon->shutdown) );

  daemon->shutdown = true;
  mhd_probe_log("Set daemon->shutdown = true for daemon=%p", (void *) daemon);

  if (daemon->was_quiesced)
  {
    fd = MHD_INVALID_SOCKET; /* Do not use FD if daemon was quiesced */
    mhd_probe_log("Daemon was quiesced, using invalid socket for daemon=%p", (void *) daemon);
  }
  else
  {
    fd = daemon->listen_fd;
    mhd_probe_log("Using listen_fd=%d for daemon=%p", (int) fd, (void *) daemon);
  }

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {   /* Master daemon with worker pool. */
    mhd_assert (1 < daemon->worker_pool_size);
    mhd_assert (MHD_D_IS_USING_THREADS_ (daemon));

    /* Let workers shutdown in parallel. */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      daemon->worker_pool[i].shutdown = true;
      mhd_probe_log("Marked worker_pool[%u].shutdown = true (daemon=%p)", i, (void *) daemon);
      if (MHD_ITC_IS_VALID_ (daemon->worker_pool[i].itc))
      {
        /* Log the call that could be problematic: passing a string literal "e". */
        mhd_probe_log("About to call MHD_itc_activate_ for worker %u: itc=%p, msg_literal=%p, msg=\"e\", daemon=%p",
                      i,
                      (void *) daemon->worker_pool[i].itc,
                      (void *) "e",
                      (void *) daemon);
        if (! MHD_itc_activate_ (daemon->worker_pool[i].itc,
                                 "e"))
        {
          mhd_probe_log("MHD_itc_activate_ returned FALSE for worker %u (itc=%p, daemon=%p)",
                        i,
                        (void *) daemon->worker_pool[i].itc,
                        (void *) daemon);
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
        }
        else
        {
          mhd_probe_log("MHD_itc_activate_ returned TRUE for worker %u (itc=%p, daemon=%p)",
                        i,
                        (void *) daemon->worker_pool[i].itc,
                        (void *) daemon);
        }
      }
      else
      {
        mhd_probe_log("Worker %u has invalid ITC and expects socket close (fd=%d), daemon=%p",
                      i, (int) fd, (void *) daemon);
        mhd_assert (MHD_INVALID_SOCKET != fd);
      }
    }
#ifdef HAVE_LISTEN_SHUTDOWN
    if (MHD_INVALID_SOCKET != fd)
    {
      mhd_probe_log("Calling shutdown(fd=%d) for master daemon=%p", (int) fd, (void *) daemon);
      (void) shutdown (fd,
                       SHUT_RDWR);
    }
#endif /* HAVE_LISTEN_SHUTDOWN */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      mhd_probe_log("Recursively stopping worker daemon at &worker_pool[%u] for master daemon=%p", i, (void *) daemon);
      MHD_stop_daemon (&daemon->worker_pool[i]);
    }
    free (daemon->worker_pool);
    mhd_probe_log("Freed daemon->worker_pool for daemon=%p", (void *) daemon);
    mhd_assert (MHD_ITC_IS_INVALID_ (daemon->itc));
#ifdef EPOLL_SUPPORT
    mhd_assert (-1 == daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    mhd_assert (-1 == daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */
  }
  else
#endif
  {   /* Worker daemon or single daemon. */
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    if (MHD_D_IS_USING_THREADS_ (daemon))
    {     /* Worker daemon or single daemon with internal thread(s). */
      mhd_assert (0 == daemon->worker_pool_size);
      /* Separate thread(s) is used for polling sockets. */
      if (MHD_ITC_IS_VALID_ (daemon->itc))
      {
        mhd_probe_log("About to call MHD_itc_activate_ for daemon itc=%p, msg_literal=%p, daemon=%p",
                      (void *) daemon->itc,
                      (void *) "e",
                      (void *) daemon);
        if (! MHD_itc_activate_ (daemon->itc,
                                 "e"))
        {
          mhd_probe_log("MHD_itc_activate_ returned FALSE for daemon itc=%p, daemon=%p",
                        (void *) daemon->itc,
                        (void *) daemon);
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
        }
        else
        {
          mhd_probe_log("MHD_itc_activate_ returned TRUE for daemon itc=%p, daemon=%p",
                        (void *) daemon->itc,
                        (void *) daemon);
        }
      }
      else
      {
#ifdef HAVE_LISTEN_SHUTDOWN
        if (MHD_INVALID_SOCKET != fd)
        {
          if (NULL == daemon->master)
          {
            mhd_probe_log("Calling shutdown(fd=%d) for non-master daemon=%p", (int) fd, (void *) daemon);
            (void) shutdown (fd,
                             SHUT_RDWR);
          }
        }
        else
#endif /* HAVE_LISTEN_SHUTDOWN */
        {
          mhd_probe_log("Unexpected: No ITC and invalid fd for daemon=%p; asserting false", (void *) daemon);
          mhd_assert (false); /* Should never happen */
        }
      }

      if (! MHD_thread_handle_ID_join_thread_ (daemon->tid))
      {
        mhd_probe_log("Failed to join thread for daemon tid=%p, daemon=%p", (void *) &daemon->tid, (void *) daemon);
        MHD_PANIC (_ ("Failed to join a thread.\n"));
      }
      /* close_all_connections() was called in daemon thread. */
    }
    else
#endif
    {
      /* No internal threads are used for polling sockets. */
      mhd_probe_log("No internal threads: calling close_all_connections for daemon=%p", (void *) daemon);
      close_all_connections (daemon);
    }
    mhd_assert (NULL == daemon->connections_head);
    mhd_assert (NULL == daemon->cleanup_head);
    mhd_assert (NULL == daemon->suspended_connections_head);
    mhd_assert (NULL == daemon->new_connections_head);
#if defined(UPGRADE_SUPPORT) && defined(HTTPS_SUPPORT)
    mhd_assert (NULL == daemon->urh_head);
#endif /* UPGRADE_SUPPORT && HTTPS_SUPPORT */

    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      mhd_probe_log("Destroying ITC for daemon itc=%p, daemon=%p", (void *) daemon->itc, (void *) daemon);
      MHD_itc_destroy_chk_ (daemon->itc);
    }

#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_fd) )
      MHD_socket_close_chk_ (daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_upgrade_fd) )
      MHD_socket_close_chk_ (daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->cleanup_connection_mutex);
    MHD_mutex_destroy_chk_ (&daemon->new_connections_mutex);
#endif
  }

  if (NULL == daemon->master)
  {   /* Cleanup that should be done only one time in master/single daemon.
       * Do not perform this cleanup in worker daemons. */

    if (MHD_INVALID_SOCKET != fd)
    {
      mhd_probe_log("Closing listen fd=%d for master daemon=%p", (int) fd, (void *) daemon);
      MHD_socket_close_chk_ (fd);
    }

    /* TLS clean up */
#ifdef HTTPS_SUPPORT
    if (daemon->have_dhparams)
    {
      gnutls_dh_params_deinit (daemon->https_mem_dhparams);
      daemon->have_dhparams = false;
      mhd_probe_log("Deinitialized DH params for daemon=%p", (void *) daemon);
    }
    if (0 != (daemon->options & MHD_USE_TLS))
    {
      gnutls_priority_deinit (daemon->priority_cache);
      if (daemon->x509_cred)
        gnutls_certificate_free_credentials (daemon->x509_cred);
      if (daemon->psk_cred)
        gnutls_psk_free_server_credentials (daemon->psk_cred);
      mhd_probe_log("Deinitialized TLS credentials for daemon=%p", (void *) daemon);
    }
#endif /* HTTPS_SUPPORT */

#ifdef DAUTH_SUPPORT
    free (daemon->digest_auth_random_copy);
    free (daemon->nnc);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->nnc_lock);
#endif
#endif
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->per_ip_connection_mutex);
#endif
    mhd_probe_log("Freeing daemon structure for master daemon=%p", (void *) daemon);
    free (daemon);
  }
  mhd_probe_log("Exiting MHD_stop_daemon for daemon=%p", (void *) daemon);
}
