#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>
#include <limits.h>

/* Probe logging helper: append textual logs to /tmp/logs/58.txt */
static void add_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/58.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  add_log("ENTER add_response_header_connection: response=%p value='%s'",
          (void *) response, value ? value : "(null)");

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    add_log("REJECT: value contains CR or LF");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Probe the assertion conditions without changing program logic. */
    {
      int memcmp_is_close = 0;
      if (hdr != NULL && hdr->value != NULL && hdr->value_size >= 5)
      {
        memcmp_is_close = (0 == memcmp (hdr->value, "close", 5));
      }
      add_log("ASSERT_PROBE[1]: already_has_close=%d memcmp_is_close=%d hdr=%p hdr->value='%s' hdr->value_size=%zu",
              (int) already_has_close, memcmp_is_close,
              (void *) hdr,
              (hdr && hdr->value) ? hdr->value : "(null)",
              (hdr) ? hdr->value_size : 0);

      add_log("ASSERT_PROBE[2]: hdr_null_check=%d",
              (int) (hdr != NULL));
    }

    /* Keep original asserts untouched in code (they may be no-ops in build). */
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;

    /* Probe the original assertion that the header is not present. */
    {
      struct MHD_HTTP_Res_Header *probe_hdr =
        MHD_get_response_element_n_ (response, MHD_HEADER_KIND, key, key_len);
      add_log("ASSERT_PROBE[3]: MHD_get_response_element_n_ returned %p (expected NULL in this branch)",
              (void *) probe_hdr);
    }

    add_log("ASSERT_PROBE[4]: flags_auto_has_connection_close=%d",
            (int) (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE)));
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }

  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  add_log("STATE: value_len=%zu old_value_len=%zu already_has_close=%d",
          value_len, old_value_len, (int) already_has_close);

  if (value_len >= SSIZE_MAX)
  {
    add_log("REJECT: value_len >= SSIZE_MAX");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    add_log("REJECT: norm_len >= SSIZE_MAX norm_len=%zu", norm_len);
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  add_log("ALLOC: buf=%p buf_size=%zu", (void *) buf, buf_size);
  if (NULL == buf)
  {
    add_log("REJECT: malloc failed");
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    add_log("CALL: MHD_str_remove_token_caseless_ returned value_has_close=%d norm_len_s=%zd",
            (int) value_has_close, norm_len_s);

    mhd_assert (0 <= norm_len_s);
    add_log("ASSERT_PROBE[5]: norm_len_s_nonneg=%d", (int) (norm_len_s >= 0));

    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      add_log("REJECT: norm_len_s < 0, freeing buf");
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    add_log("REJECT: upgrade_handler present and value_has_close");
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      add_log("REJECT: new value empty after normalisation and no 'close' token");
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      add_log("NOOP: already has 'close', nothing to modify");
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    /* Probe the buffer size assertion: use literal lengths for constants. */
    size_t len_close_comma = 7; /* length of "close, " */
    size_t len_close = 5; /* length of "close" */
    add_log("ASSERT_PROBE[6]: buf_size=%zu old_value_len=%zu norm_len=%zu required=%zu",
            buf_size, old_value_len, norm_len,
            old_value_len + norm_len + len_close_comma + 1);

    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    /* Probe assertion about pos using literal for close + comma length */
    size_t len_close_comma_probe = 7;
    add_log("ASSERT_PROBE[7]: value_has_close=%d already_has_close=%d pos=%zu old_value_len=%zu norm_len=%zu",
            (int) value_has_close, (int) already_has_close, pos, old_value_len, norm_len);

    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
  }
  add_log("STATE before final assert: buf_size=%zu pos=%zu", buf_size, pos);
  mhd_assert (buf_size > pos);
  add_log("ASSERT_PROBE[8]: final_buf_space_ok=%d", (int) (buf_size > pos));
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    add_log("ALLOC: new_hdr=%p (calloc)", (void *) new_hdr);
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      add_log("ALLOC: new_hdr->header=%p (malloc)", (void *) new_hdr->header);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        add_log("INSERTED new header: new_hdr=%p response->flags_auto=0x%x", (void *) new_hdr, (unsigned) response->flags_auto);
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    add_log("REJECT: failed to create new header, cleanup done");
    return MHD_NO;
  }

  /* Update existing header entry */
  add_log("UPDATE: hdr=%p old_value_ptr=%p old_value_size=%zu freeing old value", (void *) hdr, (void *) hdr->value, hdr->value_size);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  add_log("DONE: updated hdr=%p new_value='%s' new_value_size=%zu response->flags_auto=0x%x",
          (void *) hdr, hdr->value ? hdr->value : "(null)", hdr->value_size, (unsigned) response->flags_auto);
  return MHD_YES;
}
