#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>
#include <stdint.h>

/* Probe logging helper: append formatted logs to /tmp/logs/58.txt. */
static void probe_logf(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/58.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  probe_logf("add_response_header_connection: entry: response=%p, value='%s'",
             (void *)response, value ? value : "(null)");

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    probe_logf("add_response_header_connection: rejected: value contains CR/LF");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Probe log and safe evaluation for the original assert:
       mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5))); */
    if (hdr != NULL)
    {
      int cmp_is_close = (0 == memcmp (hdr->value, "close", 5));
      probe_logf("assert#1: already_has_close=%d, hdr=%p, hdr->value='%.*s', cmp_is_close=%d",
                 already_has_close, (void *)hdr,
                 (int) hdr->value_size, hdr->value ? hdr->value : "(null)",
                 cmp_is_close);
      /* Construct a safe expression that does not dereference hdr when NULL. */
      mhd_assert (already_has_close == (hdr ? cmp_is_close : already_has_close));
    }
    else
    {
      probe_logf("assert#1: already_has_close=%d, hdr=NULL; memcmp not evaluated",
                 already_has_close);
      mhd_assert (already_has_close == already_has_close); /* safe no-op */
    }

    /* Probe and execute the original NULL check assert safely */
    probe_logf("assert#2: checking hdr != NULL (hdr=%p)", (void *)hdr);
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    /* Original assert called MHD_get_response_element_n_ inside the assert.
       To avoid changing program behaviour (calling that function when it may
       be intentionally omitted by assert macro), we do not call it here.
       Instead we log the expectation and use a safe equivalent expression. */
    probe_logf("assert#3: expecting no existing header: hdr set to NULL, not calling MHD_get_response_element_n_");
    mhd_assert (NULL == hdr);

    probe_logf("assert#4: expecting flags_auto has no CONNECTION_CLOSE flag: flags_auto=0x%zx",
               (size_t) response->flags_auto);
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }

  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  probe_logf("value_len=%zu, old_value_len=%zu", value_len, old_value_len);
  if (value_len >= SSIZE_MAX)
  {
    probe_logf("add_response_header_connection: rejected: value_len >= SSIZE_MAX");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    probe_logf("add_response_header_connection: rejected: norm_len >= SSIZE_MAX");
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    probe_logf("add_response_header_connection: malloc failed for size %zu", buf_size);
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    probe_logf("after remove_token: value_has_close=%d, norm_len_s=%zd",
               value_has_close, norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      probe_logf("add_response_header_connection: abnormal negative norm_len_s -> returning NO");
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    free (buf);
    probe_logf("add_response_header_connection: rejected: upgrade_handler present and value_has_close");
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  probe_logf("after normalisation: norm_len=%zu, value_has_close=%d", norm_len, value_has_close);
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      probe_logf("add_response_header_connection: new value empty and no close token -> NO");
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      probe_logf("add_response_header_connection: already has close -> YES");
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    probe_logf("about to assert buffer size for inserting close: buf_size=%zu, old_value_len=%zu, norm_len=%zu",
               buf_size, old_value_len, norm_len);
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    probe_logf("inserted 'close' at front: pos=%zu", pos);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    probe_logf("appended old header value, new pos=%zu", pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    probe_logf("before assert#7: value_has_close=%d, already_has_close=%d, MHD_STATICSTR_LEN(close, )=%zu, old_value_len=%zu, pos=%zu, norm_len=%zu",
               value_has_close, already_has_close,
               (size_t) MHD_STATICSTR_LEN_ ("close, "), old_value_len, pos, norm_len);
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
  }
  probe_logf("before final assert: buf_size=%zu, pos=%zu", buf_size, pos);
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        probe_logf("inserted new header: header='%.*s', value='%s', flags_auto=0x%zx",
                   (int) new_hdr->header_size, new_hdr->header,
                   new_hdr->value ? new_hdr->value : "(null)", (size_t) response->flags_auto);
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    probe_logf("failed to create new header -> NO");
    return MHD_NO;
  }

  /* Update existing header entry */
  probe_logf("updating existing header (hdr=%p): old_value='%.*s' -> new_value='%s'",
             (void *) hdr,
             (int) hdr->value_size, hdr->value ? hdr->value : "(null)",
             buf ? buf : "(null)");
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  probe_logf("update done: hdr=%p, value_size=%zu, flags_auto=0x%zx",
             (void *) hdr, hdr->value_size, (size_t) response->flags_auto);
  return MHD_YES;
}
