static void
mhd_log_probe (const char *stage,
               struct MHD_Response *response,
               struct MHD_HTTP_Res_Header *hdr,
               const char *value,
               size_t value_len,
               size_t old_value_len,
               size_t buf_size,
               size_t norm_len,
               bool value_has_close,
               bool already_has_close,
               size_t pos)
{
  FILE *f = fopen("/tmp/logs/63.txt", "a");
  if (NULL == f)
    return;
  fprintf (f, "STAGE=%s\n", stage);
  fprintf (f, "  value=%s\n", value ? value : "(null)");
  fprintf (f, "  value_len=%zu old_value_len=%zu buf_size=%zu norm_len=%zu pos=%zu\n",
           value_len, old_value_len, buf_size, norm_len, pos);
  fprintf (f, "  value_has_close=%d already_has_close=%d\n",
           (int) value_has_close, (int) already_has_close);
  if (NULL != hdr)
  {
    fprintf (f, "  hdr->value_size=%zu\n", hdr->value_size);
    fprintf (f, "  hdr->value (truncated)='");
    /* Print a truncated preview of hdr->value for safety */
    if (NULL != hdr->value)
    {
      size_t i, lim = hdr->value_size < (size_t)200 ? hdr->value_size : (size_t)200;
      for (i = 0; i < lim; ++i)
        fputc ((unsigned char) hdr->value[i], f);
      if (hdr->value_size > lim)
        fprintf (f, "...(truncated, total=%zu)", hdr->value_size);
    }
    fprintf (f, "'\n");
  }
  if (NULL != response)
    fprintf (f, "  response->flags_auto=0x%lx\n", (unsigned long) response->flags_auto);
  fprintf (f, "-----\n");
  fclose (f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
    return MHD_NO;

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }

  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  if (value_len >= SSIZE_MAX)
    return MHD_NO;
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
    return MHD_NO;
  buf_size = old_value_len + (size_t) norm_len;

  /* Log computed lengths and initial state */
  mhd_log_probe ("computed_lengths", response, hdr, value, value_len,
                 old_value_len, buf_size, norm_len, false, already_has_close,
                 pos);

  buf = malloc (buf_size);
  if (NULL == buf)
    return MHD_NO;

  /* Log after allocation */
  mhd_log_probe ("after_malloc", response, hdr, value, value_len,
                 old_value_len, buf_size, norm_len, false, already_has_close,
                 pos);

  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;

    /* Log after removal of close token */
    mhd_log_probe ("after_str_remove_token", response, hdr, value, value_len,
                   old_value_len, buf_size, norm_len, value_has_close,
                   already_has_close, pos);
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));

  /* Log after keep-alive removal if any */
  mhd_log_probe ("after_keep_alive_removal", response, hdr, value, value_len,
                 old_value_len, buf_size, norm_len, value_has_close,
                 already_has_close, pos);

  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      return MHD_YES;
    }
  }

  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);

    /* Log before inserting close token and possible memmove */
    mhd_log_probe ("before_insert_close", response, hdr, value, value_len,
                   old_value_len, buf_size, norm_len, value_has_close,
                   already_has_close, pos);

    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");

    /* Log after inserting close token */
    mhd_log_probe ("after_insert_close", response, hdr, value, value_len,
                   old_value_len, buf_size, norm_len, value_has_close,
                   already_has_close, pos);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }

    /* Log just before copying old header value to buffer -- this is where overflow may occur */
    mhd_log_probe ("before_copy_old_value", response, hdr, value, value_len,
                   old_value_len, buf_size, norm_len, value_has_close,
                   already_has_close, pos);

    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;

    /* Log after copying old value */
    mhd_log_probe ("after_copy_old_value", response, hdr, value, value_len,
                   old_value_len, buf_size, norm_len, value_has_close,
                   already_has_close, pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }

    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;

    /* Log after accounting for new value */
    mhd_log_probe ("after_account_new_value", response, hdr, value, value_len,
                   old_value_len, buf_size, norm_len, value_has_close,
                   already_has_close, pos);
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  /* Final state log before installing header */
  mhd_log_probe ("final_state_before_install", response, hdr, value, value_len,
                 old_value_len, buf_size, norm_len, value_has_close,
                 already_has_close, pos);

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);

        /* Log installation of new header */
        mhd_log_probe ("installed_new_header", response, new_hdr, value,
                       value_len, old_value_len, buf_size, norm_len,
                       value_has_close, already_has_close, pos);

        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;

  /* Log updated existing header */
  mhd_log_probe ("updated_existing_header", response, hdr, value, value_len,
                 old_value_len, buf_size, norm_len, value_has_close,
                 already_has_close, pos);

  return MHD_YES;
}
