#include <stdio.h>
#include <stdlib.h>
#include <string.h>

/* Probe helper: append a log line to /tmp/logs/490.txt */
static void probe_log(const char *fmt, ...)
{
  va_list ap;
  FILE *f = fopen("/tmp/logs/490.txt", "a");
  if (NULL == f)
  {
    return;
  }
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static enum MHD_Result
ahc_cb (void *cls, struct MHD_Connection *con, const char *url,
        const char *method, const char *version, const char *upload_data,
        size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *res;
  const char *upg_header;
  const char *con_header;
  const char *ws_version_header;
  const char *ws_key_header;
  char *ws_ac_value;
  enum MHD_Result ret;
  size_t key_size;

  /* Log entry and pointer parameter values (not dereferencing req_cls or upload_data_size) */
  probe_log("ahc_cb ENTRY: cls=%p, con=%p, url=%p, method=%p, version=%p, upload_data=%p, upload_data_size=%p, req_cls=%p",
            (void *) cls, (void *) con, (const void *) url, (const void *) method,
            (const void *) version, (const void *) upload_data,
            (const void *) upload_data_size, (const void *) req_cls);

  (void) cls;               /* Unused. Silent compiler warning. */
  probe_log("executed no-op: (void) cls; (value was %p)", (void *) cls);
  (void) url;               /* Unused. Silent compiler warning. */
  probe_log("executed no-op: (void) url; (value was %p)", (const void *) url);
  (void) upload_data;       /* Unused. Silent compiler warning. */
  probe_log("executed no-op: (void) upload_data; (value was %p)", (const void *) upload_data);
  (void) upload_data_size;  /* Unused. Silent compiler warning. */
  probe_log("executed no-op: (void) upload_data_size; (value was %p)", (const void *) upload_data_size);

  if (NULL == *req_cls)
  {
    probe_log("req_cls dereferenced: *req_cls was NULL -> setting to (void*)1 and returning MHD_YES");
    *req_cls = (void *) 1;
    return MHD_YES;
  }
  probe_log("req_cls dereferenced: *req_cls was non-NULL -> clearing and continuing");
  *req_cls = NULL;
  upg_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_UPGRADE);
  probe_log("upg_header lookup: %p%s", (const void *) upg_header,
            (upg_header ? (" -> value present") : (" -> NULL")));
  con_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_CONNECTION);
  probe_log("con_header lookup: %p%s", (const void *) con_header,
            (con_header ? (" -> value present") : (" -> NULL")));
  if (MHD_NO == is_websocket_request (con, upg_header, con_header))
  {
    probe_log("Decision: not a websocket request -> send_chat_page");
    return send_chat_page (con);
  }
  probe_log("Decision: websocket request -> check method/version");
  if ((0 != strcmp (method, MHD_HTTP_METHOD_GET))
      || (0 != strcmp (version, MHD_HTTP_VERSION_1_1)))
  {
    probe_log("Bad method/version: method=%s, version=%s -> send_bad_request", method ? method : "(null)", version ? version : "(null)");
    return send_bad_request (con);
  }
  probe_log("Method/version OK: method=%s, version=%s", method ? method : "(null)", version ? version : "(null)");
  ws_version_header =
    MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                 MHD_HTTP_HEADER_SEC_WEBSOCKET_VERSION);
  probe_log("ws_version_header lookup: %p%s", (const void *) ws_version_header,
            (ws_version_header ? (" -> value present") : (" -> NULL")));
  if ((NULL == ws_version_header)
      || (0 != strcmp (ws_version_header, WS_SEC_WEBSOCKET_VERSION)))
  {
    probe_log("Websocket version missing or mismatch -> send_upgrade_required");
    return send_upgrade_required (con);
  }
  probe_log("Websocket version OK -> fetch Sec-WebSocket-Key header");
  ret = MHD_lookup_connection_value_n (con, MHD_HEADER_KIND,
                                       MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY,
                                       strlen (
                                         MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY),
                                       &ws_key_header, &key_size);
  probe_log("MHD_lookup_connection_value_n returned %d, key_size=%zu, ws_key_header=%p",
            (int) ret, key_size, (const void *) ws_key_header);
  if ((MHD_NO == ret) || (key_size != WS_KEY_LEN))
  {
    probe_log("Bad websocket key -> send_bad_request");
    return send_bad_request (con);
  }
  probe_log("ws_get_accept_value start");
  ret = ws_get_accept_value (ws_key_header, &ws_ac_value);
  probe_log("ws_get_accept_value returned %d, ws_ac_value=%p", (int) ret, (void *) ws_ac_value);
  if (MHD_NO == ret)
  {
    probe_log("ws_get_accept_value failed -> returning %d", (int) ret);
    return ret;
  }
  probe_log("Creating upgrade response and adding headers");
  res = MHD_create_response_for_upgrade (&uh_cb, NULL);
  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_SEC_WEBSOCKET_ACCEPT,
                               ws_ac_value))
  {
    probe_log("Failed to add Sec-WebSocket-Accept header -> cleaning up and returning MHD_NO");
    free (ws_ac_value);
    MHD_destroy_response (res);
    return MHD_NO;
  }
  probe_log("Added Sec-WebSocket-Accept header (value ptr=%p)", (void *) ws_ac_value);
  free (ws_ac_value);
  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_UPGRADE, WS_UPGRADE_VALUE))
  {
    probe_log("Failed to add Upgrade header -> destroying response and returning MHD_NO");
    MHD_destroy_response (res);
    return MHD_NO;
  }
  probe_log("Added Upgrade header -> queueing response");
  ret = MHD_queue_response (con, MHD_HTTP_SWITCHING_PROTOCOLS, res);
  probe_log("MHD_queue_response returned %d", (int) ret);
  MHD_destroy_response (res);
  probe_log("ahc_cb EXIT -> returning %d", (int) ret);
  return ret;
}
